'''
def is_prime(n):
    """Check if a number is prime."""
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True
def sum_of_digits(n):
    """Return the sum of the digits of a number."""
    return sum(int(digit) for digit in str(n))
def skjkasdkd(lst):
    """Find the largest prime number in the list and return the sum of its digits."""
    largest_prime = None
    for number in lst:
        if is_prime(number):
            if largest_prime is None or number > largest_prime:
                largest_prime = number
    if largest_prime is not None:
        return sum_of_digits(largest_prime)
    return 0

'''
Given a string representing a space-separated lowercase letters, return a dictionary
of the letter with the most repetition and containing the corresponding count.
If several letters have the same occurrence, return all of them.
Example:
histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
histogram('a b b a') == {'a': 2, 'b': 2}
histogram('a b c a b') == {'a': 2, 'b': 2}
histogram('b b b b a') == {'b': 4}
histogram('') == {}
'''
def histogram(test):
    # Split the input string into a list of letters
    letters = test.split()
    # Initialize a dictionary to count occurrences of each letter
    letter_count = {}
    # Count each letter's occurrences
    for letter in letters:
        if letter in letter_count:
            letter_count[letter] += 1
        else:
            letter_count[letter] = 1
    # Determine the maximum frequency
    max_frequency = max(letter_count.values(), default=0)
    # Create a dictionary of letters with the maximum frequency
    result = {letter: count for letter, count in letter_count.items() if count == max_frequency}
    return result

'''
This module contains a function to validate a given date string based on specific rules.
'''
def valid_date(date):
    import re
    # Check if the date string is empty
    if not date:
        return False
    # Check the format using regular expression
    if not re.match(r'^\d{2}-\d{2}-\d{4}$', date):
        return False
    # Extract month, day, and year
    month, day, year = map(int, date.split('-'))
    # Validate month
    if month < 1 or month > 12:
        return False
    # Validate day based on the month
    if month in {1, 3, 5, 7, 8, 10, 12}:
        if day < 1 or day > 31:
            return False
    elif month in {4, 6, 9, 11}:
        if day < 1 or day > 30:
            return False
    elif month == 2:
        if day < 1:
            return False
        # Check for leap year
        if (year % 4 == 0 and year % 100 != 0) or (year % 400 == 0):
            if day > 29:
                return False
        else:
            if day > 28:
                return False
    # If all checks pass, return True
    return True

'''
Given a string of words, return a list of words split on whitespace. If no whitespaces exist in the text, split on commas ','. 
If no commas exist, return the number of lowercase letters with odd order in the alphabet, where ord('a') = 0, ord('b') = 1, ..., ord('z') = 25.
Examples:
split_words("Hello world!") ➞ ["Hello", "world!"]
split_words("Hello,world!") ➞ ["Hello", "world!"]
split_words("abcdef") == 3 
'''
def split_words(txt):
    # Check if there are any whitespaces in the text
    if ' ' in txt:
        return txt.split()
    # Check if there are any commas in the text
    elif ',' in txt:
        return txt.split(',')
    else:
        # Calculate the number of lowercase letters with odd order in the alphabet
        odd_count = sum(1 for char in txt if char.islower() and (ord(char) - ord('a')) % 2 == 1)
        return odd_count

'''
This module contains the implementation of the compare_one function, which compares two values
that can be integers, floats, or strings representing real numbers. It returns the larger value
in its original type or None if they are equal.
'''
def compare_one(a, b):
    """
    Create a function that takes integers, floats, or strings representing
    real numbers, and returns the larger variable in its given variable type.
    Return None if the values are equal.
    Note: If a real number is represented as a string, the floating point might be . or ,
    compare_one(1, 2.5) ➞ 2.5
    compare_one(1, "2,3") ➞ "2,3"
    compare_one("5,1", "6") ➞ "6"
    compare_one("1", 1) ➞ None
    """
    def to_float(value):
        if isinstance(value, str):
            value = value.replace(',', '.')
        try:
            return float(value)
        except ValueError:
            return None
    a_float = to_float(a)
    b_float = to_float(b)
    if a_float is None or b_float is None:
        return None
    if a_float > b_float:
        return a
    elif b_float > a_float:
        return b
    else:
        return None

'''
This module contains the implementation of the specialFilter function, which filters numbers based on specific criteria.
'''
def specialFilter(nums):
    """Write a function that takes an array of numbers as input and returns 
    the number of elements in the array that are greater than 10 and both 
    first and last digits of a number are odd (1, 3, 5, 7, 9).
    For example:
    specialFilter([15, -73, 14, -15]) => 1 
    specialFilter([33, -2, -3, 45, 21, 109]) => 2
    """
    def is_odd_digit(digit):
        return digit in {'1', '3', '5', '7', '9'}
    count = 0
    for num in nums:
        if num > 10:
            num_str = str(abs(num))  # Use absolute value to handle negative numbers
            if is_odd_digit(num_str[0]) and is_odd_digit(num_str[-1]):
                count += 1
    return count
