'''
For a given number n, find the largest number that divides n evenly, smaller than n.
'''
def largest_divisor(n: int) -> int:
    for i in range(n-1, 0, -1):
        if n % i == 0:
            return i
# Example usage
if __name__ == "__main__":
    print(largest_divisor(15))  # Output should be 5

'''
This module contains the implementation of the prime_fib function which returns the n-th number that is both a Fibonacci number and a prime number.
'''
def is_prime(num: int) -> bool:
    """
    Check if a number is prime.
    """
    if num <= 1:
        return False
    if num <= 3:
        return True
    if num % 2 == 0 or num % 3 == 0:
        return False
    i = 5
    while i * i <= num:
        if num % i == 0 or num % (i + 2) == 0:
            return False
        i += 6
    return True
def fibonacci_sequence():
    """
    Generator to yield Fibonacci numbers indefinitely.
    """
    a, b = 0, 1
    while True:
        yield b
        a, b = b, a + b
def prime_fib(n: int) -> int:
    """
    Returns the n-th number that is a Fibonacci number and it's also prime.
    """
    count = 0
    for fib in fibonacci_sequence():
        if is_prime(fib):
            count += 1
            if count == n:
                return fib

'''
Change numerical base of input number x to base.
Return string representation after the conversion.
Base numbers are less than 10.
'''
def change_base(x: int, base: int) -> str:
    if base < 2 or base > 9:
        raise ValueError("Base must be between 2 and 9, inclusive.")
    if x == 0:
        return "0"
    digits = []
    while x:
        digits.append(str(x % base))
        x //= base
    return ''.join(reversed(digits))
# Example usage
if __name__ == "__main__":
    print(change_base(8, 3))  # Output: '22'
    print(change_base(8, 2))  # Output: '1000'
    print(change_base(7, 2))  # Output: '111'

'''
This module provides functions to encode and decode strings by shifting characters in the alphabet.
'''
def encode_shift(s: str):
    """
    Returns encoded string by shifting every character by 5 in the alphabet.
    """
    return "".join([chr(((ord(ch) + 5 - ord("a")) % 26) + ord("a")) for ch in s])
def decode_shift(s: str):
    """
    Takes as input a string encoded with the encode_shift function. Returns the decoded string.
    """
    return "".join([chr(((ord(ch) - 5 - ord("a")) % 26) + ord("a")) for ch in s])

'''
This module contains the function is_simple_power which checks if a number x is a simple power of n.
'''
def is_simple_power(x, n):
    """Check if x is a simple power of n."""
    if x < 1 or n < 1:
        return False
    if x == 1:
        return True
    while x % n == 0:
        x //= n
    return x == 1

