def simple_cipher(text, key, mode='encrypt'):
    """
    Encrypts or decrypts a text using a simple Caesar cipher.
    :param text: The text to be encrypted or decrypted.
    :param key: The key to use for the cipher. Must be an integer.
    :param mode: 'encrypt' or 'decrypt'. Determines the operation mode.
    :return: The encrypted or decrypted text.
    """
    if not isinstance(key, int):
        raise ValueError("Key must be an integer.")
    if mode not in ['encrypt', 'decrypt']:
        raise ValueError("Mode must be 'encrypt' or 'decrypt'.")
    result = ""
    for char in text:
        if char.isalpha():
            shift = key if mode == 'encrypt' else -key
            char_code = ord(char) + shift
            if char.islower():
                if char_code > ord('z'):
                    char_code -= 26
                elif char_code < ord('a'):
                    char_code += 26
            elif char.isupper():
                if char_code > ord('Z'):
                    char_code -= 26
                elif char_code < ord('A'):
                    char_code += 26
            result += chr(char_code)
        else:
            result += char
    return result
