import pytest
from simple_coder import simple_cipher
def test_encrypt_lowercase():
    assert simple_cipher("abc", 3) == "def", "Should encrypt lowercase letters"
def test_decrypt_lowercase():
    assert simple_cipher("def", 3, 'decrypt') == "abc", "Should decrypt to lowercase letters"
def test_encrypt_with_non_alpha():
    assert simple_cipher("abc 123", 3) == "def 123", "Should ignore non-alphabetical characters"
def test_encrypt_uppercase():
    assert simple_cipher("ABC", 3) == "DEF", "Should encrypt uppercase letters"
def test_wraparound():
    assert simple_cipher("xyz", 3) == "abc", "Should wrap around from z to a"
