def detect_palindromes(file_path):
    palindromes = []
    with open(file_path, 'r') as file:
        for line in file:
            words = line.split()
            for word in words:
                word_clean = ''.join(filter(str.isalnum, word)).lower()
                if word_clean == word_clean[::-1] and len(word_clean) > 1:
                    palindromes.append(word_clean)
    return palindromes
