    """Generate code from an idea (with deterministic fallback)."""
    name = "SimpleWriteCode"

    async def run(self, idea: str = "") -> str:
        idea_text = _safe_str(idea).strip()
        self._log("ACTION_START", f"idea_len={len(idea_text)}")
        if not idea_text:
            self._log("ACTION_END", "no_idea_fallback")
            return (
                "# No idea provided. Placeholder implementation\n"
                "def solution(*args, **kwargs):\n"
                "    \"\"\"Placeholder solution.\"\"\"\n"
                "    return None\n"
            )
        prompt = (
