class SimpleWriteReview(Action):
    """Provide a concise review (automated or human-like)."""
    name = "SimpleWriteReview"

    def __init__(self, *, is_human: bool = False, context: Optional[Context] = None):
        super().__init__(context=context)
        self.is_human = bool(is_human)

    async def run(self, code: str = "", tests: str = "") -> str:
        code_text = _safe_str(code)[:1500]
        tests_text = _safe_str(tests)[:1500]
        self._log("ACTION_START", f"code_len={len(code_text)} tests_len={len(tests_text)} human={self.is_human}")
        if self.is_human:
            review = "Human review: Looks generally good. Consider more edge-case tests and clearer error messages."
            self._log("ACTION_END", "human_review")
            return review
        prompt = (
            "You are a senior reviewer. Provide a concise actionable review of the code and tests.\n\n"
            f"Code (first 1500 chars):\n{code_text}\n\n"
            f"Tests (first 1500 chars):\n{tests_text}\n\n"
            "Focus on readability, potential bugs, and test coverage. Be concise."
        )
        messages = [{"role": "system", "content": "You are a senior software engineer doing code review."},
                    {"role": "user", "content": prompt}]
        try:
            llm = self.llm
            if llm:
                out = await llm.ask(messages)
                review = _safe_str(out)
            else:
                review = "Automated review: Structure acceptable. Add more boundary and negative-case tests."
        except Exception as e:
            review = f"Error during review: {_safe_str(e)}"
        self._log("ACTION_END", f"review_len={len(review)}")
        return review

class Role:
    """
    Lightweight role that holds a primary action and an ordered watch list.
    Simplified API reduces accidental failure modes.
    """
    def __init__(self, *, name: str = "Role", profile: str = "Default", context: Optional[Context] = None, is_human: bool = False):
        self.name = name
        self.profile = profile
        self.context = context
        self.is_human = bool(is_human)
        self.action: Optional[Action] = None
        # watch_list contains cause_by strings this role should respond to
        self.watch_list: List[str] = []

        # environment will be attached when hired
        self.env: Optional["Environment"] = None

    def set_action(self, action: Action):
        """Bind a single primary action to the role and ensure context alignment."""
        if action is None:
            self.action = None
            return
        try:
            action.context = action.context or self.context
        except Exception:
            action.context = self.context
        self.action = action

    def watch(self, causes: List[str]):
        """Set the list of cause_by tokens to respond to."""
        self.watch_list = [c for c in (causes or []) if c]

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Execute primary action using a small deterministic routing strategy."""
        if not self.action:
            _safe_log(getattr(self.context, "tracer", None), "ROLE_SKIP", self.name, "no_action")
            return None

        action_name = getattr(self.action, "name", getattr(self.action, "__class__", type(self.action)).__name__)
        _safe_log(getattr(self.context, "tracer", None), "ROLE_ACT", self.name, f"action={action_name} msg_id={getattr(message, 'id', None)}")
        try:
            # Map inputs based on action type
            if action_name == SimpleWriteCode.name:
                idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") if message else ""
                result = await self.action.run(idea or "")
            elif action_name == SimpleWriteTest.name:
                # Prefer code from triggering message if it's a coder output; otherwise use latest code in env
                code = ""
                if message and getattr(message, "cause_by", "") == SimpleWriteCode.name:
                    code = getattr(message, "content", "") or ""
                if not code and self.env:
                    latest = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                    code = getattr(latest, "content", "") if latest else ""
                result = await self.action.run(code or "")
            elif action_name == SimpleWriteReview.name:
                # Gather code and tests from env or message
                code = ""
                tests = ""
                if message and getattr(message, "cause_by", "") == SimpleWriteTest.name:
                    tests = getattr(message, "content", "") or ""
                if self.env:
                    if not code:
                        latest_code = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                        code = getattr(latest_code, "content", "") if latest_code else ""
                    if not tests:
                        latest_tests = self.env.get_latest_message_by_cause(SimpleWriteTest.name)
                        tests = getattr(latest_tests, "content", "") if latest_tests else ""
                # allow role to influence reviewer human flag
                if isinstance(self.action, SimpleWriteReview):
                    try:
                        self.action.is_human = self.is_human
                    except Exception:
                        pass
                result = await self.action.run(code or "", tests or "")
            else:
                payload = getattr(message, "content", "") if message else ""
                result = await self.action.run(payload)
        except Exception as e:
            _safe_log(getattr(self.context, "tracer", None), "ROLE_ERROR", self.name, f"Exception during act: {_safe_str(e)}")
            result = f"Error in role {self.name}: {_safe_str(e)}"

        response = make_message(content=result, role=self.profile, cause_by=action_name, sent_from=self.name)
        _safe_log(getattr(self.context, "tracer", None), "ROLE_COMPLETE", self.name, f"created_message_id={getattr(response, 'id', None)}")
        return response

