def _safe_str(x: Any) -> str:
    """Safe string conversion without raising."""
    try:
        if x is None:
            return ""
        if isinstance(x, str):
            return x
        return str(x)
    except Exception:
        return ""

def _safe_log(tracer: Optional[ExecutionTracer], event: str, agent: str, details: str):
    """Best-effort logging that never raises."""
    if tracer:
        try:
            tracer.log(event, agent, _safe_str(details))
        except Exception:
            pass

def _make_message(content: str, role: str, cause_by: str = "", sent_from: Optional[str] = None, instruct_content: Optional[str] = None) -> Message:
    """
    Robustly construct a Message-like object. Tries the project's Message class first,
    falls back to a minimal object with the expected attributes.
    """
    content = _safe_str(content)
    try:
        return Message(
            content=content,
            instruct_content=instruct_content,
            role=role,
            cause_by=cause_by or "",
            sent_from=sent_from
        )
    except Exception:
        try:
            return Message(content, role, instruct_content=instruct_content, cause_by=cause_by or "", sent_from=sent_from)
        except Exception:
            class _M:
                pass
            m = _M()
            m.id = str(uuid.uuid4())
            m.content = content
            m.instruct_content = instruct_content
            m.role = role
            m.cause_by = cause_by or ""
            m.sent_from = sent_from
            m.sent_to = None
            m.send_to = set()
            return m

def _sanitize_llm_output(text: str, fallback: str) -> str:
    """
    Ensure LLM output is usable; if it's empty or an error string, return a deterministic fallback.
    """
    t = _safe_str(text).strip()
    if not t:
        return fallback
    prefix = t.lower()
    if prefix.startswith("error:") or "error communicating" in prefix:
        return fallback
    return t

class Action(ABC):
    """Small, robust action base with lazy LLM init and centralized execution mapping."""
    name: str = "Action"

    def __init__(self, context: Optional[Context] = None):
        self.context = context
        self._llm: Optional[LLMInterface] = None

    @property:
    def llm(self) -> Optional[LLMInterface]:
        if self._llm is None:
            try:
                cfg = getattr(self.context, "config", None)
                if cfg and getattr(cfg, "llm", None):
                    self._llm = LLMInterface(cfg.llm)
            except Exception:
                self._llm = None
        return self._llm

    def _log(self, event: str, details: str):
        _safe_log(getattr(self.context, "tracer", None), event, getattr(self, "name", "Action"), details)

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError

    async def execute(self, message: Optional[Message] = None, env: Optional["Environment"] = None) -> str:
        """
        Central mapping of incoming message/env to action input:
        - coder: uses instruct_content or content
        - tester: prefers message from coder else latest code in env
        - reviewer: fetches latest code/tests from env, message overrides tests if provided
        """
        aname = getattr(self, "name", "")
        try:
            if aname == SimpleWriteCode.name:
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") or ""
                return await self.run(_safe_str(idea))

            if aname == SimpleWriteTest.name:
                code = ""
                if message and getattr(message, "cause_by", "") == SimpleWriteCode.name:
                    code = getattr(message, "content", "") or ""
                if not code and env:
                    latest = env.get_latest_message_by_cause(SimpleWriteCode.name)
                    code = getattr(latest, "content", "") if latest else ""
                return await self.run(_safe_str(code))

            if aname == SimpleWriteReview.name:
                code = ""
                tests = ""
                if env:
                    mc = env.get_latest_message_by_cause(SimpleWriteCode.name)
                    mt = env.get_latest_message_by_cause(SimpleWriteTest.name)
                    code = getattr(mc, "content", "") if mc else ""
                    tests = getattr(mt, "content", "") if mt else ""
                if message and getattr(message, "cause_by", "") == SimpleWriteTest.name:
                    tests = getattr(message, "content", "") or tests
                return await self.run(_safe_str(code), _safe_str(tests))

            # Generic fallback
            return await self.run(_safe_str(getattr(message, "content", "")) if message else "")
        except Exception as e:
            self._log("ACTION_EXCEPTION", _safe_str(e))
            return f"Error executing {aname or 'Action'}: {e}"

class SimpleWriteCode(Action):
    """Generate code for a given idea or requirement."""
    name = "SimpleWriteCode"

    async def run(self, idea: str = "") -> str:
        idea = _safe_str(idea).strip()
        self._log("ACTION_START", f"idea_len={len(idea)}")
        if not idea:
            # Deterministic, valid placeholder
            code = (
                "# No idea provided. Placeholder implementation.\n"
                "def solution(*args, **kwargs):\n"
                "    \"\"\"Placeholder solution.\"\"\"\n"
                "    try:\n"
                "        return None\n"
                "    except Exception:\n"
                "        raise\n"
            )
            self._log("ACTION_END", "used_fallback_no_idea")
            return code

        prompt = (
            "You are an expert Python programmer. Write clean, testable Python code for the task below.\n\n"
            f"Task:\n{idea}\n\n"
            "Requirements:\n- Clean, testable code\n- Basic error handling\n- Short explanatory comments\n\n"
            "Return only the code."
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt},
        ]
        try:
            if self.llm:
                resp = await self.llm.ask(messages)
                fallback = (
                    f"# Implementation for: {idea}\n"
                    "def solution(*args, **kwargs):\n"
                    "    \"\"\"LLM-unavailable fallback.\"\"\"\n"
                    "    return None\n"
                )
                code = _sanitize_llm_output(resp, fallback)
            else:
                code = (
                    f"# Implementation for: {idea}\n"
                    "def solution(*args, **kwargs):\n"
                    "    \"\"\"LLM-unavailable fallback.\"\"\"\n"
                    "    try:\n"
                    "        return None\n"
                    "    except Exception:\n"
                    "        raise\n"
                )
        except Exception as e:
            code = f"# Error generating code: {_safe_str(e)}"
        self._log("ACTION_END", f"code_len={len(_safe_str(code))}")
        return _safe_str(code)

class SimpleWriteTest(Action):
    """Generate pytest-style tests for a given implementation."""
    name = "SimpleWriteTest"

    async def run(self, code: str = "") -> str:
        code = _safe_str(code)
        self._log("ACTION_START", f"code_len={len(code)}")
        if not code.strip():
            tests = (
                "import pytest\n\n"
                "def test_placeholder():\n"
                "    \"\"\"Placeholder test (no code available).\"\"\"\n"
                "    assert True\n"
            )
            self._log("ACTION_END", "used_fallback_no_code")
            return tests

        prompt = (
            "You are a QA engineer. Write pytest-style tests for the implementation below.\n\n"
            f"Code (first 2000 chars):\n{code[:2000]}\n\n"
            "Requirements:\n- Use pytest\n- Cover normal and edge cases\n- Include short docstrings\n\n"
            "Return only the test code."
        )
        messages = [
            {"role": "system", "content": "You are an expert QA engineer."},
            {"role": "user", "content": prompt},
        ]
        try:
            if self.llm:
                resp = await self.llm.ask(messages)
                fallback = (
                    "import pytest\n\n"
                    "def test_placeholder():\n"
                    "    \"\"\"LLM-unavailable fallback test.\"\"\"\n"
                    "    assert True\n"
                )
                tests = _sanitize_llm_output(resp, fallback)
            else:
                tests = (
                    "import pytest\n\n"
                    "def test_placeholder():\n"
                    "    \"\"\"LLM-unavailable fallback test.\"\"\"\n"
                    "    assert True\n"
                )
        except Exception as e:
            tests = f"# Error generating tests: {_safe_str(e)}"
        self._log("ACTION_END", f"tests_len={len(_safe_str(tests))}")
        return _safe_str(tests)

class SimpleWriteReview(Action):
    """Provide a concise review of code and tests."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, context: Optional[Context] = None):
        super().__init__(context=context)
        self.is_human = bool(is_human)

    async def run(self, code: str = "", tests: str = "") -> str:
        code = _safe_str(code)[:1500]
        tests = _safe_str(tests)[:1500]
        self._log("ACTION_START", f"code_len={len(code)} tests_len={len(tests)} human={self.is_human}")
        if self.is_human:
            review = "Human review: Looks reasonable. Add edge-case tests and clearer error handling."
            self._log("ACTION_END", "human_review")
            return review

        prompt = (
            "You are a senior reviewer. Provide a concise, actionable review of the code and tests below.\n\n"
            f"Code:\n{code}\n\n"
            f"Tests:\n{tests}\n\n"
            "Focus on readability, correctness, and coverage. Keep it brief and practical."
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code review."},
            {"role": "user", "content": prompt},
        ]
        try:
            if self.llm:
                resp = await self.llm.ask(messages)
                fallback = "Automated review: Code structure acceptable; add more negative tests and clearer error messages."
                review = _sanitize_llm_output(resp, fallback)
            else:
                review = "Automated review: Code structure acceptable; add more negative tests and clearer error messages."
        except Exception as e:
            review = f"Review error: {_safe_str(e)}"
        self._log("ACTION_END", f"review_len={len(_safe_str(review))}")
        return _safe_str(review)

class Role(ABC):
    """Minimal role abstraction: single primary action, simple watch rules."""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, name: Optional[str] = None, profile: Optional[str] = None, context: Optional[Context] = None, is_human: bool = False):
        self.name = name or self.name
        self.profile = profile or self.profile
        self.context = context
        self.is_human = bool(is_human)
        # store a single action instance in a list for compatibility with Team.hire
        self.actions: List[Action] = []
        # watch_list stores cause_by names this role reacts to
        self.watch_list: List[str] = []
        self.env: Optional["Environment"] = None

    def set_actions(self, actions: List[Action]):
        self.actions = actions or []
        for a in self.actions:
            try:
                if getattr(a, "context", None) is None:
                    a.context = self.context
            except Exception:
                pass

    def _watch(self, actions: List[Type[Action] or str]):
        names: List[str] = []
        for a in actions or []:
            if isinstance(a, str):
                names.append(a)
            elif hasattr(a, "name"):
                names.append(getattr(a, "name"))
            elif isinstance(a, type) and hasattr(a, "name"):
                names.append(getattr(a, "name"))
        self.watch_list = names

    def _log(self, event: str, details: str):
        _safe_log(getattr(self.context, "tracer", None), event, self.name, details)

    async def act(self, message: Optional[Message] = None, env: Optional["Environment"] = None) -> Optional[Message]:
        if not self.actions:
            self._log("ROLE_SKIP", "no_actions")
            return None

        action = self.actions[0]
        # Propagate human flag to review action if applicable
        if isinstance(action, SimpleWriteReview):
            action.is_human = self.is_human

        self._log("ROLE_ACT", f"action={getattr(action, 'name', 'Action')} msg_id={getattr(message, 'id', None)}")
        try:
            result = await action.execute(message=message, env=env or self.env)
        except Exception as e:
            self._log("ROLE_ERROR", _safe_str(e))
            result = f"Error executing role {self.name}: {_safe_str(e)}"

        # Always tag response with the action name to support routing
        response = _make_message(
            content=_safe_str(result),
            role=self.profile,
            cause_by=getattr(action, "name", ""),
            sent_from=self.name
        )
        self._log("ROLE_COMPLETE", f"produced_len={len(_safe_str(result))}")
        return response

class SimpleCoder(Role):
    """Role that writes code"""
    name = "Alice"
    profile = "SimpleCoder"

    def __init__(self, **kwargs):
        super().__init__(name=kwargs.get("name", "Alice"), profile=kwargs.get("profile", "SimpleCoder"), context=kwargs.get("context"), is_human=kwargs.get("is_human", False))
        self.set_actions([SimpleWriteCode(context=self.context)])

class SimpleTester(Role):
    """Role that writes tests"""
    name = "Bob"
    profile = "SimpleTester"

    def __init__(self, **kwargs):
        super().__init__(name=kwargs.get("name", "Bob"), profile=kwargs.get("profile", "SimpleTester"), context=kwargs.get("context"), is_human=kwargs.get("is_human", False))
        self.set_actions([SimpleWriteTest(context=self.context)])
        self._watch([SimpleWriteCode.name])

class SimpleReviewer(Role):
    """Role that reviews code and tests"""
    name = "Charlie"
    profile = "SimpleReviewer"

    def __init__(self, **kwargs):
        super().__init__(name=kwargs.get("name", "Charlie"), profile=kwargs.get("profile", "SimpleReviewer"), context=kwargs.get("context"), is_human=kwargs.get("is_human", False))
        self.set_actions([SimpleWriteReview(is_human=self.is_human, context=self.context)])
        self._watch([SimpleWriteTest.name])

class Environment:
    """Environment storing roles and messages with simple, safe operations."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer

    def add_role(self, role: Role):
        try:
            role.env = self
            self.roles.append(role)
            _safe_log(self.tracer, "ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")
        except Exception:
            pass

    def publish_message(self, message: Message):
        """Publish a message to history and log a small preview; never raise."""
        try:
            if not getattr(message, "id", None):
                setattr(message, "id", str(uuid.uuid4()))
        except Exception:
            pass
        try:
            self.history.append(message)
        except Exception:
            # As a last resort, wrap as minimal message
            m = _make_message(
                content=_safe_str(getattr(message, "content", message)),
                role=_safe_str(getattr(message, "role", "Unknown")),
                cause_by=_safe_str(getattr(message, "cause_by", "")),
                sent_from=_safe_str(getattr(message, "sent_from", "Unknown")),
            )
            self.history.append(m)
            message = m
        # Log best-effort
        preview = _safe_str(getattr(message, "content", ""))[:120]
        sender = _safe_str(getattr(message, "sent_from", "Unknown"))
        cause = _safe_str(getattr(message, "cause_by", ""))
        _safe_log(self.tracer, "ENV_MESSAGE", "Environment", f"From {sender} cause={cause} preview={preview}")

    def get_messages_for_role(self, role: Role) -> List[Message]:
        """Return messages whose cause_by matches any watched name."""
        watched = set(role.watch_list or [])
        if not watched:
            return []
        relevant: List[Message] = []
        for m in self.history:
            try:
                if getattr(m, "cause_by", "") in watched:
                    relevant.append(m)
            except Exception:
                continue
        return relevant

    def get_latest_message_by_cause(self, cause_name: str) -> Optional[Message]:
        for m in reversed(self.history):
            try:
                if getattr(m, "cause_by", "") == cause_name:
                    return m
            except Exception:
                continue
        return None

class Team:
    """Deterministic, robust orchestration of multi-agent collaboration."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        try:
            self.context.tracer = self.tracer
        except Exception:
            pass
        self.env = Environment(self.tracer)
        try:
            setattr(self.context, "env", self.env)
        except Exception:
            pass
        self.investment: float = 10.0
        self.idea: str = ""
        self.log_file = log_file

    def hire(self, roles: List[Role]):
        """Hire roles, wire context/env, and align action contexts."""
        for r in roles or []:
            try:
                r.context = r.context or self.context
                r.env = self.env
                for a in getattr(r, "actions", []) or []:
                    try:
                        a.context = a.context or self.context
                        # Initialize LLM if possible
                        if getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
                            a._llm = LLMInterface(self.context.config.llm)
                    except Exception:
                        pass
            except Exception:
                pass
            self.env.add_role(r)

    def invest(self, investment: float):
        self.investment = float(investment)

    def run_project(self, idea: str):
        self.idea = _safe_str(idea)
        _safe_log(self.tracer, "TEAM_START", "Team", f"Starting project: {self.idea}")

    async def run(self, n_round: int = 3):
        n_round = max(1, int(n_round or 1))
        _safe_log(self.tracer, "TEAM_RUN", "Team", f"Running {n_round} rounds")

        # Initial user message
        initial_msg = _make_message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput"
        )
        self.env.publish_message(initial_msg)

        for round_num in range(n_round):
            _safe_log(self.tracer, "ROUND_START", "Team", f"Round {round_num + 1}/{n_round}")

            # Iterate over a stable snapshot to avoid concurrent modification issues
            for role in list(self.env.roles):
                response: Optional[Message] = None
                try:
                    if round_num == 0 and role.profile == "SimpleCoder":
                        response = await role.act(initial_msg, env=self.env)
                    else:
                        relevant = self.env.get_messages_for_role(role)
                        if not relevant:
                            continue
                        response = await role.act(relevant[-1], env=self.env)
                except Exception as e:
                    _safe_log(self.tracer, "ROLE_EXCEPTION", getattr(role, "name", "Unknown"), _safe_str(e))
                    continue

                if response:
                    # Ensure cause_by is set for routing
                    try:
                        if not getattr(response, "cause_by", None):
                            response.cause_by = getattr(role.actions[0], "name", "UnknownAction") if role.actions else "UnknownAction"
                    except Exception:
                        pass
                    self.env.publish_message(response)

            _safe_log(self.tracer, "ROUND_END", "Team", f"Round {round_num + 1} completed")

        _safe_log(self.tracer, "TEAM_END", "Team", "Project completed")
        summary = f"Project '{self.idea}' completed after {n_round} rounds with {len(self.env.history)} messages exchanged."
        _safe_log(self.tracer, "SUMMARY", "Team", summary)

