def _safe_str(x: Any) -> str:
    """Return a stable string representation, avoiding exceptions."""
    try:
        if x is None:
            return ""
        if isinstance(x, str):
            return x
        return str(x)
    except Exception:
        return ""

def _make_message(content: str, role: str, cause_by: str = "", sent_from: Optional[str] = None, instruct_content: Optional[str] = None):
    """
    Robust helper to construct Message objects compatible with both pydantic and fallback Message.
    """
    content = _safe_str(content)
    try:
        # Preferred: named constructor (works with pydantic Message)
        return Message(content=content, instruct_content=instruct_content, role=role, cause_by=cause_by, sent_from=sent_from)
    except Exception:
        try:
            # Fallback: positional constructor for legacy Message
            return Message(content, role, instruct_content=instruct_content, cause_by=cause_by, sent_from=sent_from)
        except Exception:
            # Last resort: create a minimal object with required attributes
            class _SimpleMsg:
                def __init__(self, content, role, cause_by, sent_from, instruct_content):
                    self.id = str(uuid.uuid4())
                    self.content = content
                    self.instruct_content = instruct_content
                    self.role = role
                    self.cause_by = cause_by
                    self.sent_from = sent_from
                    self.sent_to = None
                    self.send_to = set()
            return _SimpleMsg(content, role, cause_by, sent_from, instruct_content)

class Action(ABC):
    """Minimal, robust action base class with safe logging and optional LLM."""
    name: str = "Action"

    def __init__(self, context: Optional[Context] = None):
        self.context = context
        self._llm: Optional[LLMInterface] = None

    @property
    def llm(self) -> Optional[LLMInterface]:
        """Lazily create LLM interface if available in context; safe and idempotent"""
        if self._llm is None and self.context:
            try:
                cfg = getattr(self.context, "config", None)
                if cfg and getattr(cfg, "llm", None):
                    self._llm = LLMInterface(cfg.llm)
            except Exception:
                self._llm = None
        return self._llm

    def _log(self, event: str, details: str):
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            try:
                tracer.log(event, self.name, details)
            except Exception:
                pass

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError

