class Role(ABC):
    """Simple role with predictable behavior and minimal mutable state."""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, name: Optional[str] = None, profile: Optional[str] = None, context: Optional[Context] = None, is_human: bool = False):
        self.name = name or self.name
        self.profile = profile or self.profile
        self.context = context
        self.is_human = bool(is_human)
        self.actions: List[Action] = []
        # watch_list stores action names
        self.watch_list: List[str] = []
        # environment reference set when hired
        self.env: Optional["Environment"] = None

    def set_actions(self, actions: List[Action]):
        prepared = []
        for a in actions or []:
            try:
                a.context = self.context
            except Exception:
                pass
            prepared.append(a)
        self.actions = prepared

    def _watch(self, actions: List[Type[Action]]):
        names: List[str] = []
        for a in (actions or []):
            if isinstance(a, str):
                names.append(a)
            elif hasattr(a, "name"):
                try:
                    names.append(getattr(a, "name"))
                except Exception:
                    names.append(str(a))
            elif hasattr(a, "__name__"):
                names.append(getattr(a, "__name__"))
        self.watch_list = names

    def _log(self, event: str, details: str):
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            try:
                tracer.log(event, self.name, details)
            except Exception:
                pass

    def _select_action(self, message: Optional[Message]) -> Optional[Action]:
        if not self.actions:
            return None
        # Prefer explicit instruction -> coder
        if message and getattr(message, "instruct_content", None):
            for action in self.actions:
                if action.name == SimpleWriteCode.name:
                    return action
        # Map cause to action
        cause = getattr(message, "cause_by", "") if message else ""
        if cause:
            if cause == SimpleWriteCode.name:
                for action in self.actions:
                    if action.name == SimpleWriteTest.name:
                        return action
            if cause == SimpleWriteTest.name:
                for action in self.actions:
                    if action.name == SimpleWriteReview.name:
                        return action
        # Default fallback
        return self.actions[0]

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        self._log("ROLE_ACT", f"Acting on message id={getattr(message, 'id', None)}")
        if not self.actions:
            self._log("ROLE_SKIP", "No actions")
            return None

        action = self._select_action(message)
        if not action:
            self._log("ROLE_SKIP", "No action selected")
            return None

        result = ""
        try:
            if action.name == SimpleWriteCode.name:
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") or ""
                result = await action.run(idea)
            elif action.name == SimpleWriteTest.name:
                code_text = ""
                if message and getattr(message, "cause_by", "") == SimpleWriteCode.name:
                    code_text = getattr(message, "content", "") or ""
                else:
                    # find last code in env
                    try:
                        if self.env:
                            for m in reversed(self.env.history):
                                if getattr(m, "cause_by", "") == SimpleWriteCode.name:
                                    code_text = getattr(m, "content", "") or ""
                                    break
                    except Exception:
                        code_text = ""
                result = await action.run(code_text)
            elif action.name == SimpleWriteReview.name:
                code_text = ""
                tests_text = ""
                # Prefer message content if it matches cause_by
                if message:
                    if getattr(message, "cause_by", "") == SimpleWriteCode.name:
                        code_text = getattr(message, "content", "") or ""
                    if getattr(message, "cause_by", "") == SimpleWriteTest.name:
                        tests_text = getattr(message, "content", "") or ""
                # fill missing from env
                try:
                    if self.env:
                        if not code_text:
                            for m in reversed(self.env.history):
                                if getattr(m, "cause_by", "") == SimpleWriteCode.name:
                                    code_text = getattr(m, "content", "") or ""
                                    break
                        if not tests_text:
                            for m in reversed(self.env.history):
                                if getattr(m, "cause_by", "") == SimpleWriteTest.name:
                                    tests_text = getattr(m, "content", "") or ""
                                    break
                except Exception:
                    pass
                # ensure human flag propagation
                if isinstance(action, SimpleWriteReview):
                    action.is_human = self.is_human
                result = await action.run(code_text, tests_text)
            else:
                # Generic single-argument fallback
                payload = getattr(message, "content", "") if message else ""
                result = await action.run(payload)
        except Exception as e:
            self._log("ROLE_ERROR", f"{action.name} failed: {e}")
            result = f"Error executing action {action.name}: {e}"

        response = _make_message(content=result, role=self.profile, cause_by=action.name, sent_from=self.name)
        self._log("ROLE_COMPLETE", f"{action.name} produced message id={getattr(response, 'id', '')}")
        return response

