            return Message(
                content,
                role,
                instruct_content=instruct_content,
                cause_by=cause_by,
                sent_from=sent_from
            )
        except Exception:
            # Last-resort minimal message
            class _M:
                def __init__(self):
                    self.id = str(uuid.uuid4())
                    self.content = content
                    self.instruct_content = instruct_content
                    self.role = role
                    self.cause_by = cause_by
                    self.sent_from = sent_from
                    self.sent_to = None
                    self.send_to = set()
            return _M()

class Action(ABC):
    """Simple, robust base action with unified execution mapping."""
    name: str = "Action"

    def __init__(self, **kwargs):
        self.context: Optional[Context] = kwargs.get("context")
        self.llm: Optional[LLMInterface] = None
        if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
            try:
                self.llm = LLMInterface(self.context.config.llm)
            except Exception:
                self.llm = None

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError()

    async def execute(self, message: Optional[Message] = None, env: Optional["Environment"] = None) -> str:
        """
        Map incoming message + environment into the appropriate arguments for run().
        Keep this mapping small and explicit to reduce failure surface.
        """
        try:
            aname = getattr(self, "name", "") or self.__class__.__name__
            if aname == "SimpleWriteCode":
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") or ""
                return await self.run(_safe_str(idea))
            if aname == "SimpleWriteTest":
                # Prefer message.content as code; fall back to last code in env
                code = getattr(message, "content", "") if message else ""
                if (not code) and env:
                    latest = env.get_latest_message_by_cause("SimpleWriteCode")
                    code = getattr(latest, "content", "") if latest else ""
                return await self.run(_safe_str(code))
            if aname == "SimpleWriteReview":
                # Need both code and tests
                code = ""
                tests = ""
                # If message triggered from tests, prefer tests from message
                if message:
                    if getattr(message, "cause_by", "") == "SimpleWriteTest":
                        tests = getattr(message, "content", "") or ""
                    elif getattr(message, "cause_by", "") == "SimpleWriteCode":
                        code = getattr(message, "content", "") or ""
                    # also allow instruct_content to carry code
                    if not code:
                        code = getattr(message, "instruct_content", "") or ""
                if env:
                    if not code:
                        latest_code = env.get_latest_message_by_cause("SimpleWriteCode")
                        code = getattr(latest_code, "content", "") if latest_code else code
                    if not tests:
                        latest_tests = env.get_latest_message_by_cause("SimpleWriteTest")
                        tests = getattr(latest_tests, "content", "") if latest_tests else tests
                return await self.run(_safe_str(code), _safe_str(tests))
            # generic fallback
            return await self.run(_safe_str(getattr(message, "content", "")) if message else "")
        except Exception as e:
            _safe_log(getattr(self.context, "tracer", None), "ACTION_EXCEPTION", getattr(self, "name", "Action"), str(e))
            return f"Error executing {getattr(self, 'name', 'Action')}: {e}"

