def _make_message(content: str, role: str, cause_by: str = "", sent_from: Optional[str] = None, instruct_content: Optional[str] = None) -> Message:
    """
    Robustly create a Message instance compatible with both pydantic and fallback Message.
    Always returns an object that has at least: id, content, instruct_content, role, cause_by, sent_from.
    """
    content = _safe_str(content)
    kwargs = {
        "content": content,
        "instruct_content": instruct_content,
        "role": role,
        "cause_by": cause_by or "",
        "sent_from": sent_from
    }
    try:
        # Preferred construction (works with pydantic)
        return Message(**{k: v for k, v in kwargs.items() if v is not None or k in ("content", "role")})
    except Exception:
        try:
            # Fallback positional style
            return Message(content, role, instruct_content=instruct_content, cause_by=cause_by or "", sent_from=sent_from)
        except Exception:
            # Last resort: minimal object with expected attributes
            class Minimal:
                def __init__(self, content, instruct_content, role, cause_by, sent_from):
                    self.id = str(uuid.uuid4())
                    self.content = content
                    self.instruct_content = instruct_content
                    self.role = role
                    self.cause_by = cause_by
                    self.sent_from = sent_from
                    self.sent_to = None
                    self.send_to = set()
                def __str__(self):
                    return f"Message(role={self.role}, content={self.content[:50]}...)"
            return Minimal(content, instruct_content, role, cause_by or "", sent_from or "System")

class Action(ABC):
    """Small, robust Action base: lazy LLM init, safe logging, deterministic fallback."""
