        self._llm: Optional[LLMInterface] = None

    @property
    def llm(self) -> Optional[LLMInterface]:
        if self._llm is None and getattr(self.context, "config", None) is not None:
            try:
                cfg = getattr(self.context, "config")
                if getattr(cfg, "llm", None) is not None:
                    self._llm = LLMInterface(cfg.llm)
            except Exception:
                self._llm = None
        return self._llm

    def _log(self, event: str, details: str):
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            try:
                tracer.log(event, getattr(self, "name", "Action"), _safe_str(details))
            except Exception:
                pass

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError

