        if not code.strip():
            tests = (
                "import pytest\n\n"
                "def test_placeholder():\n"
                "    \"\"\"Placeholder test (no code available).\"\"\"\n"
                "    assert True\n"
            )
            self._log("ACTION_END", "used_fallback_no_code")
            return tests

        prompt = (
            "You are a QA engineer. Write pytest-style tests for the implementation below.\n\n"
            f"Code (first 2000 chars):\n{code[:2000]}\n\n"
