class SimpleWriteReview(Action):
    """Provide a concise review of code and tests."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, context: Optional[Context] = None):
        super().__init__(context=context)
        self.is_human = bool(is_human)

    async def run(self, code: str = "", tests: str = "") -> str:
        code = _safe_str(code)[:1500]
        tests = _safe_str(tests)[:1500]
        self._log("ACTION_START", f"code_len={len(code)} tests_len={len(tests)} human={self.is_human}")
        if self.is_human:
            review = "Human review: Looks reasonable. Add edge-case tests and clearer error handling."
            self._log("ACTION_END", "human_review")
            return review

        prompt = (
            "You are a senior reviewer. Provide a concise, actionable review of the code and tests below.\n\n"
            f"Code:\n{code}\n\n"
            f"Tests:\n{tests}\n\n"
            "Focus on readability, correctness, and coverage. Keep it brief and practical."
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code review."},
            {"role": "user", "content": prompt},
        ]
        try:
            if self.llm:
                resp = await self.llm.ask(messages)
                fallback = "Automated review: Code structure acceptable; add more negative tests and clearer error messages."
                review = _sanitize_llm_output(resp, fallback)
            else:
                review = "Automated review: Code structure acceptable; add more negative tests and clearer error messages."
        except Exception as e:
            review = f"Review error: {_safe_str(e)}"
        self._log("ACTION_END", f"review_len={len(_safe_str(review))}")
        return _safe_str(review)

class Role(ABC):
    """Minimal role abstraction: single primary action, simple watch rules."""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, name: Optional[str] = None, profile: Optional[str] = None, context: Optional[Context] = None, is_human: bool = False):
        self.name = name or self.name
        self.profile = profile or self.profile
        self.context = context
        self.is_human = bool(is_human)
        # store a single action instance in a list for compatibility with Team.hire
        self.actions: List[Action] = []
        # watch_list stores cause_by names this role reacts to
        self.watch_list: List[str] = []
        self.env: Optional["Environment"] = None

    def set_actions(self, actions: List[Action]):
        self.actions = actions or []
        for a in self.actions:
            try:
                if getattr(a, "context", None) is None:
                    a.context = self.context
            except Exception:
                pass

    def _watch(self, actions: List[Type[Action] or str]):
        names: List[str] = []
        for a in actions or []:
            if isinstance(a, str):
                names.append(a)
            elif hasattr(a, "name"):
                names.append(getattr(a, "name"))
            elif isinstance(a, type) and hasattr(a, "name"):
                names.append(getattr(a, "name"))
        self.watch_list = names

    def _log(self, event: str, details: str):
        _safe_log(getattr(self.context, "tracer", None), event, self.name, details)

    async def act(self, message: Optional[Message] = None, env: Optional["Environment"] = None) -> Optional[Message]:
        if not self.actions:
            self._log("ROLE_SKIP", "no_actions")
            return None

        action = self.actions[0]
        # Propagate human flag to review action if applicable
        if isinstance(action, SimpleWriteReview):
            action.is_human = self.is_human

        self._log("ROLE_ACT", f"action={getattr(action, 'name', 'Action')} msg_id={getattr(message, 'id', None)}")
        try:
            result = await action.execute(message=message, env=env or self.env)
        except Exception as e:
            self._log("ROLE_ERROR", _safe_str(e))
            result = f"Error executing role {self.name}: {_safe_str(e)}"

        # Always tag response with the action name to support routing
        response = _make_message(
            content=_safe_str(result),
            role=self.profile,
            cause_by=getattr(action, "name", ""),
            sent_from=self.name
        )
        self._log("ROLE_COMPLETE", f"produced_len={len(_safe_str(result))}")
        return response

class SimpleCoder(Role):
