class Action(ABC):
    """Lightweight base action with safe LLM access and logging helpers."""
    name: str = "Action"

    def __init__(self, *, context: Optional[Context] = None):
        self.context = context
        self._llm: Optional[LLMInterface] = None

    @property
    def llm(self) -> Optional[LLMInterface]:
        """Lazily construct LLMInterface if configuration is present."""
        if self._llm is None and self.context is not None:
            try:
                cfg = getattr(self.context, "config", None)
                if cfg is not None and getattr(cfg, "llm", None) is not None:
                    self._llm = LLMInterface(cfg.llm)
            except Exception:
                self._llm = None
        return self._llm

    def _log(self, event: str, details: str):
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            try:
                tracer.log(event, self.name, details)
            except Exception:
                # never allow tracing issues to propagate
                pass

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError

class SimpleWriteCode(Action):
    name = "SimpleWriteCode"
    def __init__(self, *, context: Optional[Context] = None):
        super().__init__(context=context)

    async def run(self, idea: str) -> str:
        idea = (idea or "").strip()
        self._log("ACTION_START", f"Preparing code generation (len={len(idea)})")
        if not idea:
            # explicit deterministic fallback: small template that is valid Python
            fallback = (
                "# No idea provided. Placeholder implementation\n"
                "def solution(*args, **kwargs):\n"
                "    \"\"\"Placeholder function.\"\"\"\n"
                "    return None\n"
            )
            self._log("ACTION_END", "Used deterministic fallback for code")
            return fallback

        prompt = (
            "You are an expert Python programmer. Produce clear, correct Python code for the task below.\n\n"
            f"Task: {idea}\n\n"
            "Requirements:\n"
            "- Clean, testable code\n"
            "- Basic error handling when applicable\n"
            "- Short comments explaining non-obvious parts\n\n"
            "Return only the code."
        )
        messages = [{"role": "system", "content": "You are an expert Python programmer."},
                    {"role": "user", "content": prompt}]
        try:
            if self.llm:
                result = await self.llm.ask(messages)
            else:
                result = f"# Implementation for: {idea}\n# [LLM not available - deterministic stub]\n"
        except Exception as e:
            result = f"# Error generating code: {e}\n"
        self._log("ACTION_END", f"Generated code (len={len(result)})")
        return result

class SimpleWriteTest(Action):
    name = "SimpleWriteTest"
    def __init__(self, *, context: Optional[Context] = None):
        super().__init__(context=context)

    async def run(self, code: str) -> str:
        code = (code or "").strip()
        self._log("ACTION_START", f"Preparing tests generation (code_len={len(code)})")
        if not code:
            fallback = (
                "import pytest\n\n"
                "def test_placeholder():\n"
                "    assert True\n"
            )
            self._log("ACTION_END", "Used deterministic fallback for tests")
            return fallback

        prompt = (
            "You are a QA engineer. Write pytest-style tests for the provided implementation.\n\n"
            f"Code (first 2000 chars):\n{code[:2000]}\n\n"
            "Requirements:\n"
            "- Use pytest\n"
            "- Cover typical and edge cases\n"
            "- Return only test code"
        )
        messages = [{"role": "system", "content": "You are an expert QA engineer."},
                    {"role": "user", "content": prompt}]
        try:
            if self.llm:
                result = await self.llm.ask(messages)
            else:
                result = (
                    "import pytest\n\n"
                    "def test_placeholder():\n"
                    "    assert True\n"
                )
        except Exception as e:
            result = f"# Error generating tests: {e}\n"
