class Role(ABC):
    """Base role class for agents"""
    def __init__(self, *, name: str = "Role", profile: str = "Default", context: Optional[Context] = None, is_human: bool = False):
        self.name: str = name
        self.profile: str = profile
        self.context: Optional[Context] = context
        self.is_human: bool = is_human
        # per-instance lists to avoid shared mutable state bugs
        self.actions: List[Action] = []
        # watch_list will hold either action class names or action name strings
        self.watch_list: List[str] = []
        # environment reference will be assigned when hired
        self.env = None

    def set_actions(self, actions: List[Action]):
        """Set the actions this role can perform"""
        # ensure actions know current context
        for a in actions:
            a.context = self.context
            if a.context and getattr(a.context, "config", None):
                try:
                    a.llm = LLMInterface(a.context.config.llm)
                except Exception:
                    a.llm = None
        self.actions = actions

    def _watch(self, actions: List[Type[Action]]):
        """Set the actions this role watches for; accepts types or names"""
        names: List[str] = []
        for a in actions:
            if isinstance(a, str):
                names.append(a)
            elif isinstance(a, type):
                # try to instantiate a temporary to read name or use class attribute
                names.append(getattr(a, "__name__", getattr(a, "name", str(a))))
            else:
                # fallback to str
                names.append(str(a))
        self.watch_list = names

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Perform an action based on the message and environment history"""
        if not self.actions:
            return None

        action = self.actions[0]  # simplified single-action-per-role model

        if self.context and getattr(self.context, "tracer", None):
            self.context.tracer.log("ROLE_ACT", self.name, f"Executing action: {action.name}")

        try:
            result = ""
            # Decide action behavior by action.name (string) for decoupling
            if action.name == "SimpleWriteCode":
                # Use instruct_content when available, otherwise message content
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") or ""
                result = await action.run(idea)
            elif action.name == "SimpleWriteTest":
                # Prefer latest code message from env history if available
                code_text = ""
                if self.env:
                    # scan history backwards for a SimpleWriteCode cause
                    for msg in reversed(self.env.history):
                        if getattr(msg, "cause_by", "") == "SimpleWriteCode":
                            code_text = getattr(msg, "content", "") or ""
                            break
                # fallback to message content if nothing found
                if not code_text and message:
                    code_text = getattr(message, "content", "") or ""
                result = await action.run(code_text)
            elif action.name == "SimpleWriteReview":
                # Need both latest code and latest tests
                code_text = ""
                tests_text = ""
                if self.env:
                    for msg in reversed(self.env.history):
                        cause = getattr(msg, "cause_by", "")
                        if not tests_text and cause == "SimpleWriteTest":
                            tests_text = getattr(msg, "content", "") or ""
                        if not code_text and cause == "SimpleWriteCode":
                            code_text = getattr(msg, "content", "") or ""
                        if code_text and tests_text:
                            break
                # if not found, fall back to message content
                if not code_text and message:
                    code_text = getattr(message, "content", "") or ""
                result = await action.run(code_text, tests_text)
            else:
                # generic action execution - attempt to pass message content
                try:
                    if message:
                        result = await action.run(getattr(message, "content", "") or "")
                    else:
                        result = await action.run("")
                except TypeError:
                    # action may expect different signature; call without args
                    result = await action.run()
        except Exception as e:
            result = f"Error executing action {action.name}: {e}"
            if self.context and getattr(self.context, "tracer", None):
                self.context.tracer.log("ROLE_ERROR", self.name, result)

        # Construct response message robustly
        response = Message(
            content=result,
            role=self.profile,
            cause_by=action.name if action else "",
            sent_from=self.name
        )

        if self.context and getattr(self.context, "tracer", None):
            self.context.tracer.log("ROLE_COMPLETE", self.name, f"Action completed, message created ({len(result)} chars)")

        return response

