class Role(ABC):
    """Base role class for agents"""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, *, name: Optional[str] = None, profile: Optional[str] = None, context: Optional[Context] = None, env: Optional["Environment"] = None, is_human: bool = False):
        self.name = name or self.name
        self.profile = profile or self.profile
        self.context = context
        self.env = env  # environment reference (set by Team.hire)
        self.is_human = is_human
        self.actions: List[Action] = []
        # watch_list stores action names (strings) that this role reacts to
        self.watch_list: List[str] = []

    def set_actions(self, actions: List[Action]):
        """Set the actions this role can perform"""
        self.actions = actions

    def _watch(self, actions: List[Union[Type[Action], str]]):
        """Set the actions (by class or name) this role watches for"""
        names: List[str] = []
        for a in actions:
            if isinstance(a, str):
                names.append(a)
            elif hasattr(a, "name"):
                names.append(getattr(a, "name"))
            elif hasattr(a, "__name__"):
                names.append(a.__name__)
        self.watch_list = names

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Perform the first available action based on the message."""
        if not self.actions:
            if self.context and self.context.tracer:
                self.context.tracer.log("ROLE_NOP", self.name, "No actions assigned")
            return None

        action = self.actions[0]
        if self.context and self.context.tracer:
            self.context.tracer.log("ROLE_ACT", self.name, f"Starting action {action.name} on message id={(getattr(message, 'id', None))}")

        result = ""
        try:
            # Decide how to call run depending on action type
            if isinstance(action, SimpleWriteCode):
                # prefer instruct_content for code generation
                idea = getattr(message, "instruct_content", None) or getattr(message, "content", "")
                result = await action.run(idea or "")
            elif isinstance(action, SimpleWriteTest):
                # expect code in message.content
                code = getattr(message, "content", "") if message else ""
                result = await action.run(code)
            elif isinstance(action, SimpleWriteReview):
                # For review, try to pull latest code and tests from environment history when available
                code = ""
                tests = ""
                if message:
                    # message may be a tests message; find latest code in env
                    tests = getattr(message, "content", "")
                if getattr(self, "env", None):
                    # find latest code produced (cause_by == SimpleWriteCode)
                    latest_code = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                    if latest_code:
                        code = latest_code.content
                    # find latest tests produced
                    latest_tests = self.env.get_latest_message_by_cause(SimpleWriteTest.name)
                    if latest_tests:
                        tests = tests or latest_tests.content
                result = await action.run(code, tests)
            else:
                # Generic fallback: pass message.content
                content = getattr(message, "content", "") if message else ""
                result = await action.run(content)
        except Exception as e:
            # Catch and log exceptions to avoid failing the team run
            err = f"Error in action {action.name}: {e}"
            if self.context and self.context.tracer:
                self.context.tracer.log("ROLE_ERROR", self.name, err)
            result = err

        # Build response message; ensure compatibility with both Message implementations
        response = Message(
            content=result,
            role=self.profile,
            cause_by=getattr(action, "name", "Action"),
            sent_from=self.name
        )

        if self.context and self.context.tracer:
            self.context.tracer.log("ROLE_COMPLETE", self.name, f"Action {action.name} completed, produced message id={getattr(response, 'id', None)}")

        return response

