                    names.append(a.__name__)
            except Exception:
                continue
        self.watch_list = names

    def _log(self, event: str, details: str):
        if self.context and getattr(self.context, "tracer", None):
            try:
                self.context.tracer.log(event, self.name, details)
            except Exception:
                pass

    def _select_action_for_message(self, message: Optional[Message]) -> Optional[Action]:
        """Choose the most appropriate action for a message, simple deterministic policy"""
        if not self.actions:
            return None
        # If message contains instruct_content, prefer actions that accept instruction (SimpleWriteCode)
        if message and getattr(message, "instruct_content", None):
            for action in self.actions:
                if action.name == "SimpleWriteCode":
                    return action
        # Otherwise choose based on watch_list mapping and available actions
        if message and getattr(message, "cause_by", None):
            cause = message.cause_by
            for action in self.actions:
                # Tester watches for coder outputs, reviewer watches for tester outputs
                if action.name and cause and ((action.name == "SimpleWriteTest" and "SimpleWriteCode" in (self.watch_list or [])) or (action.name == "SimpleWriteReview" and "SimpleWriteTest" in (self.watch_list or []))):
                    # This heuristic is conservative; fall back if nothing matches
                    return action
        # Default: return first action
        return self.actions[0] if self.actions else None

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Perform an action based on the message"""
        self._log("ROLE_ACT", f"Preparing to act on message: {getattr(message, 'id', 'none')}")
        if not self.actions:
            self._log("ROLE_SKIP", "No actions available for role")
            return None

        action = self._select_action_for_message(message)
        if not action:
            self._log("ROLE_SKIP", "No suitable action selected")
            return None

        try:
            # Determine inputs for action in a simple, explicit manner
            if action.name == "SimpleWriteCode":
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "")
                result = await action.run(idea)
            elif action.name == "SimpleWriteTest":
                code_text = ""
                if message:
                    # If message is from coder (cause_by == SimpleWriteCode), its content is code
                    code_text = getattr(message, "content", "")
                result = await action.run(code_text)
            elif action.name == "SimpleWriteReview":
                # Reviewer may need both code and tests; try to extract the latest code and tests from context tracer history if available
                code_text = ""
                tests_text = ""
                if message:
                    # If message contains code, assume it's code; otherwise try to find last coder/tester outputs in context history
                    if getattr(message, "cause_by", "") == "SimpleWriteCode":
                        code_text = getattr(message, "content", "")
                    elif getattr(message, "cause_by", "") == "SimpleWriteTest":
                        tests_text = getattr(message, "content", "")
                # Try to supplement missing pieces from tracer history safely
                try:
                    hist = getattr(self.context, "tracer", None)
                    # We avoid parsing the tracer file; keep simple and deterministic
                except Exception:
                    pass
                result = await action.run(code_text, tests_text)
            else:
                # Generic action invocation
                result = await action.run(message)
        except Exception as e:
            # Ensure failures inside actions do not crash the system; return an error message
            self._log("ROLE_ERROR", f"Action {action.name} failed: {str(e)}")
            result = f"Action {action.name} encountered an error: {str(e)}"

        # Create response message with clear metadata
        try:
            response = Message(
                content=result,
                role=self.profile,
                cause_by=action.name,
                sent_from=self.name
            )
        except Exception:
            # In case pydantic Message signature differs, try fallback construction
            try:
                response = Message(content=result, role=self.profile, cause_by=action.name, sent_from=self.name)
            except Exception:
                # As last resort, build a minimal object
                class _M:
                    pass
                response = _M()
                response.content = result
                response.role = self.profile
                response.cause_by = action.name
                response.sent_from = self.name

        self._log("ROLE_COMPLETE", f"Action {action.name} completed, message {getattr(response, 'id', '')}")
        return response

