                        code_text = getattr(message, "content", "")
                    elif getattr(message, "cause_by", "") == "SimpleWriteTest":
                        tests_text = getattr(message, "content", "")
                # Try to supplement missing pieces from tracer history safely
                try:
                    hist = getattr(self.context, "tracer", None)
                    # We avoid parsing the tracer file; keep simple and deterministic
                except Exception:
                    pass
                result = await action.run(code_text, tests_text)
            else:
                # Generic action invocation
                result = await action.run(message)
        except Exception as e:
            # Ensure failures inside actions do not crash the system; return an error message
            self._log("ROLE_ERROR", f"Action {action.name} failed: {str(e)}")
            result = f"Action {action.name} encountered an error: {str(e)}"

        # Create response message with clear metadata
        try:
            response = Message(
                content=result,
                role=self.profile,
                cause_by=action.name,
                sent_from=self.name
            )
        except Exception:
            # In case pydantic Message signature differs, try fallback construction
            try:
                response = Message(content=result, role=self.profile, cause_by=action.name, sent_from=self.name)
            except Exception:
                # As last resort, build a minimal object
                class _M:
                    pass
                response = _M()
                response.content = result
                response.role = self.profile
                response.cause_by = action.name
                response.sent_from = self.name

        self._log("ROLE_COMPLETE", f"Action {action.name} completed, message {getattr(response, 'id', '')}")
        return response

