            if action.name == "SimpleWriteCode":
                # Use instruct_content when available, otherwise message content
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") or ""
                result = await action.run(idea)
            elif action.name == "SimpleWriteTest":
                # Prefer latest code message from env history if available
                code_text = ""
                if self.env:
                    # scan history backwards for a SimpleWriteCode cause
                    for msg in reversed(self.env.history):
                        if getattr(msg, "cause_by", "") == "SimpleWriteCode":
                            code_text = getattr(msg, "content", "") or ""
                            break
                # fallback to message content if nothing found
                if not code_text and message:
                    code_text = getattr(message, "content", "") or ""
                result = await action.run(code_text)
            elif action.name == "SimpleWriteReview":
                # Need both latest code and latest tests
                code_text = ""
                tests_text = ""
                if self.env:
                    for msg in reversed(self.env.history):
                        cause = getattr(msg, "cause_by", "")
                        if not tests_text and cause == "SimpleWriteTest":
                            tests_text = getattr(msg, "content", "") or ""
                        if not code_text and cause == "SimpleWriteCode":
                            code_text = getattr(msg, "content", "") or ""
                        if code_text and tests_text:
                            break
                # if not found, fall back to message content
                if not code_text and message:
                    code_text = getattr(message, "content", "") or ""
                result = await action.run(code_text, tests_text)
            else:
                # generic action execution - attempt to pass message content
                try:
                    if message:
                        result = await action.run(getattr(message, "content", "") or "")
                    else:
                        result = await action.run("")
                except TypeError:
                    # action may expect different signature; call without args
                    result = await action.run()
        except Exception as e:
            result = f"Error executing action {action.name}: {e}"
            if self.context and getattr(self.context, "tracer", None):
                self.context.tracer.log("ROLE_ERROR", self.name, result)

        # Construct response message robustly
        response = Message(
            content=result,
            role=self.profile,
            cause_by=action.name if action else "",
            sent_from=self.name
        )

        if self.context and getattr(self.context, "tracer", None):
            self.context.tracer.log("ROLE_COMPLETE", self.name, f"Action completed, message created ({len(result)} chars)")

