def _safe_str(x: Any) -> str:
    """Return a stable string representation, avoiding exceptions."""
    try:
        if x is None:
            return ""
        if isinstance(x, str):
            return x
        return str(x)
    except Exception:
        return ""

def _make_message(content: str, role: str, cause_by: str = "", sent_from: str = "System") -> Message:
    """Construct a Message instance robustly for both pydantic and fallback Message classes."""
    content = _safe_str(content)
    try:
        # Preferred: named constructor (works with pydantic Message)
        return Message(content=content, instruct_content=None, role=role, cause_by=cause_by, sent_from=sent_from)
    except Exception:
        try:
            # Fallback: positional constructor for legacy Message
            return Message(content, role, instruct_content=None, cause_by=cause_by, sent_from=sent_from)
        except Exception:
            # Last resort: create a minimal object with required attributes
            class _SimpleMsg:
                def __init__(self, content, role, cause_by, sent_from):
                    self.id = str(uuid.uuid4())
                    self.content = content
                    self.instruct_content = None
                    self.role = role
                    self.cause_by = cause_by
                    self.sent_from = sent_from
                    self.sent_to = None
                    self.send_to = set()
            return _SimpleMsg(content, role, cause_by, sent_from)

class Action(ABC):
    """Minimal, robust action base class with safe logging and optional LLM."""
    name: str = "Action"

    def __init__(self, context: Optional[Context] = None):
        self.context = context
        self.llm: Optional[LLMInterface] = None
        try:
            if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
                self.llm = LLMInterface(self.context.config.llm)
        except Exception:
            self.llm = None

    def _log(self, event: str, details: str):
        try:
            if self.context and getattr(self.context, "tracer", None):
                self.context.tracer.log(event, getattr(self, "name", "Action"), details)
        except Exception:
            # Logging must not raise
            pass

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError

class SimpleWriteCode(Action):
    name = "SimpleWriteCode"

    async def run(self, idea: str = "") -> str:
        idea = _safe_str(idea).strip()
        self._log("ACTION_START", f"Writing code for: {idea[:120]}")
        prompt = (
            "You are a professional programmer. Write Python code for the following task:\n"
            f"Task: {idea}\n\n"
            "Requirements:\n"
            "1. Write clean, functional Python code\n"
            "2. Include proper error handling\n"
            "3. Add comments explaining the logic\n"
            "4. Make it production-ready\n\n"
            "Please provide only the code without any explanation."
        )
        messages = [{"role": "system", "content": "You are an expert Python programmer."},
                    {"role": "user", "content": prompt}]
        try:
            if self.llm:
                code = await self.llm.ask(messages)
            else:
                # Deterministic fallback reduces flakiness
                code = (
                    f"# Implementation for: {idea}\n"
                    "def placeholder_function():\n"
                    "    \"\"\"Placeholder implementation until LLM available.\"\"\"\n"
                    "    try:\n"
                    "        return 'placeholder'\n"
                    "    except Exception:\n"
                    "        raise\n"
                )
        except Exception as e:
            code = f"# Error generating code: {e}"
            self._log("ACTION_ERROR", f"{e}")
        code = _safe_str(code)
        self._log("ACTION_END", f"Generated {len(code)} chars")
        return code

class SimpleWriteTest(Action):
    name = "SimpleWriteTest"

    async def run(self, code: str = "") -> str:
        code = _safe_str(code)
        preview = code[:2000]
        self._log("ACTION_START", "Writing tests for code")
        prompt = (
