        return tests

class SimpleWriteReview(Action):
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, context: Optional[Context] = None):
        super().__init__(context=context)
        self.is_human = bool(is_human)

    async def run(self, code: str = "", tests: str = "") -> str:
        code = _safe_str(code)
        tests = _safe_str(tests)
        self._log("ACTION_START", f"Reviewing code (human={self.is_human})")
        try:
            if self.is_human:
                review = "Human review: Looks reasonable. Consider additional error checks and clearer docstrings."
            else:
                prompt = (
                    "You are a senior code reviewer. Review the following code and tests:\n\n"
                    f"Code:\n{code[:1500]}\n\n"
                    f"Tests:\n{tests[:1500]}\n\n"
                    "Provide a brief review focusing on:\n"
                    "1. Code quality and best practices\n"
                    "2. Test coverage\n"
                    "3. Potential bugs or issues\n"
                    "4. Suggestions for improvement\n\n"
                    "Keep your review concise and actionable."
                )
                messages = [{"role": "system", "content": "You are a senior software engineer doing code review."},
                            {"role": "user", "content": prompt}]
                if self.llm:
                    review = await self.llm.ask(messages)
                else:
                    review = "Review: Code structure OK. Tests cover basics; consider more edge cases."
        except Exception as e:
            review = f"Error during review: {e}"
            self._log("ACTION_ERROR", f"{e}")
        review = _safe_str(review)
        self._log("ACTION_END", f"Review completed ({len(review)} chars)")
        return review

class Role(ABC):
    """Simple role with predictable behavior and minimal mutable state."""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, name: Optional[str] = None, profile: Optional[str] = None, context: Optional[Context] = None, is_human: bool = False):
        self.name = name or self.name
        self.profile = profile or self.profile
        self.context = context
        self.is_human = bool(is_human)
        self.actions: List[Action] = []
        # watch_list stores action names to avoid type matching complexity
        self.watch_list: List[str] = []
        self.env: Optional["Environment"] = None

    def set_actions(self, actions: List[Action]):
        # ensure actions know current context
        prepared = []
        for a in actions or []:
            try:
                a.context = self.context
            except Exception:
                pass
            prepared.append(a)
        self.actions = prepared

    def _watch(self, actions: List[Type[Action]]):
        names: List[str] = []
        for a in actions or []:
            if isinstance(a, str):
                names.append(a)
            elif hasattr(a, "name"):
                try:
                    names.append(getattr(a, "name"))
                except Exception:
                    names.append(str(a))
            elif isinstance(a, type):
                names.append(getattr(a, "name", a.__name__))
        self.watch_list = names

    def _log(self, event: str, details: str):
        try:
            if self.context and getattr(self.context, "tracer", None):
                self.context.tracer.log(event, self.name, details)
        except Exception:
            pass

    async def act(self, message: Optional[Message] = None, env: Optional["Environment"] = None) -> Optional[Message]:
        if not self.actions:
            return None
        action = self.actions[0]
        env = env or self.env
        self._log("ROLE_ACT", f"Executing action: {getattr(action, 'name', 'Action')} message_id={getattr(message, 'id', None)}")

        result = ""
        try:
            # Map action by name (simple and robust)
            act_name = getattr(action, "name", "")
            if act_name == SimpleWriteCode.name:
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") or ""
                result = await action.run(idea)

            elif act_name == SimpleWriteTest.name:
                # Prefer message (likely code), else lookup latest code in env
                code_text = ""
                if message and getattr(message, "content", None):
                    code_text = getattr(message, "content")
                elif env:
                    for m in reversed(env.history):
                        if getattr(m, "cause_by", "") == SimpleWriteCode.name and getattr(m, "content", None):
                            code_text = getattr(m, "content", "")
                            break
                result = await action.run(code_text)

            elif act_name == SimpleWriteReview.name:
                code_text = ""
                tests_text = ""
                if message:
                    # If the triggering message is tests, prefer that
                    if getattr(message, "cause_by", "") == SimpleWriteTest.name:
                        tests_text = getattr(message, "content", "") or ""
                    elif getattr(message, "cause_by", "") == SimpleWriteCode.name:
                        code_text = getattr(message, "content", "") or ""
                # Fill missing pieces from env
                if env:
                    if not code_text:
                        for m in reversed(env.history):
                            if getattr(m, "cause_by", "") == SimpleWriteCode.name:
                                code_text = getattr(m, "content", "") or code_text
                                break
                    if not tests_text:
                        for m in reversed(env.history):
                            if getattr(m, "cause_by", "") == SimpleWriteTest.name:
                                tests_text = getattr(m, "content", "") or tests_text
                                break
                # propagate human flag if possible
                if isinstance(action, SimpleWriteReview):
                    action.is_human = self.is_human
                result = await action.run(code_text, tests_text)
            else:
                # Generic fallback: attempt to pass message content
                payload = getattr(message, "content", "") if message else ""
                result = await action.run(payload)
        except Exception as e:
            err = f"Error executing action {getattr(action, 'name', '')}: {e}"
            self._log("ROLE_ERROR", err)
            result = err

        # Build response message robustly
        try:
            response = _make_message(content=_safe_str(result), role=self.profile, cause_by=getattr(action, "name", ""), sent_from=self.name)
        except Exception:
            # As a last resort, return None (shouldn't happen)
            self._log("ROLE_ERROR", "Failed to create response message")
            return None

        self._log("ROLE_COMPLETE", f"Action completed, message created (cause_by={getattr(action, 'name', '')})")
        return response

class SimpleCoder(Role):
    name = "Alice"
