class Environment:
    """Environment for multi-agent collaboration with robust message routing."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer

    def add_role(self, role: Role):
        try:
            role.env = self
            self.roles.append(role)
            if self.tracer:
                self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")
        except Exception:
            # avoid raising from add_role
            pass

    def get_roles(self, profile: Optional[str] = None) -> List[Role]:
        try:
            if profile:
                return [r for r in self.roles if r.profile == profile]
            return list(self.roles)
        except Exception:
            return []

    def publish_message(self, message: Message):
        """Append a message to history and log a short preview; must not raise."""
        try:
            self.history.append(message)
            if self.tracer:
                sender = getattr(message, "sent_from", "Unknown")
                content_preview = (_safe_str(getattr(message, "content", "")) or "")[:100]
                cause = getattr(message, "cause_by", "")
                self.tracer.log("ENV_MESSAGE", "Environment", f"Message from {sender} (cause={cause}): {content_preview}")
        except Exception as e:
            if self.tracer:
                try:
                    self.tracer.log("ENV_ERROR", "Environment", f"Failed to publish message: {e}")
                except Exception:
                    pass

    def get_messages_for_role(self, role: Role) -> List[Message]:
        """Return messages whose cause_by matches any name in role.watch_list."""
        results: List[Message] = []
        try:
            watched = set(getattr(role, "watch_list", []) or [])
            if not watched:
                return results
            for m in self.history:
                try:
                    if getattr(m, "cause_by", "") in watched:
                        results.append(m)
                except Exception:
                    continue
        except Exception:
            if self.tracer:
                try:
                    self.tracer.log("ENV_ERROR", "Environment", f"Error in get_messages_for_role for {getattr(role, 'name', '')}")
                except Exception:
                    pass
        return results

class Team:
    """Team of agents working together. Simplified, robust orchestration."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        try:
            self.context.tracer = self.tracer
        except Exception:
            pass
        self.env = Environment(self.tracer)
        self.investment: float = 10.0
        self.idea: str = ""
        self.log_file = log_file

    def hire(self, roles: List[Role]):
        for r in roles or []:
            try:
                r.context = self.context
                # Recreate or update actions' contexts
                for a in getattr(r, "actions", []) or []:
                    try:
                        a.context = self.context
                        if self.context and getattr(self.context, "config", None):
                            try:
                                a.llm = LLMInterface(self.context.config.llm)
                            except Exception:
                                a.llm = None
                    except Exception:
                        pass
            except Exception:
                pass
            self.env.add_role(r)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        try:
            if self.tracer:
                self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")
        except Exception:
            pass

    async def run(self, n_round: int = 3):
        try:
            if self.tracer:
                self.tracer.log("TEAM_RUN", "Team", f"Running {n_round} rounds")
        except Exception:
            pass

        initial_msg = _make_message(content=f"Let's work on this project: {self.idea}", role="Human", cause_by="UserInput", sent_from="User")
        self.env.publish_message(initial_msg)

        for round_num in range(n_round):
            try:
                if self.tracer:
                    self.tracer.log("ROUND_START", "Team", f"Round {round_num + 1}/{n_round}")
            except Exception:
                pass

            # iterate over a snapshot of roles to avoid mutation during iteration
            for role in list(self.env.roles):
                response = None
                try:
                    # First round: let the coder respond to the user input explicitly
                    if round_num == 0 and getattr(role, "profile", "") == "SimpleCoder":
                        response = await role.act(initial_msg, env=self.env)
                    else:
                        relevant = self.env.get_messages_for_role(role)
                        if relevant:
                            response = await role.act(relevant[-1], env=self.env)
                        else:
                            continue
                except Exception as e:
                    try:
                        if self.tracer:
                            self.tracer.log("ROLE_EXCEPTION", getattr(role, "name", "Unknown"), f"Exception while acting: {e}")
                    except Exception:
                        pass
                    continue

                if response:
                    try:
                        # Ensure response has cause_by for routing; best-effort
                        if not getattr(response, "cause_by", None):
                            try:
                                response.cause_by = getattr(response, "cause_by", "")
                            except Exception:
                                pass
                    except Exception:
                        pass
                    self.env.publish_message(response)

            try:
                if self.tracer:
                    self.tracer.log("ROUND_END", "Team", f"Round {round_num + 1} completed")
            except Exception:
                pass

        try:
            if self.tracer:
                self.tracer.log("TEAM_END", "Team", "Project completed")
        except Exception:
            pass

        summary = f"Project '{self.idea}' completed after {n_round} rounds with {len(self.env.history)} messages exchanged."
        try:
            if self.tracer:
                self.tracer.log("SUMMARY", "Team", summary)
        except Exception:
            pass

