        self.profile = kwargs.get('profile', self.profile)
        self.context: Optional[Context] = kwargs.get('context')
        self.is_human: bool = kwargs.get('is_human', False)
        self.actions: List[Action] = []
        self.watch_list: List[Type[Action]] = []

    def set_actions(self, actions: List[Action]):
        """Set the actions this role can perform"""
        self.actions = list(actions)

    def _watch(self, actions: List[Type[Action]]):
        """Set the actions this role watches for"""
        self.watch_list = list(actions)

    async def act(self, message: Optional[Message] = None, env: Optional["Environment"] = None) -> Optional[Message]:
        """Perform an action based on the message and environment context"""
        if not self.actions:
            return None

        action = self.actions[0]
        if self.context and getattr(self.context, "tracer", None):
            self.context.tracer.log("ROLE_ACT", self.name, f"Executing action: {action.name} on message id={(getattr(message, 'id', None))}")

        # Default result
        result = ""
        try:
            # Handle code writing
            if isinstance(action, SimpleWriteCode):
                prompt_text = ""
                if message and getattr(message, "instruct_content", None):
                    prompt_text = message.instruct_content or message.content or ""
                elif message and getattr(message, "content", None):
                    prompt_text = message.content
                else:
                    prompt_text = ""
                result = await action.run(prompt_text)

            # Handle test writing: try to use provided message (likely code); if none, look up latest code in env
            elif isinstance(action, SimpleWriteTest):
                code_text = ""
                if message and getattr(message, "content", None):
                    code_text = message.content
                elif env:
                    # Find the most recent code message in history
                    for msg in reversed(env.history):
                        if getattr(msg, "cause_by", "") == SimpleWriteCode.name and getattr(msg, "content", None):
                            code_text = msg.content
                            break
                result = await action.run(code_text)

            # Handle review: try to assemble both code and tests from message/env
            elif isinstance(action, SimpleWriteReview):
                code_text = ""
                tests_text = ""

                if message:
                    if getattr(message, "cause_by", "") == SimpleWriteTest.name:
                        tests_text = message.content or ""
                        # find latest code
                        if env:
                            for msg in reversed(env.history):
                                if getattr(msg, "cause_by", "") == SimpleWriteCode.name and getattr(msg, "content", None):
                                    code_text = msg.content
                                    break
                    elif getattr(message, "cause_by", "") == SimpleWriteCode.name:
                        code_text = message.content or ""
                        # find latest tests
                        if env:
                            for msg in reversed(env.history):
                                if getattr(msg, "cause_by", "") == SimpleWriteTest.name and getattr(msg, "content", None):
                                    tests_text = msg.content
                                    break
                    else:
                        # Unknown source; try to extract both from env
                        if env:
                            for msg in reversed(env.history):
                                if not code_text and getattr(msg, "cause_by", "") == SimpleWriteCode.name:
                                    code_text = msg.content or ""
                                if not tests_text and getattr(msg, "cause_by", "") == SimpleWriteTest.name:
                                    tests_text = msg.content or ""
                                if code_text and tests_text:
                                    break
                else:
                    # No message given; search env for most recent code and tests
                    if env:
                        for msg in reversed(env.history):
                            if not code_text and getattr(msg, "cause_by", "") == SimpleWriteCode.name:
                                code_text = msg.content or ""
                            if not tests_text and getattr(msg, "cause_by", "") == SimpleWriteTest.name:
                                tests_text = msg.content or ""
                            if code_text and tests_text:
                                break

                result = await action.run(code_text, tests_text)

            else:
                # Generic fallback
                result = await action.run(*([]))
        except Exception as e:
            result = f"Error executing action {action.name}: {e}"
            if self.context and getattr(self.context, "tracer", None):
                self.context.tracer.log("ROLE_ERROR", self.name, f"{e}")

        # Create response message; ensure all fields expected by Message are provided
        try:
            response = Message(
                content=_safe_str(result),
                role=self.profile,
                cause_by=getattr(action, "name", ""),
                sent_from=self.name
            )
        except Exception:
            # Fallback to simple dict-like object if Message construction fails
            class _SimpleMsg:
                def __init__(self, content, role, cause_by, sent_from):
                    self.content = content
                    self.role = role
                    self.cause_by = cause_by
                    self.sent_from = sent_from
                    self.id = str(uuid.uuid4())
            response = _SimpleMsg(_safe_str(result), self.profile, getattr(action, "name", ""), self.name)

        if self.context and getattr(self.context, "tracer", None):
            self.context.tracer.log("ROLE_COMPLETE", self.name, f"Action completed, message created, cause_by={getattr(action, 'name', '')}")

        return response

