class Role(ABC):
    """Simple, predictable role with clear responsibilities and minimal state."""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, name: Optional[str] = None, profile: Optional[str] = None, context: Optional[Context] = None, is_human: bool = False):
        self.name = name or self.name
        self.profile = profile or self.profile
        self.context: Optional[Context] = context
        self.is_human = bool(is_human)
        # actions are Action instances; Team.hire is responsible for creating them with context
        self.actions: List[Action] = []
        # watch_list stores action names to simplify checks
        self.watch_list: List[str] = []
        # environment reference (set by Team.hire)
        self.env: Optional["Environment"] = None

    def set_actions(self, actions: List[Action]):
        # Ensure each action has the current context
        prepared = []
        for a in actions or []:
            try:
                a.context = self.context
                prepared.append(a)
            except Exception:
                prepared.append(a)
        self.actions = prepared

    def _watch(self, actions: List[Type[Action]]):
        names = []
        for a in actions or []:
            if isinstance(a, str):
                names.append(a)
            elif hasattr(a, "name"):
                names.append(getattr(a, "name"))
            elif isinstance(a, type) and hasattr(a, "name"):
                names.append(getattr(a, "name"))
        self.watch_list = names

    def _log(self, event: str, details: str):
        try:
            if self.context and getattr(self.context, "tracer", None):
                self.context.tracer.log(event, self.name, details)
        except Exception:
            pass

    async def act(self, message: Optional[Message] = None, env: Optional["Environment"] = None) -> Optional[Message]:
        if not self.actions:
            return None
        action = self.actions[0]
        # Ensure env available for lookups
        env = env or self.env

        self._log("ROLE_ACT", f"Executing action: {action.name}")

        try:
            # Simple, explicit mapping of inputs to actions
            if action.name == SimpleWriteCode.name:
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "")
                result = await action.run(idea)
            elif action.name == SimpleWriteTest.name:
                # Prefer message content; otherwise look up latest code in env
                code = ""
                if message and getattr(message, "content", None):
                    code = message.content
                elif env:
                    for m in reversed(env.history):
                        if getattr(m, "cause_by", "") == SimpleWriteCode.name:
                            code = getattr(m, "content", "") or code
                            break
                result = await action.run(code)
            elif action.name == SimpleWriteReview.name:
                # Assemble code and tests from message + env
                code = ""
                tests = ""
                if message:
                    # If message contains tests, treat content as tests and instruct_content as code
                    tests = getattr(message, "content", "") or ""
                    code = getattr(message, "instruct_content", "") or ""
                if env:
                    if not code:
                        for m in reversed(env.history):
                            if getattr(m, "cause_by", "") == SimpleWriteCode.name:
                                code = getattr(m, "content", "") or code
                                break
                    if not tests:
                        for m in reversed(env.history):
                            if getattr(m, "cause_by", "") == SimpleWriteTest.name:
                                tests = getattr(m, "content", "") or tests
                                break
                # If role is human, reflect that to the action if possible
                if isinstance(action, SimpleWriteReview):
                    action.is_human = self.is_human
                result = await action.run(code, tests)
            else:
                # Generic fallback
                payload = getattr(message, "content", "") if message else ""
                result = await action.run(payload)
        except Exception as e:
            # Convert failure to safe response and log
            err = f"Error executing {action.name}: {e}"
            self._log("ROLE_EXCEPTION", err)
            result = err

        # Build response message robustly (support both pydantic and fallback)
        response_kwargs = {
            "content": result,
            "role": self.profile,
            "cause_by": getattr(action, "name", ""),
            "sent_from": self.name
        }
        try:
            response = Message(**response_kwargs)
        except Exception:
            try:
                response = Message(response_kwargs["content"], response_kwargs["role"],
                                   cause_by=response_kwargs["cause_by"], sent_from=response_kwargs["sent_from"])
            except Exception:
                # Last-resort simple object
                class _SimpleMsg:
                    def __init__(self, content, role, cause_by, sent_from):
                        self.id = str(uuid.uuid4())
                        self.content = content
                        self.instruct_content = None
                        self.role = role
                        self.cause_by = cause_by
                        self.sent_from = sent_from
                        self.sent_to = None
                        self.send_to = set()
                response = _SimpleMsg(response_kwargs["content"], response_kwargs["role"],
                                      response_kwargs["cause_by"], response_kwargs["sent_from"])

        self._log("ROLE_COMPLETE", "Action completed, message created")
        return response

