def _safe_str(x: Any) -> str:
    """Return a stable string representation, avoiding exceptions."""
    try:
        if x is None:
            return ""
        if isinstance(x, str):
            return x
        return str(x)
    except Exception:
        return ""

def _make_message(content: str, role: str, cause_by: str = "", sent_from: str = "System") -> Message:
    """Construct a Message instance robustly for both pydantic and fallback Message classes."""
    content = _safe_str(content)
    try:
