class Role(ABC):
    """Base role class for agents (simplified and robust)"""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, **kwargs):
        self.name = kwargs.get("name", self.name)
        self.profile = kwargs.get("profile", self.profile)
        self.context: Optional[Context] = kwargs.get("context")
        self.is_human: bool = kwargs.get("is_human", False)
        # Use action instances for simplicity
        self.actions: List[Action] = []
        # watch list stores action names causing messages this role reacts to
        self.watch_list: List[str] = []
        self.env: Optional["Environment"] = None

    def set_actions(self, actions: List[Action]):
        """Assign action instances to the role, aligning context if needed"""
        self.actions = list(actions or [])
        for a in self.actions:
            if getattr(a, "context", None) is None:
                a.context = self.context
            # Ensure LLM available if context exists
            try:
                if self.context and getattr(a, "llm", None) is None:
                    a.llm = LLMInterface(self.context.config.llm)
            except Exception:
                a.llm = None

    def _watch(self, actions: List[Type[Action]]):
        """Set the actions this role watches for; accepts classes or names"""
        names: List[str] = []
        for a in actions or []:
            if isinstance(a, str):
                names.append(a)
            elif isinstance(a, type) and hasattr(a, "name"):
                names.append(getattr(a, "name"))
            elif hasattr(a, "name"):
                names.append(getattr(a, "name"))
        self.watch_list = names

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Execute the primary action with simple, deterministic routing"""
        if not self.actions:
            return None

        action = self.actions[0]
        tracer = getattr(self.context, "tracer", None) if self.context else None
        if tracer:
            try:
                tracer.log("ROLE_ACT", self.name, f"Executing action: {action.name}")
            except Exception:
                pass

        # Determine the selected message
        selected_msg = message
        if selected_msg is None and self.env:
            msgs = self.env.get_messages_for_role(self)
            selected_msg = msgs[-1] if msgs else None

        try:
            if action.name == SimpleWriteCode.name:
                idea = ""
                if selected_msg:
                    idea = getattr(selected_msg, "instruct_content", None) or getattr(selected_msg, "content", "") or ""
                result = await action.run(idea)
            elif action.name == SimpleWriteTest.name:
                code_content = ""
                if selected_msg and getattr(selected_msg, "cause_by", "") == SimpleWriteCode.name:
                    code_content = getattr(selected_msg, "content", "") or ""
                elif self.env:
                    latest_code = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                    code_content = getattr(latest_code, "content", "") if latest_code else ""
                result = await action.run(code_content)
            elif action.name == SimpleWriteReview.name:
                # Collect latest code and tests from environment
                code_content = ""
                tests_content = ""
                if self.env:
                    latest_code = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                    latest_tests = self.env.get_latest_message_by_cause(SimpleWriteTest.name)
                    code_content = getattr(latest_code, "content", "") if latest_code else ""
                    tests_content = getattr(latest_tests, "content", "") if latest_tests else ""
                # Fallback to selected message if environment data not found
                if not code_content and selected_msg:
                    code_content = getattr(selected_msg, "instruct_content", "") or ""
                if not tests_content and selected_msg:
                    tests_content = getattr(selected_msg, "content", "") or ""
                result = await action.run(code_content, tests_content)
            else:
                payload = getattr(selected_msg, "content", "") if selected_msg else ""
                result = await action.run(payload)
        except Exception as e:
            if tracer:
                tracer.log("ROLE_ERROR", self.name, f"Error during action: {e}")
            result = f"Error in action {action.name}: {e}"

        # Build response message robustly
        try:
            response = Message(
                content=result,
                role=self.profile,
                cause_by=action.name,
                sent_from=self.name
            )
        except Exception:
            # Fallback minimal message object if pydantic model fails
            class _SimpleMsg:
                def __init__(self, content, role, cause_by, sent_from):
                    self.id = str(uuid.uuid4())
                    self.content = content
                    self.instruct_content = None
                    self.role = role
                    self.cause_by = cause_by
                    self.sent_from = sent_from
                    self.sent_to = None
                    self.send_to = set()
                def __str__(self):
                    return f"Message(role={self.role}, content={self.content[:50]}...)"
            response = _SimpleMsg(result, self.profile, action.name, self.name)

        # Mark processed to avoid duplicates
        try:
            if self.env and selected_msg:
                self.env.mark_processed(self, selected_msg)
        except Exception:
            pass

        if tracer:
            try:
                tracer.log("ROLE_COMPLETE", self.name, "Action completed, message created")
            except Exception:
                pass

        return response

