def _safe_str(value: Any) -> str:
    """Return a stable string representation without raising."""
    try:
        if value is None:
            return ""
        if isinstance(value, str):
            return value
        return str(value)
    except Exception:
        return ""

def _make_message(content: str, role: str, cause_by: str = "", sent_from: Optional[str] = None, instruct_content: Optional[str] = None):
    """
    Robustly construct a Message-like object. Tries the project's Message class first,
    falls back to a minimal object that has the expected attributes.
    """
    content = _safe_str(content)
    try:
        return Message(content=content, instruct_content=instruct_content, role=role, cause_by=cause_by or "", sent_from=sent_from)
    except Exception:
        class Minimal:
            pass
        m = Minimal()
        m.id = str(uuid.uuid4())
        m.content = content
        m.instruct_content = instruct_content
        m.role = role
        m.cause_by = cause_by or ""
        m.sent_from = sent_from
        m.sent_to = None
        m.send_to = set()
        return m

class Action(ABC):
    """Base action with lazy LLM init and safe logging to reduce failure modes."""
    name: str = "Action"

    def __init__(self, *, context: Optional[Context] = None):
        self.context = context
        self._llm: Optional[LLMInterface] = None

    @property
    def llm(self) -> Optional[LLMInterface]:
        """Lazily create LLMInterface; swallow exceptions."""
        if self._llm is None:
            try:
                cfg = getattr(self.context, "config", None)
                if cfg and getattr(cfg, "llm", None):
                    self._llm = LLMInterface(cfg.llm)
            except Exception:
                self._llm = None
        return self._llm

    def _log(self, event: str, details: str):
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            try:
                tracer.log(event, getattr(self, "name", "Action"), details)
            except Exception:
                # Do not propagate tracer errors
                pass

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError

class SimpleWriteCode(Action):
    """Generate Python code given an idea or instruction."""
    name: str = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        idea = _safe_str(idea).strip()
        self._log("ACTION_START", f"idea_len={len(idea)}")
        prompt = (
            "You are an expert Python programmer. Produce concise, correct Python code for the following task.\n\n"
            f"Task: {idea}\n\n"
            "Requirements:\n- Clean, testable code\n- Basic error handling\n- Short explanatory comments\n\nReturn only the code."
        )
        messages = [{"role": "system", "content": "You are an expert Python programmer."},
                    {"role": "user", "content": prompt}]
        try:
            if self.llm:
                resp = await self.llm.ask(messages)
                code = _safe_str(resp)
            else:
                # Deterministic fallback to minimize variability
                code = (
                    f"# Placeholder implementation for: {idea}\n\n"
                    "def solution(*args, **kwargs):\n"
                    "    \"\"\"Placeholder solution.\"\"\"\n"
                    "    try:\n"
                    "        return None\n"
                    "    except Exception:\n"
                    "        raise\n"
                )
        except Exception as e:
            code = f"# Error generating code: {_safe_str(e)}"
            self._log("ACTION_ERROR", f"code_error={_safe_str(e)}")
        self._log("ACTION_END", f"code_len={len(code)}")
        return code

class SimpleWriteTest(Action):
    """Generate pytest-style tests for provided code."""
    name: str = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        code_text = _safe_str(code)[:4000]
        self._log("ACTION_START", f"code_snip_len={len(code_text)}")
        prompt = (
            "You are an expert QA engineer. Write pytest tests for the implementation below.\n\n"
            f"{code_text}\n\n"
            "Requirements:\n- Use pytest\n- Cover typical and edge cases\n- Short docstrings for tests\n\nReturn only test code."
        )
        messages = [{"role": "system", "content": "You are an expert QA engineer."},
                    {"role": "user", "content": prompt}]
        try:
            if self.llm:
                resp = await self.llm.ask(messages)
                tests = _safe_str(resp)
            else:
                tests = (
                    "import pytest\n\n"
                    "def test_placeholder():\n"
                    "    \"\"\"Placeholder positive test.\"\"\"\n"
                    "    assert True\n"
                )
        except Exception as e:
            tests = f"# Error generating tests: {_safe_str(e)}"
            self._log("ACTION_ERROR", f"test_error={_safe_str(e)}")
        self._log("ACTION_END", f"tests_len={len(tests)}")
        return tests

class SimpleWriteReview(Action):
    """Review code and tests and produce concise feedback."""
    name: str = "SimpleWriteReview"

    def __init__(self, *, is_human: bool = False, context: Optional[Context] = None):
        super().__init__(context=context)
        self.is_human = bool(is_human)

    async def run(self, code: str, tests: str) -> str:
        code_text = _safe_str(code)[:2000]
        tests_text = _safe_str(tests)[:2000]
        self._log("ACTION_START", f"code_len={len(code_text)} tests_len={len(tests_text)} human={self.is_human}")
        if self.is_human:
            review = "Human review: Looks reasonable. Consider adding edge-case tests and more explicit error handling."
        else:
            prompt = (
                "You are a senior software engineer. Provide a concise, actionable review of the code and tests below.\n\n"
                f"Code:\n{code_text}\n\nTests:\n{tests_text}\n\nFocus on readability, correctness, coverage, and improvements. Keep it short."
            )
            messages = [{"role": "system", "content": "You are a senior software engineer doing code review."},
                        {"role": "user", "content": prompt}]
            try:
                if self.llm:
                    resp = await self.llm.ask(messages)
                    review = _safe_str(resp)
                else:
                    review = "Automated review: Structure acceptable; add more negative tests and clearer error handling."
            except Exception as e:
                review = f"Review error: {_safe_str(e)}"
                self._log("ACTION_ERROR", f"review_error={_safe_str(e)}")
        self._log("ACTION_END", f"review_len={len(review)}")
        return review

class Role(ABC):
    """Agent role: holds actions, reacts deterministically to messages, minimal mutable state."""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, *, name: Optional[str] = None, profile: Optional[str] = None, context: Optional[Context] = None, env: Optional["Environment"] = None, is_human: bool = False):
        self.name = name or self.name
        self.profile = profile or self.profile
        self.context = context
        self.env = env
        self.is_human = bool(is_human)
        self.actions: List[Action] = []
        # watch_list: names of action outputs (cause_by) this role cares about
        self.watch_list: List[str] = []

    def set_actions(self, actions: List[Action]):
        # ensure action context bound
        prepared: List[Action] = []
        for a in (actions or []):
            try:
                if getattr(a, "context", None) is None:
                    a.context = self.context
            except Exception:
                pass
            prepared.append(a)
        self.actions = prepared

    def _watch(self, actions: List[Union[Type[Action], str]]):
        names: List[str] = []
        for a in (actions or []):
            try:
                if isinstance(a, str):
                    names.append(a)
                elif hasattr(a, "name"):
                    names.append(getattr(a, "name"))
                elif hasattr(a, "__name__"):
                    names.append(getattr(a, "__name__"))
            except Exception:
                continue
        self.watch_list = names

    def _log(self, event: str, details: str):
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            try:
                tracer.log(event, self.name, details)
            except Exception:
                pass

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Deterministic routing: coder handles user instructions; tester reacts to coder outputs; reviewer reacts to tests."""
        if not self.actions:
            self._log("ROLE_NOP", "No actions assigned")
            return None

        action = self.actions[0]
        action_name = getattr(action, "name", "Action")
        self._log("ROLE_ACT", f"action={action_name} msg_id={getattr(message, 'id', None)}")

        try:
            if action_name == SimpleWriteCode.name:
                idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") if message else ""
                result = await action.run(idea or "")
            elif action_name == SimpleWriteTest.name:
                code_text = ""
                # prefer code from message if it's from coder
                if message and getattr(message, "cause_by", "") == SimpleWriteCode.name:
                    code_text = getattr(message, "content", "") or ""
                elif self.env:
                    latest = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                    code_text = getattr(latest, "content", "") if latest else ""
                result = await action.run(code_text or "")
            elif action_name == SimpleWriteReview.name:
                code_text = ""
                tests_text = ""
                if message:
                    if getattr(message, "cause_by", "") == SimpleWriteTest.name:
                        tests_text = getattr(message, "content", "") or ""
                if self.env:
                    if not code_text:
                        latest_code = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                        code_text = getattr(latest_code, "content", "") if latest_code else ""
                    if not tests_text:
                        latest_tests = self.env.get_latest_message_by_cause(SimpleWriteTest.name)
                        tests_text = getattr(latest_tests, "content", "") if latest_tests else ""
                result = await action.run(code_text or "", tests_text or "")
            else:
                payload = getattr(message, "content", "") if message else ""
                result = await action.run(payload)
        except Exception as e:
            err = f"Action {action_name} failed: {_safe_str(e)}"
            self._log("ROLE_ERROR", err)
            result = err

        response = _make_message(content=result, role=self.profile, cause_by=action_name, sent_from=self.name)
        self._log("ROLE_COMPLETE", f"produced_len={len(_safe_str(getattr(response, 'content', '')))} cause_by={getattr(response, 'cause_by', '')}")
        return response

class SimpleCoder(Role):
    name: str = "Alice"
    profile: str = "SimpleCoder"

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.set_actions([SimpleWriteCode(context=self.context)])
        self._watch([])  # listens to explicit user instruction

class SimpleTester(Role):
    name: str = "Bob"
    profile: str = "SimpleTester"

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.set_actions([SimpleWriteTest(context=self.context)])
        self._watch([SimpleWriteCode])

class SimpleReviewer(Role):
    name: str = "Charlie"
    profile: str = "SimpleReviewer"

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.set_actions([SimpleWriteReview(is_human=self.is_human, context=self.context)])
        self._watch([SimpleWriteTest])

class Environment:
    """Environment storing roles and message history with robust operations."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer

    def add_role(self, role: Role):
        try:
            role.env = self
        except Exception:
            pass
        self.roles.append(role)
        if self.tracer:
            try:
                self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")
            except Exception:
                pass

    def publish_message(self, message: Message):
        try:
            self.history.append(message)
        except Exception:
            try:
                m = _make_message(content=_safe_str(getattr(message, "content", message)), role=_safe_str(getattr(message, "role", "System")), cause_by=_safe_str(getattr(message, "cause_by", "")), sent_from=_safe_str(getattr(message, "sent_from", "System")))
                self.history.append(m)
                message = m
            except Exception:
                return
        if self.tracer:
            try:
                sender = getattr(message, "sent_from", "Unknown")
                snippet = _safe_str(getattr(message, "content", ""))[:120]
                cause = getattr(message, "cause_by", "")
                self.tracer.log("ENV_MESSAGE", "Environment", f"From={sender} cause={cause} snippet={snippet}")
            except Exception:
                pass

    def get_messages_for_role(self, role: Role) -> List[Message]:
        if not role.watch_list:
            return []
        res: List[Message] = []
        watched = set(role.watch_list or [])
        for m in self.history:
            try:
                if getattr(m, "cause_by", "") in watched:
                    res.append(m)
            except Exception:
                continue
        return res

    def get_latest_message_by_cause(self, cause_name: str) -> Optional[Message]:
        for m in reversed(self.history):
            try:
                if getattr(m, "cause_by", "") == cause_name:
                    return m
            except Exception:
                continue
        return None

class Team:
    """Orchestrates roles and rounds with deterministic, safe execution to reduce failures."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        try:
            self.context.tracer = self.tracer
        except Exception:
            pass
        self.env = Environment(self.tracer)
        try:
            self.context.env = self.env
        except Exception:
            pass
        self.investment: float = 10.0
        self.idea: str = ""
        self.log_file = log_file

    def hire(self, roles: List[Role]):
        for r in (roles or []):
            try:
                r.context = self.context
                for a in getattr(r, "actions", []) or []:
                    if getattr(a, "context", None) is None:
                        a.context = self.context
            except Exception:
                pass
            self.env.add_role(r)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = _safe_str(idea)
        if self.tracer:
            try:
                self.tracer.log("TEAM_START", "Team", f"Starting project: {self.idea}")
            except Exception:
                pass

    async def run(self, n_round: int = 3):
        n_round = max(1, int(n_round or 1))
        if self.tracer:
            try:
                self.tracer.log("TEAM_RUN", "Team", f"Running {n_round} rounds")
            except Exception:
                pass

        initial = _make_message(content=f"Let's work on this project: {self.idea}", instruct_content=self.idea, role="Human", sent_from="User", cause_by="UserInput")
        self.env.publish_message(initial)

        for rnum in range(n_round):
            if self.tracer:
                try:
                    self.tracer.log("ROUND_START", "Team", f"Round {rnum+1}/{n_round}")
                except Exception:
                    pass

            # iterate over a stable snapshot of roles
            for role in list(self.env.roles):
                response = None
                try:
                    # allow coder(s) to respond to initial instruction in first round
                    if rnum == 0 and any(getattr(a, "name", "") == SimpleWriteCode.name for a in role.actions):
                        response = await role.act(initial)
                    else:
                        relevant = self.env.get_messages_for_role(role)
                        if not relevant:
                            continue
                        response = await role.act(relevant[-1])
                except Exception as e:
                    if self.tracer:
                        try:
                            self.tracer.log("ROLE_EXCEPTION", role.name, _safe_str(e))
                        except Exception:
                            pass
                    continue

                if response:
                    try:
                        if not getattr(response, "cause_by", None):
                            response.cause_by = getattr(role.actions[0], "name", "UnknownAction") if role.actions else "UnknownAction"
                    except Exception:
                        pass
                    self.env.publish_message(response)

            if self.tracer:
                try:
                    self.tracer.log("ROUND_END", "Team", f"Round {rnum+1} completed")
                except Exception:
                    pass

        if self.tracer:
            try:
                self.tracer.log("TEAM_END", "Team", "Project completed")
                summary = f"Project '{self.idea}' completed after {n_round} rounds with {len(self.env.history)} messages exchanged."
                self.tracer.log("SUMMARY", "Team", summary)
            except Exception:
                pass

# EVOLVE-BLOCK-END

