                    review = "Automated review: Acceptable structure; consider more tests and edge cases."
        except Exception as e:
            review = f"Review error: {str(e)}"
        self._log("ACTION_END", f"Review length {len(review)}")
        return review

class Role(ABC):
    """Base role class for agents - simplified decision logic and robust behavior"""
    def __init__(self, name: str = "Role", profile: str = "Default", context: Optional[Context] = None, is_human: bool = False):
        self.name = name
        self.profile = profile
        self.context = context
        self.is_human = is_human
        self.actions: List[Action] = []
        # watch_list contains cause_by strings that this role should respond to
        self.watch_list: Set[str] = set()

    def set_actions(self, actions: List[Action]):
        self.actions = actions or []

    def _watch(self, actions: List[Type[Action]]):
        """Normalize watched action identifiers into string names (robust)"""
        names: Set[str] = set()
        for a in (actions or []):
            try:
                if isinstance(a, str):
                    names.add(a)
                elif hasattr(a, "name"):
                    # instance or class with 'name' attribute
                    names.add(getattr(a, "name"))
                elif hasattr(a, "__name__"):
                    names.add(getattr(a, "__name__"))
            except Exception:
                continue
        self.watch_list = names

    def _log(self, event: str, details: str):
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            try:
                tracer.log(event, self.name, details)
            except Exception:
                pass

    def _select_action_for_message(self, message: Optional[Message]) -> Optional[Action]:
        """
        Deterministic selection:
        - If message has instruct_content -> prefer SimpleWriteCode
        - Else choose action based on watch_list mapping: if the message.cause_by is in this role's watch_list,
          then map that cause to the most appropriate action by known mapping.
        - Else default to first action.
        """
        if not self.actions:
            return None

        # Prefer explicit instruction to coder
        if message and getattr(message, "instruct_content", None):
            for action in self.actions:
                if action.name == "SimpleWriteCode":
                    return action

        cause = getattr(message, "cause_by", "") if message else ""
        if cause and cause in (self.watch_list or set()):
            # Simple mapping: when coder output -> tester, when tester output -> reviewer
            if cause == "SimpleWriteCode":
                for action in self.actions:
                    if action.name == "SimpleWriteTest":
                        return action
            if cause == "SimpleWriteTest":
                for action in self.actions:
                    if action.name == "SimpleWriteReview":
                        return action

        # Default fallback
        return self.actions[0] if self.actions else None

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Perform an action based on the message; always returns a Message-like object or None"""
        self._log("ROLE_ACT", f"Acting on message {getattr(message, 'id', 'none')}")
        if not self.actions:
            self._log("ROLE_SKIP", "No actions")
            return None

        action = self._select_action_for_message(message)
        if not action:
            self._log("ROLE_SKIP", "No action selected")
            return None

        try:
            # Prepare inputs based on action type (explicit and simple)
            if action.name == "SimpleWriteCode":
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "")
                result = await action.run(idea)
            elif action.name == "SimpleWriteTest":
                code_text = ""
                if message and getattr(message, "cause_by", "") == "SimpleWriteCode":
                    code_text = getattr(message, "content", "")
                else:
                    # Try to find last code from environment history
                    code_text = ""
                    try:
                        hist = getattr(self.context, "tracer", None)
                        # Avoid parsing tracer; rely on environment history if available
                        env = getattr(self.context, "env", None)
                        if env:
                            for m in reversed(getattr(env, "history", []) or []):
                                if getattr(m, "cause_by", "") == "SimpleWriteCode":
                                    code_text = getattr(m, "content", "")
                                    break
                    except Exception:
                        code_text = ""
                result = await action.run(code_text)
            elif action.name == "SimpleWriteReview":
                # Attempt to gather latest code and tests deterministically from env.history if available
                code_text = ""
                tests_text = ""
                try:
                    env = getattr(self.context, "env", None)
                    if env:
                        for m in reversed(getattr(env, "history", []) or []):
                            c = getattr(m, "cause_by", "")
                            if not tests_text and c == "SimpleWriteTest":
                                tests_text = getattr(m, "content", "")
                            if not code_text and c == "SimpleWriteCode":
                                code_text = getattr(m, "content", "")
                            if code_text and tests_text:
                                break
                except Exception:
                    code_text = ""
                    tests_text = ""
                # If the incoming message contains one of them, prefer it
                if message:
                    if getattr(message, "cause_by", "") == "SimpleWriteCode":
                        code_text = getattr(message, "content", "") or code_text
                    if getattr(message, "cause_by", "") == "SimpleWriteTest":
                        tests_text = getattr(message, "content", "") or tests_text
                result = await action.run(code_text, tests_text)
            else:
                # Generic single-arg invocation
                result = await action.run(getattr(message, "content", "") if message else "")
        except Exception as e:
            self._log("ROLE_ERROR", f"{action.name} failed: {str(e)}")
            result = f"Action {action.name} encountered an error: {str(e)}"

        # Construct response message robustly
        try:
            response = _make_message(content=result, role=self.profile, cause_by=action.name, sent_from=self.name)
        except Exception:
            # Fallback minimal object
            response = _make_message(content=result, role=self.profile, cause_by=action.name, sent_from=self.name)

        self._log("ROLE_COMPLETE", f"{action.name} produced message {getattr(response, 'id', '')}")
        return response

