            try:
                if isinstance(a, str):
                    names.add(a)
                elif hasattr(a, "name"):
                    # instance or class with 'name' attribute
                    names.add(getattr(a, "name"))
                elif hasattr(a, "__name__"):
                    names.add(getattr(a, "__name__"))
            except Exception:
                continue
        self.watch_list = names

    def _log(self, event: str, details: str):
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            try:
