            return None

        # Prefer explicit instruction to coder
        if message and getattr(message, "instruct_content", None):
            for action in self.actions:
                if action.name == "SimpleWriteCode":
                    return action

        cause = getattr(message, "cause_by", "") if message else ""
        if cause and cause in (self.watch_list or set()):
            # Simple mapping: when coder output -> tester, when tester output -> reviewer
            if cause == "SimpleWriteCode":
                for action in self.actions:
                    if action.name == "SimpleWriteTest":
                        return action
            if cause == "SimpleWriteTest":
                for action in self.actions:
                    if action.name == "SimpleWriteReview":
                        return action

        # Default fallback
        return self.actions[0] if self.actions else None

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Perform an action based on the message; always returns a Message-like object or None"""
        self._log("ROLE_ACT", f"Acting on message {getattr(message, 'id', 'none')}")
        if not self.actions:
            self._log("ROLE_SKIP", "No actions")
            return None

        action = self._select_action_for_message(message)
        if not action:
