class Environment:
    """Environment for multi-agent collaboration"""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer

    def add_role(self, role: Role):
        """Add a role to the environment"""
        # Ensure role has pointer to environment context for history lookups
        try:
            if getattr(role, "context", None) is None:
                role.context = Context()
            # Provide environment reference inside context if possible (safe assignment)
            try:
                role.context.env = self
            except Exception:
                pass
        except Exception:
            pass

        self.roles.append(role)
        if self.tracer:
            try:
                self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")
            except Exception:
                pass

    def get_roles(self, profile: Optional[str] = None) -> List[Role]:
        """Get roles by profile"""
        if profile:
            return [r for r in self.roles if r.profile == profile]
        return self.roles

    def publish_message(self, message: Message):
        """Publish a message to the environment; robustly append and log"""
        try:
            self.history.append(message)
        except Exception:
            try:
                m = _make_message(content=str(getattr(message, "content", message)), role=getattr(message, "role", "Unknown"), cause_by=getattr(message, "cause_by", ""))
                self.history.append(m)
                message = m
            except Exception:
                return
        if self.tracer:
            try:
                self.tracer.log("ENV_MESSAGE", "Environment", f"Message from {getattr(message, 'sent_from', 'Unknown')}: {str(getattr(message, 'content', ''))[:200]}")
            except Exception:
                pass

    def get_messages_for_role(self, role: Role) -> List[Message]:
        """Get messages that a role should respond to based on watched cause_by names"""
        if not role.watch_list:
            return []
        relevant: List[Message] = []
        for msg in self.history:
            try:
                if getattr(msg, "cause_by", "") in role.watch_list:
                    relevant.append(msg)
            except Exception:
                continue
        return relevant

