class Role(ABC):
    """General agent role that holds actions and reacts to environment messages."""
    name: str = "Role"
    profile: str = "Default"

    def __init__(self, *, name: Optional[str] = None, profile: Optional[str] = None, context: Optional[Context] = None, env: Optional["Environment"] = None, is_human: bool = False):
        self.name = name or self.name
        self.profile = profile or self.profile
        self.context = context
        self.env = env
        self.is_human = is_human
        self.actions: List[Action] = []
        # watch_list contains cause_by strings this role should respond to
        self.watch_list: List[str] = []

    def set_actions(self, actions: List[Action]):
        # bind context to actions for consistent logging/LLM usage
        for a in actions:
            if getattr(a, "context", None) is None:
                a.context = self.context
        self.actions = actions

    def _watch(self, actions: List[Union[Type[Action], str]]):
        names: List[str] = []
        for a in actions:
            if isinstance(a, str):
                names.append(a)
            elif hasattr(a, "name"):
                names.append(getattr(a, "name"))
            elif hasattr(a, "__name__"):
                names.append(a.__name__)
        self.watch_list = names

    def _log(self, event: str, details: str):
        if self.context and getattr(self.context, "tracer", None):
            try:
                self.context.tracer.log(event, self.name, details)
            except Exception:
                pass

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Execute the primary action for this role using a straightforward, safe policy."""
        if not self.actions:
            self._log("ROLE_NOP", "No actions assigned")
            return None

        action = self.actions[0]
        self._log("ROLE_ACT", f"action={action.name} msg_id={getattr(message, 'id', None)}")
        try:
            # Routing by action name keeps behavior explicit and simple
            if action.name == SimpleWriteCode.name:
                idea = getattr(message, "instruct_content", None) or getattr(message, "content", "")
                result = await action.run(idea or "")
            elif action.name == SimpleWriteTest.name:
                # Prefer code from the triggering message; fallback to latest code in env
                code_text = ""
                if message and getattr(message, "cause_by", "") == SimpleWriteCode.name:
                    code_text = getattr(message, "content", "") or ""
                elif self.env:
                    latest_code = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                    code_text = getattr(latest_code, "content", "") if latest_code else ""
                result = await action.run(code_text or "")
            elif action.name == SimpleWriteReview.name:
                # Gather latest code and tests from environment
                code_text = ""
                tests_text = ""
                if self.env:
                    latest_code = self.env.get_latest_message_by_cause(SimpleWriteCode.name)
                    latest_tests = self.env.get_latest_message_by_cause(SimpleWriteTest.name)
                    code_text = getattr(latest_code, "content", "") if latest_code else ""
                    tests_text = getattr(latest_tests, "content", "") if latest_tests else ""
                # allow message to override tests
                if message and getattr(message, "cause_by", "") == SimpleWriteTest.name:
                    tests_text = getattr(message, "content", "") or tests_text
                result = await action.run(code_text or "", tests_text or "")
            else:
                # Generic fallback: pass message content or empty string
                payload = getattr(message, "content", "") if message else ""
                result = await action.run(payload)
        except Exception as e:
            err = f"Action {action.name} failed: {e}"
            self._log("ROLE_ERROR", err)
            result = err

        # Construct a Message response compatible with both pydantic and fallback Message
        try:
            response = Message(
                content=result,
                role=self.profile,
                cause_by=getattr(action, "name", "Action"),
                sent_from=self.name
            )
        except Exception:
            # Fallback minimal message-like object
            class _M:
                def __init__(self, content, role, cause_by, sent_from):
                    self.id = str(uuid.uuid4())
                    self.content = content
                    self.instruct_content = None
                    self.role = role
                    self.cause_by = cause_by
                    self.sent_from = sent_from
            response = _M(result, self.profile, getattr(action, "name", "Action"), self.name)

        self._log("ROLE_COMPLETE", f"produced_len={len(_safe_str(getattr(response, 'content', '')))} cause_by={getattr(response, 'cause_by', '')}")
        return response

