    def __init__(self):
        self.api_type = LLMType.OPENAI
        self.model = "gpt-4o"
        self.api_key = None
        self.base_url = "https://api.openai.com/v1"
        self.proxy = ""
        self.temperature = 0.7
        self.max_token = 2048

class Config:
    """Configuration object"""
    def __init__(self):
