def _s(x: Any) -> str:
    """Safe string conversion."""
    try:
        if x is None:
            return ""
        return x if isinstance(x, str) else str(x)
    except Exception:
        return ""

def _log(tracer: Optional[ExecutionTracer], event: str, agent: str, details: str):
    """Never-throw logger."""
    if tracer:
        try:
            tracer.log(event, agent, details)
        except Exception:
            pass

def make_message(content: str, role: str, cause_by: str = "", sent_from: Optional[str] = None, instruct_content: Optional[str] = None) -> Message:
    """Robust message constructor that works for both pydantic and fallback Message."""
    kwargs = {
        "content": _s(content),
        "role": _s(role),
        "cause_by": _s(cause_by),
        "sent_from": _s(sent_from) if sent_from is not None else None,
    }
    if instruct_content is not None:
        kwargs["instruct_content"] = _s(instruct_content)
    try:
        return Message(**kwargs)
    except Exception:
        # Try positional form (fallback Message)
        try:
            return Message(kwargs["content"], kwargs["role"], instruct_content=kwargs.get("instruct_content"), cause_by=kwargs["cause_by"], sent_from=kwargs["sent_from"])
        except Exception:
            # Minimal object with required attributes
            class _M:
                def __init__(self, content, role, cause_by, sent_from, instruct_content=None):
                    self.id = str(uuid.uuid4())
                    self.content = content
                    self.instruct_content = instruct_content
                    self.role = role
                    self.cause_by = cause_by
                    self.sent_from = sent_from
                    self.sent_to = None
                    self.send_to = set()
                def __str__(self):
                    return f"Message(role={self.role}, content={self.content[:50]}...)"
            return _M(kwargs["content"], kwargs["role"], kwargs["cause_by"], kwargs["sent_from"], kwargs.get("instruct_content"))

class Action(ABC):
    """Base action with lazy LLM, safe logging, and unified execute wrapper."""
    name: str = "Action"

    def __init__(self, *, context: Optional[Context] = None):
        self.context = context
        self._llm: Optional[LLMInterface] = None

    @property
    def llm(self) -> Optional[LLMInterface]:
        if self._llm is None and self.context is not None:
            try:
                cfg = getattr(self.context, "config", None)
                if cfg and getattr(cfg, "llm", None):
                    self._llm = LLMInterface(cfg.llm)
            except Exception:
                self._llm = None
        return self._llm

    def _log(self, event: str, details: str):
        _log(getattr(self.context, "tracer", None), event, getattr(self, "name", "Action"), details)

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError

    async def execute(self, message: Optional[Message] = None, env: Optional["Environment"] = None) -> str:
        """Map (message, env) into concrete run() args in one centralized place."""
        try:
            aname = getattr(self, "name", "") or self.__class__.__name__
            if aname == SimpleWriteCode.name:
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") or ""
                return await self.run(_s(idea))
            if aname == SimpleWriteTest.name:
                code = getattr(message, "content", "") if message else ""
                if not code and env:
                    latest = env.get_latest_message_by_cause(SimpleWriteCode.name)
                    if latest:
                        code = getattr(latest, "content", "") or ""
                return await self.run(_s(code))
            if aname == SimpleWriteReview.name:
                # Prefer tests in triggering message; pull latest code/tests from env if missing
                tests = getattr(message, "content", "") if message else ""
                code = getattr(message, "instruct_content", "") if message else ""
                if env:
                    if not code:
                        cmsg = env.get_latest_message_by_cause(SimpleWriteCode.name)
                        code = getattr(cmsg, "content", "") if cmsg else code
                    if not tests:
                        tmsg = env.get_latest_message_by_cause(SimpleWriteTest.name)
                        tests = getattr(tmsg, "content", "") if tmsg else tests
                return await self.run(_s(code), _s(tests))
            # Fallback
            return await self.run(_s(getattr(message, "content", "")) if message else "")
        except Exception as e:
            self._log("ACTION_EXCEPTION", _s(e))
            return f"Error executing {getattr(self, 'name', 'Action')}: {e}"

class SimpleWriteCode(Action):
    name = "SimpleWriteCode"

    async def run(self, idea: str = "") -> str:
        idea_text = (idea or "").strip()
        self._log("ACTION_START", f"Writing code for: {idea_text[:120]}")
        if not idea_text:
            code = (
                "# No task provided. Placeholder implementation.\n"
                "def solution(*args, **kwargs):\n"
                "    \"\"\"Placeholder function.\"\"\"\n"
                "    return None\n"
            )
            self._log("ACTION_END", f"Generated {len(code)} chars (fallback)")
            return code

        prompt = (
            "You are a professional programmer. Write Python code for the following task:\n"
            f"Task: {idea_text}\n\n"
            "Requirements:\n"
            "1. Clean, functional Python\n"
            "2. Basic error handling\n"
            "3. Comments explaining non-obvious logic\n"
            "4. Production-ready where reasonable\n\n"
            "Return only the code."
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt},
        ]
        try:
            if self.llm:
                code = await self.llm.ask(messages)
            else:
                code = (
                    f"# Implementation for: {idea_text}\n"
                    "def placeholder_function():\n"
                    "    \"\"\"Placeholder until LLM is available.\"\"\"\n"
                    "    try:\n"
                    "        return 'ok'\n"
                    "    except Exception as exc:\n"
                    "        raise exc\n"
                )
        except Exception as e:
            code = f"# Error generating code: {e}"
        self._log("ACTION_END", f"Generated {len(_s(code))} characters of code")
        return _s(code)

