class SimpleWriteReview(Action):
    name = "SimpleWriteReview"

    def __init__(self, *, is_human: bool = False, context: Optional[Context] = None):
        super().__init__(context=context)
        self.is_human = bool(is_human)

    async def run(self, code: str = "", tests: str = "") -> str:
        code_text = (code or "").strip()
        tests_text = (tests or "").strip()
        self._log("ACTION_START", f"Reviewing (human={self.is_human})")
        try:
            if self.is_human:
                review = "Human review: Looks good overall; consider more defensive checks and additional edge-case tests."
            else:
                prompt = (
                    "You are a senior code reviewer. Review the following code and tests.\n\n"
                    f"Code (first 1500 chars):\n{code_text[:1500]}\n\n"
                    f"Tests (first 1500 chars):\n{tests_text[:1500]}\n\n"
                    "Provide a brief, actionable review focusing on:\n"
                    "- Code quality and best practices\n"
                    "- Test coverage and gaps\n"
                    "- Potential bugs or edge cases\n"
                    "- Clear suggestions for improvement\n"
                    "Keep it concise."
                )
                messages = [
                    {"role": "system", "content": "You are a senior software engineer doing code review."},
                    {"role": "user", "content": prompt},
                ]
                if self.llm:
                    review = await self.llm.ask(messages)
                else:
                    review = "Automated review: Structure is reasonable; add input validation and more boundary tests."
        except Exception as e:
            review = f"Error during review: {e}"
        self._log("ACTION_END", f"Review completed ({len(_s(review))} chars)")
        return _s(review)

class Role(ABC):
    """Minimal role abstraction that executes a single primary action."""
    def __init__(self, *, name: str = "Role", profile: str = "Default", context: Optional[Context] = None, is_human: bool = False):
        self.name = name
        self.profile = profile
        self.context = context
        self.is_human = bool(is_human)
        self.actions: List[Action] = []
        # watch_list stores cause_by values this role responds to
        self.watch_list: List[str] = []
        # environment reference (set by Team.hire)
        self.env: Optional["Environment"] = None

    def set_actions(self, actions: List[Action]):
        """Bind actions to this role and align contexts."""
        bound: List[Action] = []
        for a in actions or []:
            try:
                a.context = a.context or self.context
            except Exception:
                a.context = self.context
            bound.append(a)
        self.actions = bound

    def _watch(self, actions: List[Type[Action] or str]):
        """Accept action classes or names."""
        names: List[str] = []
        for a in actions or []:
            if isinstance(a, str):
                names.append(a)
            elif isinstance(a, type) and hasattr(a, "name"):
                names.append(getattr(a, "name"))
            elif hasattr(a, "name"):
                names.append(getattr(a, "name"))
        self.watch_list = [n for n in names if n]

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Execute the primary action and return a response message."""
        if not self.actions:
            return None
        action = self.actions[0]
        tracer = getattr(self.context, "tracer", None)
        _log(tracer, "ROLE_ACT", self.name, f"Executing: {getattr(action, 'name', 'Action')}")
        try:
            result = await action.execute(message=message, env=self.env)
        except Exception as e:
            _log(tracer, "ROLE_ERROR", self.name, _s(e))
            result = f"Error in role {self.name}: {e}"

        response = make_message(
            content=_s(result),
            role=self.profile,
            cause_by=getattr(action, "name", ""),
            sent_from=self.name
        )
        _log(tracer, "ROLE_COMPLETE", self.name, "Action completed, message emitted")
        return response

class SimpleCoder(Role):
    """Role that writes code"""
    def __init__(self, **kwargs):
        name = kwargs.get("name", "Alice")
        profile = kwargs.get("profile", "SimpleCoder")
        context = kwargs.get("context")
        super().__init__(name=name, profile=profile, context=context, is_human=kwargs.get("is_human", False))
        self.set_actions([SimpleWriteCode(context=self.context)])
        # Coder responds to the initial user input (handled by Team), not to other messages
        self._watch([])

