class Environment:
    """Environment that stores messages, roles, and prevents reprocessing."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        # Track last processed message id per role to avoid duplicate processing
        self._last_processed: Dict[str, Optional[str]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        self._last_processed.setdefault(role.name, None)
        _log(self.tracer, "ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def get_roles(self, profile: Optional[str] = None) -> List[Role]:
        if profile:
            return [r for r in self.roles if r.profile == profile]
        return list(self.roles)

    def publish_message(self, message: Message):
        """Append message safely and log a short preview."""
        try:
            # Normalize minimal fields
            content = _s(getattr(message, "content", ""))
            setattr(message, "content", content)
            cause = _s(getattr(message, "cause_by", ""))
            setattr(message, "cause_by", cause)
            self.history.append(message)
            preview = content[:120]
            sender = _s(getattr(message, "sent_from", getattr(message, "role", "Unknown")))
            _log(self.tracer, "ENV_MESSAGE", "Environment", f"from={sender} cause={cause} preview={preview}")
        except Exception as e:
            _log(self.tracer, "ENV_ERROR", "Environment", f"publish failed: {_s(e)}")

    def get_messages_for_role(self, role: Role) -> List[Message]:
        """Return at most one unprocessed latest message whose cause_by matches role.watch_list."""
        watched = set(role.watch_list or [])
        if not watched:
            return []
        candidates: List[Message] = []
        for msg in self.history:
            try:
                if getattr(msg, "cause_by", "") in watched:
                    candidates.append(msg)
            except Exception:
                continue
        if not candidates:
            return []
        latest = candidates[-1]
        last_id = self._last_processed.get(role.name)
        if last_id and getattr(latest, "id", None) == last_id:
            return []
        return [latest]

    def get_latest_message_by_cause(self, cause_name: str) -> Optional[Message]:
        for msg in reversed(self.history):
            try:
                if getattr(msg, "cause_by", "") == cause_name:
                    return msg
            except Exception:
                continue
        return None

    def mark_processed(self, role: Role, message: Message):
        try:
            self._last_processed[role.name] = getattr(message, "id", None)
            _log(self.tracer, "ENV_MARKED", "Environment", f"{role.name} processed {getattr(message, 'id', '')}")
        except Exception:
            pass

class Team:
    """Team orchestrator running deterministic rounds."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        try:
            self.context.tracer = self.tracer
        except Exception:
            pass
        self.env = Environment(self.tracer)
        self.investment: float = 10.0
        self.idea: str = ""
        self.log_file = log_file

    def hire(self, roles: List[Role]):
        """Attach shared context/env and align actions' context."""
        for role in roles:
            role.context = role.context or self.context
            role.env = self.env
            for a in getattr(role, "actions", []) or []:
                try:
                    a.context = a.context or self.context
                except Exception:
                    a.context = self.context
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        _log(self.tracer, "TEAM_START", "Team", f"Starting project: {self.idea}")

    async def run(self, n_round: int = 3):
        _log(self.tracer, "TEAM_RUN", "Team", f"Running {n_round} rounds")

        # Initial user instruction
        initial = make_message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput"
        )
        self.env.publish_message(initial)

        for round_num in range(n_round):
            _log(self.tracer, "ROUND_START", "Team", f"Round {round_num + 1}/{n_round}")

            for role in list(self.env.roles):
                response: Optional[Message] = None
                used_message: Optional[Message] = None
                try:
                    # Round 0: coder responds directly to user input
                    if round_num == 0 and isinstance(role, SimpleCoder):
                        used_message = initial
                        response = await role.act(initial)
                    else:
                        relevant = self.env.get_messages_for_role(role)
                        if not relevant:
                            continue
                        used_message = relevant[-1]
                        response = await role.act(used_message)
                except Exception as e:
                    _log(self.tracer, "ROLE_EXCEPTION", getattr(role, "name", "Unknown"), _s(e))
                    response = None

                if response:
                    self.env.publish_message(response)
                    if used_message:
                        self.env.mark_processed(role, used_message)

            _log(self.tracer, "ROUND_END", "Team", f"Round {round_num + 1} completed")

        _log(self.tracer, "TEAM_END", "Team", "Project completed")
        summary = f"Project '{self.idea}' completed after {n_round} rounds with {len(self.env.history)} messages exchanged."
        _log(self.tracer, "SUMMARY", "Team", summary)

