def _preview(text: Optional[str], n: int = 160) -> str:
    if not text:
        return ""
    t = text.replace("\n", " ")
    return (t[:n] + "...") if len(t) > n else t

class Action(ABC):
    """Base action with LLM retry/backoff and deterministic fallback behavior."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None
    max_retries: int = 3
    base_backoff: float = 0.5

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        try:
            if self.context and getattr(self.context, "config", None) and self.context.config.llm:
                self.llm = LLMInterface(self.context.config.llm)
        except Exception:
            self.llm = None

    async def _safe_ask(self, messages: List[Dict[str, str]]) -> str:
        tracer = getattr(self.context, "tracer", None)
        last_err = None
        if not self.llm:
            if tracer:
                tracer.log("LLM_FALLBACK", self.name, "LLM not configured - deterministic fallback returned")
            return "LLM_UNAVAILABLE_FALLBACK"
        for attempt in range(1, self.max_retries + 1):
            try:
                if tracer:
                    tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{self.max_retries}")
                resp = await self.llm.ask(messages)
                if not isinstance(resp, str) or not resp.strip():
                    last_err = "empty_response"
                    raise RuntimeError(last_err)
                if resp.strip().lower().startswith("error"):
                    last_err = resp
                    raise RuntimeError(last_err)
                return resp
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("LLM_ERROR", self.name, f"Attempt {attempt} failed: {last_err[:200]}")
                # backoff with jitter
                await asyncio.sleep(self.base_backoff * (2 ** (attempt - 1)) + random.random() * 0.1)
        # exhausted
        final = f"LLM_FAILURE: {last_err or 'unknown'}"
        if tracer:
            tracer.log("LLM_GIVEUP", self.name, final[:200])
        return final

    @abstractmethod
    async def run(self, *args, **kwargs) -> Any:
        pass

class SimpleWriteCode(Action):
    """Produce a parseable Python module implementing the idea."""
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"Idea preview={_preview(idea,120)}")
        if not idea or not idea.strip():
            if tracer:
                tracer.log("ACTION_WARN", self.name, "Empty idea provided - deterministic fallback returned")
            return (
                "def placeholder(input_value=None):\n"
                "    \"\"\"Fallback placeholder implementation.\"\"\"\n"
                "    return input_value\n"
            )
        prompt = (
            "You are an expert Python developer. Produce a single Python module for the task below. "
            "Return only valid Python source code (no markdown). Include docstrings and input validation.\n\n"
            f"Task: {idea}\n"
        )
        messages = [{"role": "system", "content": "Expert Python developer."},
                    {"role": "user", "content": prompt}]
        code = await self._safe_ask(messages)
        # Validate parseability; if invalid use deterministic fallback
        try:
            ast.parse(code)
            valid = True
        except Exception as e:
            valid = False
            if tracer:
                tracer.log("ACTION_VALIDATION_FAIL", self.name, f"AST parse failed: {type(e).__name__}: {str(e)[:200]}")
            code = (
                "def placeholder(input_value=None):\n"
                "    \"\"\"Fallback placeholder (used when LLM output invalid).\"\"\"\n"
                "    return input_value\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"Produced code len={len(code)} valid={valid}")
        return code

class SimpleWriteTest(Action):
    """Produce pytest-style tests targeting public API of provided code."""
    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"Generating tests for code preview={_preview(code,120)}")
        if not code or not code.strip():
            if tracer:
                tracer.log("ACTION_WARN", self.name, "No code provided - deterministic fallback test returned")
            return (
                "def test_placeholder():\n"
                "    \"\"\"Fallback test that always passes.\"\"\"\n"
                "    assert True\n"
            )
        prompt = (
            "You are a QA engineer. Given the Python module below, write pytest-style tests. "
            "Return only valid pytest code.\n\n"
            f"Module (truncated):\n{(code or '')[:3000]}\n"
        )
        messages = [{"role": "system", "content": "Expert QA engineer."},
                    {"role": "user", "content": prompt}]
        tests = await self._safe_ask(messages)
        # Heuristic validate tests
        try:
            parsed = ast.parse(tests)
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in parsed.body)
            has_assert = "assert" in tests
            if not (has_test_fn or has_assert):
                raise ValueError("No pytest tests or asserts detected")
        except Exception:
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "Tests invalid - using deterministic fallback tests")
            tests = (
                "def test_placeholder():\n"
                "    \"\"\"Fallback basic test.\"\"\"\n"
                "    assert True\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"Generated tests len={len(tests)}")
        return tests

class SimpleWriteReview(Action):
    """Review code and tests; return clear decision token."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"Reviewing code/tests human={self.is_human}")
        if self.is_human:
            return "HUMAN_REVIEW: manual inspection required\nREVIEW_DECISION: REQUEST_CHANGES"
        prompt = (
            "You are a senior code reviewer. Provide a concise review and end the output with a single line:\n"
            "REVIEW_DECISION: APPROVE or REVIEW_DECISION: REQUEST_CHANGES\n\n"
            f"Code (truncated):\n{(code or '')[:2000]}\n\nTests (truncated):\n{(tests or '')[:2000]}\n"
        )
        messages = [{"role": "system", "content": "Senior software engineer."},
                    {"role": "user", "content": prompt}]
        review = await self._safe_ask(messages)
        if not isinstance(review, str) or "REVIEW_DECISION:" not in review:
            # Heuristic decision
            decision = "APPROVE" if ("def " in (code or "") and "assert" in (tests or "")) else "REQUEST_CHANGES"
            review = (review or "Automated review.") + f"\n\nREVIEW_DECISION: {decision}"
            if tracer:
                tracer.log("ACTION_WARN", self.name, f"No decision token from LLM; appended {decision}")
        if tracer:
            tracer.log("ACTION_END", self.name, f"Review len={len(review)}")
        return review

class SimpleVerify(Action):
    """Deterministic verification: syntax, tests presence, asserts, cross-reference; returns RESULT and digest."""
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "Verifying code and tests")
        diagnostics: List[str] = []
        code_ok = False
        tests_ok = False
        has_asserts = False
        ref_count = 0
        code_entities: Set[str] = set()

        # Code parse and entity extraction
        try:
            parsed_code = ast.parse(code or "")
            code_ok = True
            for n in parsed_code.body:
                if isinstance(n, (ast.FunctionDef, ast.ClassDef)):
                    code_entities.add(n.name)
            diagnostics.append(f"code_entities:{len(code_entities)}")
            diagnostics.append("code_syntax: ok")
        except Exception as e:
            diagnostics.append(f"code_syntax: fail ({type(e).__name__}: {str(e)[:120]})")

        # Tests parse and assert presence
        try:
            parsed_tests = ast.parse(tests or "")
            tests_ok = True
            has_asserts = any(isinstance(n, ast.Assert) for n in ast.walk(parsed_tests)) or ("assert" in (tests or ""))
            diagnostics.append("tests_parse: ok")
            diagnostics.append(f"tests_asserts: {has_asserts}")
        except Exception as e:
            diagnostics.append(f"tests_parse: fail ({type(e).__name__}: {str(e)[:120]})")

        # Cross-reference: count referenced code entities in tests
        try:
            if code_entities and tests:
                parsed_tests = parsed_tests if 'parsed_tests' in locals() else ast.parse(tests or "")
                names_in_tests = {n.id for n in ast.walk(parsed_tests) if isinstance(n, ast.Name)}
                ref_count = len(code_entities & names_in_tests)
                diagnostics.append(f"tests_reference_count:{ref_count}")
            else:
                diagnostics.append("tests_reference_count:0")
        except Exception as e:
            diagnostics.append(f"reference_check_error:{type(e).__name__}")

        verified = all([code_ok, tests_ok, has_asserts, ref_count > 0])
        digest = hashlib.sha256(((code or "") + "\n--\n" + (tests or "")).encode("utf-8")).hexdigest()[:12]
        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(diagnostics)
        if tracer:
            tracer.log("ACTION_END", self.name, f"{'PASS' if verified else 'FAIL'} digest={digest}")
        return result

