class Environment:
    """Message bus with routing, delivered tracking, and artifact indexing."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        self._by_cause: Dict[str, List[Message]] = {}
        self._delivered: Dict[str, Set[str]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role {role.name}({role.profile})")

    def publish_message(self, message: Message):
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        self.history.append(message)
        if getattr(message, "cause_by", ""):
            self._by_cause.setdefault(message.cause_by, []).append(message)
        if self.tracer:
            self.tracer.log("ENV_MESSAGE", "Environment", f"Msg {message.id} from {message.sent_from} cause_by={message.cause_by} send_to={getattr(message,'send_to',set())} preview={_preview(message.content,120)}")

    def get_latest_by_cause(self, cause: str) -> Optional[Message]:
        lst = self._by_cause.get(cause, [])
        return lst[-1] if lst else None

    def get_messages_for_role(self, role: Role) -> List[Message]:
        out: List[Message] = []
        seen = self._delivered.setdefault(role.name, set())
        for msg in self.history:
            if getattr(msg, "id", None) in seen:
                continue
            # avoid delivering messages created by the same role
            if getattr(msg, "sent_from", None) == role.name:
                seen.add(getattr(msg, "id", None))
                continue
            targets = getattr(msg, "send_to", None) or set(getattr(msg, "sent_to", None) or set())
            targeted = bool(targets and (role.name in targets or role.profile in targets or "*" in targets))
            watched = getattr(msg, "cause_by", "") in role.watch_list
            # default coder listen to UserInput handled in should_handle
            if targeted or watched or getattr(msg, "cause_by", "") == "UserInput":
                out.append(msg)
                seen.add(getattr(msg, "id", None))
        if out:
            self._delivered[role.name] = seen
        return out

# Team orchestrator

class Team:
    """Orchestrates the agents with deterministic ordering, retries, stable verification and nudging."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.idea: str = ""
        # verification stability tracking
        self._last_digest: Optional[str] = None
        self._streak: int = 0
        self._required_streak: int = 2
        # stagnation detection
        self._no_progress_rounds: int = 0

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment if hasattr(self, "investment") else investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {_preview(idea,200)}")

    async def _invoke_role(self, role: Role, msg: Optional[Message]) -> Optional[Message]:
        """Safely invoke role.act with timeout and return a Message (or None)."""
        try:
            coro = role.act(msg)
            resp = await asyncio.wait_for(coro, timeout=60)
            return resp
        except asyncio.TimeoutError:
            self.tracer.log("ROLE_TIMEOUT", role.name, f"Timeout invoking {role.action.name if role.action else 'action'}")
            return Message(content=f"ERROR: {role.name} timed out", role=role.profile, cause_by="timeout", sent_from=role.name)
        except Exception as e:
            self.tracer.log("ROLE_EXCEPTION", role.name, f"Exception invoking action: {type(e).__name__}: {str(e)[:200]}")
