# Environment

class Environment:
    """Message bus with routing, delivered tracking, and artifact indexing."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        self._by_cause: Dict[str, List[Message]] = {}
        self._delivered: Dict[str, Set[str]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role {role.name}({role.profile})")

    def publish_message(self, message: Message):
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        self.history.append(message)
        if getattr(message, "cause_by", ""):
            self._by_cause.setdefault(message.cause_by, []).append(message)
        if self.tracer:
            self.tracer.log("ENV_MESSAGE", "Environment", f"Msg {message.id} from {message.sent_from} cause_by={message.cause_by} send_to={getattr(message,'send_to',set())} preview={_preview(message.content,120)}")

    def get_latest_by_cause(self, cause: str) -> Optional[Message]:
        lst = self._by_cause.get(cause, [])
        return lst[-1] if lst else None

    def get_messages_for_role(self, role: Role) -> List[Message]:
        out: List[Message] = []
        seen = self._delivered.setdefault(role.name, set())
        for msg in self.history:
            if getattr(msg, "id", None) in seen:
                continue
            # avoid delivering messages created by the same role
            if getattr(msg, "sent_from", None) == role.name:
                seen.add(getattr(msg, "id", None))
                continue
            targets = getattr(msg, "send_to", None) or set(getattr(msg, "sent_to", None) or set())
            targeted = bool(targets and (role.name in targets or role.profile in targets or "*" in targets))
            watched = getattr(msg, "cause_by", "") in role.watch_list
            # default coder listen to UserInput handled in should_handle
            if targeted or watched or getattr(msg, "cause_by", "") == "UserInput":
                out.append(msg)
                seen.add(getattr(msg, "id", None))
        if out:
            self._delivered[role.name] = seen
        return out

# Team orchestrator

class Team:
    """Orchestrates the agents with deterministic ordering, retries, stable verification and nudging."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.idea: str = ""
        # verification stability tracking
        self._last_digest: Optional[str] = None
        self._streak: int = 0
        self._required_streak: int = 2
        # stagnation detection
        self._no_progress_rounds: int = 0

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment if hasattr(self, "investment") else investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {_preview(idea,200)}")

    async def _invoke_role(self, role: Role, msg: Optional[Message]) -> Optional[Message]:
        """Safely invoke role.act with timeout and return a Message (or None)."""
        try:
            coro = role.act(msg)
            resp = await asyncio.wait_for(coro, timeout=60)
            return resp
        except asyncio.TimeoutError:
            self.tracer.log("ROLE_TIMEOUT", role.name, f"Timeout invoking {role.action.name if role.action else 'action'}")
            return Message(content=f"ERROR: {role.name} timed out", role=role.profile, cause_by="timeout", sent_from=role.name)
        except Exception as e:
            self.tracer.log("ROLE_EXCEPTION", role.name, f"Exception invoking action: {type(e).__name__}: {str(e)[:200]}")
            return Message(content=f"ERROR: {role.name} exception: {type(e).__name__}: {str(e)[:200]}", role=role.profile, cause_by="exception", sent_from=role.name)

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Running up to {n_round} rounds (stable_passes={self._required_streak})")
        # initial message targeted to coders
        coder_names = {r.name for r in self.env.roles if isinstance(r, SimpleCoder)}
        initial = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to=coder_names or {"SimpleCoder"}
        )
        self.env.publish_message(initial)

        verified = False
        last_history_len = len(self.env.history)

        # deterministic order
        order = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]

        for round_idx in range(n_round):
            round_num = round_idx + 1
            self.tracer.log("ROUND_START", "Team", f"Round {round_num}/{n_round}")
            any_progress = False

            for role_type in order:
                roles = [r for r in self.env.roles if isinstance(r, role_type)]
                for role in roles:
                    msgs = self.env.get_messages_for_role(role)
                    # allow coder to proactively see initial message on first round
                    if isinstance(role, SimpleCoder) and round_idx == 0:
                        user_msgs = [m for m in self.env.history if getattr(m, "cause_by", "") == "UserInput"]
                        for um in user_msgs:
                            if um not in msgs:
                                msgs.insert(0, um)
                    for msg in msgs:
                        resp = await self._invoke_role(role, msg)
                        # mark delivered (Environment handles delivered tracking)
                        if resp:
                            self.env.publish_message(resp)
                            any_progress = True
                            # If verifier reported PASS, update stability streak
                            if isinstance(role, SimpleVerifier) and isinstance(resp.content, str):
                                content = resp.content
                                if "VERIFICATION_RESULT: PASS" in content:
                                    # extract digest
                                    digest = None
                                    for part in content.split("|"):
                                        p = part.strip()
                                        if p.startswith("digest="):
                                            digest = p.split("=", 1)[1]
                                            break
                                    if digest:
                                        if digest == self._last_digest:
                                            self._streak += 1
                                        else:
                                            self._last_digest = digest
                                            self._streak = 1
                                        self.tracer.log("VERIFIER_UPDATE", "Team", f"digest={digest} streak={self._streak}")
                                    else:
                                        # PASS without digest -> not stable
                                        self._streak = 0
                                        self._last_digest = None
                                        self.tracer.log("VERIFIER_PASS_NO_DIGEST", "Team", "Verifier PASS without digest")
                                    if self._streak >= self._required_streak:
                                        verified = True
                                elif "VERIFICATION_RESULT: FAIL" in content:
                                    # reset on fail
                                    self._streak = 0
                                    self._last_digest = None
                                    self.tracer.log("VERIFIER_FAIL", "Team", "Verifier reported FAIL - resetting streak")

            # round end analysis
            current_history_len = len(self.env.history)
            if current_history_len == last_history_len:
                self._no_progress_rounds += 1
            else:
                self._no_progress_rounds = 0
            last_history_len = current_history_len

            self.tracer.log("ROUND_END", "Team", f"Round {round_num} complete any_progress={any_progress} history_len={len(self.env.history)}")

            # Terminate only on stable verification
            if verified and round_idx >= 1:
                self.tracer.log("TEAM_EARLY_STOP", "Team", f"Verification stable (streak={self._streak}); stopping early")
                break

            # Nudging and deadlock avoidance
            if self._no_progress_rounds == 1:
                # Send a single nudge to coder(s)
                nudge = Message(
                    content=f"Nudge: please refine code for: {self.idea}",
                    instruct_content=self.idea,
                    role="System",
                    sent_from="Orchestrator",
                    cause_by="Nudge",
                    send_to={r.name for r in self.env.roles if isinstance(r, SimpleCoder)}
                )
                self.env.publish_message(nudge)
                # allow immediate processing
                for coder in [r for r in self.env.roles if isinstance(r, SimpleCoder)]:
                    try:
                        resp = await self._invoke_role(coder, nudge)
                        if resp:
                            self.env.publish_message(resp)
                            any_progress = True
                    except Exception as e:
                        self.tracer.log("NUDGE_ERROR", coder.name, f"Nudge handling error: {e}")
            elif self._no_progress_rounds >= 2:
                self.tracer.log("TEAM_NO_PROGRESS", "Team", "No progress for 2 rounds; terminating to avoid livelock")
                break

            await asyncio.sleep(0.01)

        self.tracer.log("TEAM_END", "Team", "Project completed")
        summary = f"Project '{self.idea}' completed after {round_num} rounds with {len(self.env.history)} messages. Verified={verified}"
        self.tracer.log("SUMMARY", "Team", summary)

