import asyncio
import ast
import hashlib
import random
import re
from typing import Tuple

# Simplified, robust orchestration with clear roles, retry logic, and stable verification.

# Tunables
LLM_MAX_RETRIES = 3
LLM_BACKOFF_BASE = 0.5  # seconds
ROLE_MAX_ATTEMPTS = 2
CONSECUTIVE_VERIFICATION_PASSES_REQUIRED = 2
NO_PROGRESS_LIMIT = 2  # rounds

def _preview(text: Optional[str], n: int = 160) -> str:
    try:
        s = str(text or "")
    except Exception:
        s = ""
    return (s[:n] + ("..." if len(s) > n else ""))

def _strip_code_fences(text: Optional[str]) -> str:
    """Remove common Markdown code fences/language hints to reduce syntax failures."""
    if not isinstance(text, str):
        return ""
    t = text.strip()
    # Remove triple backticks with optional language
    if t.startswith("```"):
        t = re.sub(r"^```[\w+-]*\s*", "", t)
        t = re.sub(r"\s*```$", "", t)
    # Remove single backticks artifacts
    t = t.replace("`", "")
    return t.strip()

def _safe_ast_parse(py: Optional[str]) -> Tuple[bool, Optional[ast.AST], Optional[str]]:
    try:
        tree = ast.parse(py or "")
        return True, tree, None
    except Exception as e:
        return False, None, str(e)

class Action(ABC):
    """Base action with robust LLM retry/backoff and standardized contract."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
            try:
                self.llm = LLMInterface(self.context.config.llm)
            except Exception:
                self.llm = None

    async def _ask_llm(self, messages: List[Dict[str, str]]) -> str:
        tracer = getattr(self.context, "tracer", None)
        last_err = None
        if not self.llm:
            if tracer:
                tracer.log("LLM_FALLBACK", self.name, "LLMInterface unavailable")
            return "LLM_ERROR:UNAVAILABLE"

        for attempt in range(1, LLM_MAX_RETRIES + 1):
            try:
                if tracer:
                    tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{LLM_MAX_RETRIES}")
                resp = await self.llm.ask(messages)
                if not isinstance(resp, str) or not resp.strip():
                    last_err = "empty_response"
                    raise RuntimeError("Empty response")
                if resp.strip().lower().startswith("error") or "error communicating" in resp.lower():
                    last_err = resp
                    raise RuntimeError(_preview(resp))
                # success
                clean = _strip_code_fences(resp)
                if tracer:
                    tracer.log("LLM_OK", self.name, f"Response len={len(clean)}")
                return clean
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {_preview(last_err)}")
                if attempt < LLM_MAX_RETRIES:
                    backoff = LLM_BACKOFF_BASE * (2 ** (attempt - 1))
                    jitter = random.uniform(0, backoff * 0.15)
                    await asyncio.sleep(backoff + jitter)
        if tracer:
            tracer.log("LLM_GIVEUP", self.name, f"Final error: {_preview(last_err)}")
        return f"LLM_ERROR:{last_err}"

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError()

class SimpleWriteCode(Action):
    """Produce initial implementation from the idea, with validation and fallback."""
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"Idea len={len(idea or '')}")
        if not idea or not str(idea).strip():
            return (
                "\"\"\"Fallback module (no idea provided).\"\"\"\n"
                "def generated_solution(x=None):\n"
                "    \"\"\"Echo the input (returns None if x is None).\"\"\"\n"
                "    return x\n"
            )
        prompt = (
            "You are a professional Python developer. Produce a single Python module implementing the task.\n"
            "Requirements:\n"
            "- Clean, testable functions/classes with docstrings\n"
            "- Defensive input validation\n"
            "- Only return Python source (no backticks or explanation)\n\n"
            f"Task:\n{idea}"
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt},
        ]
        code = await self._ask_llm(messages)

        # Validate parseable Python; fallback if invalid or LLM_ERROR
        ok, _, err = _safe_ast_parse(code)
        if not ok or code.startswith("LLM_ERROR"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, f"Using fallback due to parse error: {_preview(err)}")
            return (
                "\"\"\"Deterministic fallback implementation.\"\"\"\n"
                "def generated_solution(value=None):\n"
                "    \"\"\"Safe fallback that returns the value unchanged.\"\"\"\n"
                "    return value\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"Code len={len(code)} (syntax_ok)")
        return code

class SimpleWriteTest(Action):
    """Generate pytest tests for the provided code, with validation and fallback."""
    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"Code len={len(code or '')}")
        if not code or not code.strip():
            return "def test_placeholder():\n    assert True\n"

        # Extract public function/class symbols to guide testing and fallback
        func_names: List[str] = []
        class_names: List[str] = []
        ok, tree, _ = _safe_ast_parse(code)
        if ok and tree:
            for n in ast.walk(tree):
                if isinstance(n, ast.FunctionDef) and not n.name.startswith("_"):
                    func_names.append(n.name)
                if isinstance(n, ast.ClassDef) and not n.name.startswith("_"):
                    class_names.append(n.name)
        target_name = (func_names + class_names + ["generated_solution"])[0]

        prompt = (
            "You are a QA engineer. Write pytest-style tests for the provided Python module.\n"
            "Requirements:\n"
            "- Use pytest assertions\n"
            "- Cover typical behavior and edge cases\n"
            "- Return only test code\n\n"
            f"Public symbols detected: {', '.join(func_names + class_names) or '(none)'}\n\n"
            f"Code (truncated):\n{(code or '')[:3500]}"
        )
        messages = [
            {"role": "system", "content": "You are an expert QA engineer."},
            {"role": "user", "content": prompt},
        ]
        tests = await self._ask_llm(messages)

        # Validate: must parse and include 'assert' or a test_ function
        ok_t, t_tree, err = _safe_ast_parse(tests)
        has_test_fn = bool(t_tree and any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(t_tree or ast.Module(body=[]))))
        has_assert = "assert" in (tests or "")
        if not ok_t or not (has_test_fn or has_assert) or tests.startswith("LLM_ERROR"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, f"Tests invalid; fallback ({_preview(err)})")
            return (
                "import pytest\n\n"
                f"def test_{target_name}_basic():\n"
                f"    try:\n"
                f"        from builtins import callable as _callable\n"
                f"        assert _callable({target_name})  # type: ignore[name-defined]\n"
                f"    except Exception:\n"
                f"        assert True\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"Tests len={len(tests)} (syntax_ok)")
        return tests

class SimpleWriteReview(Action):
    """Produce a concise review and a verdict line."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"human={self.is_human}")
        # Quick static checks to seed the review
        static_issues: List[str] = []
        ok_c, _, e_c = _safe_ast_parse(code)
        ok_t, _, e_t = _safe_ast_parse(tests)
        if not ok_c and e_c:
            static_issues.append(f"code_syntax_error: {_preview(e_c, 120)}")
        if not ok_t and e_t:
            static_issues.append(f"tests_syntax_error: {_preview(e_t, 120)}")

        if self.is_human:
            decision = "VERDICT: FAIL" if static_issues else "VERDICT: PASS"
            return ("HUMAN_REVIEW: " + ("; ".join(static_issues) if static_issues else "Looks good.")) + f"\n{decision}"

        prompt = (
            "You are a senior engineer. Provide a terse, actionable review (bulleted issues if any) and end with one line:\n"
            "VERDICT: PASS or VERDICT: FAIL\n\n"
            f"Static hints: {static_issues or 'none'}\n\n"
            f"Code:\n{(code or '')[:2000]}\n\n"
            f"Tests:\n{(tests or '')[:2000]}"
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code review."},
            {"role": "user", "content": prompt},
        ]
        review = await self._ask_llm(messages)
        if not isinstance(review, str) or review.startswith("LLM_ERROR"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "Review unavailable; default to FAIL")
            return ("- Automated review unavailable\n" + ("- " + "; ".join(static_issues) + "\n" if static_issues else "")) + "VERDICT: FAIL"
        if "VERDICT:" not in review:
            review = review.strip() + "\nVERDICT: FAIL"
        if tracer:
            tracer.log("ACTION_END", self.name, f"Review len={len(review)}")
        return review

class SimpleVerify(Action):
    """Deterministic verification using static analysis and references."""
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "Verifying artifacts")

        details: List[str] = []
        code_ok, code_tree, e_code = _safe_ast_parse(code)
        tests_ok, tests_tree, e_tests = _safe_ast_parse(tests)

        if code_ok:
            details.append("code_syntax: ok")
        else:
            details.append(f"code_syntax: fail ({_preview(e_code, 120)})")
        has_asserts = bool(re.search(r"\bassert\b", tests or ""))
        if tests_ok and (has_asserts or "def test_" in (tests or "")):
            details.append("tests_syntax_and_presence: ok")
        else:
            if not tests_ok:
                details.append(f"tests_syntax: fail ({_preview(e_tests, 120)})")
            else:
                details.append("tests_presence: fail (no asserts/tests)")

        referenced = False
        if code_ok and tests_ok and code_tree and tests_tree:
            code_names = {n.name for n in ast.walk(code_tree) if isinstance(n, (ast.FunctionDef, ast.ClassDef))}
            test_names = {n.id for n in ast.walk(tests_tree) if isinstance(n, ast.Name)}
            if code_names:
                referenced = bool(code_names & test_names)
                details.append(f"tests_reference_count: {len(code_names & test_names)}")
            else:
                # If no defs in code, relax reference requirement
                referenced = True
                details.append("tests_reference_count: relaxed_ok")
        verified = bool(code_ok and tests_ok and has_asserts and referenced)

        digest = hashlib.sha256(((code or "") + "\n<<<TESTS>>>\n" + (tests or "")).encode("utf-8")).hexdigest()[:12]
        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(details)
        if tracer:
            tracer.log("ACTION_END", self.name, result)
        return result

class Role(ABC):
    """Base role with single action, clear watch triggers, and idempotent processing."""
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    actions: List[Action] = []
    watch_list: List[str] = []  # action names
    env: Optional["Environment"] = None

    def __init__(self, **kwargs):
        self.name = kwargs.get("name", self.name)
        self.profile = kwargs.get("profile", self.profile)
        self.context = kwargs.get("context")
        self.is_human = kwargs.get("is_human", False)
        self.actions = []
        self.watch_list = []
        self.env = kwargs.get("env", None)
        self._processed_ids: Set[str] = set()

    def set_actions(self, actions: List[Action]):
        self.actions = actions

    def watch_actions(self, actions: List[Type[Action]]):
        self.watch_list = [a.name for a in actions]

    def _should_respond(self, msg: Message) -> bool:
        if not msg:
            return False
        mid = getattr(msg, "id", None)
        if mid and mid in self._processed_ids:
            return False
        # Explicit routing takes precedence
        send_to = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None)
        if send_to:
            if isinstance(send_to, (set, list)) and (self.name in send_to or self.profile in send_to or "*" in send_to):
                return True
            if isinstance(send_to, str) and (send_to in {self.name, self.profile, "*"}):
                return True
            return False
        # Watch triggers by cause_by
        if getattr(msg, "cause_by", "") in self.watch_list:
            return True
        # User input by default routes to coder(s)
        if getattr(msg, "cause_by", "") == "UserInput" and "Coder" in self.profile:
            return True
        return False

    def _latest_content(self, cause_name: str) -> str:
        if not self.env:
            return ""
        for m in reversed(self.env.history):
            if getattr(m, "cause_by", "") == cause_name:
                return getattr(m, "content", "") or ""
        return ""

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        tracer = getattr(self.context, "tracer", None)
        if not self.actions:
            if tracer:
                tracer.log("ROLE_NO_ACTION", self.name, "No actions")
            return None

        if message and not self._should_respond(message):
            if tracer:
                tracer.log("ROLE_SKIP", self.name, f"Skip msg={getattr(message, 'id', None)}")
            return None

        action = self.actions[0]
        attempts = 0
        last_err = None

        while attempts < ROLE_MAX_ATTEMPTS:
            attempts += 1
            try:
                if isinstance(action, SimpleWriteCode):
                    idea = (getattr(message, "instruct_content", None) or getattr(message, "content", "") or "").strip()
                    out = await action.run(idea)
                    send_to = {"SimpleTester"}
                elif isinstance(action, SimpleWriteTest):
                    code_text = getattr(message, "content", "") or self._latest_content(SimpleWriteCode.name)
                    out = await action.run(code_text)
                    send_to = {"SimpleReviewer", "SimpleVerifier"}
                elif isinstance(action, SimpleWriteReview):
                    code_text = self._latest_content(SimpleWriteCode.name)
                    tests_text = self._latest_content(SimpleWriteTest.name)
                    out = await action.run(code_text, tests_text)
                    send_to = {"SimpleVerifier", "SimpleCoder"}
                elif isinstance(action, SimpleVerify):
                    code_text = self._latest_content(SimpleWriteCode.name)
                    tests_text = self._latest_content(SimpleWriteTest.name)
                    out = await action.run(code_text, tests_text)
                    send_to = set()  # broadcast
                else:
                    out = await action.run(getattr(message, "content", "") if message else "")
                    send_to = set()

                if message and getattr(message, "id", None):
                    self._processed_ids.add(message.id)

                resp = Message(
                    content=out,
                    role=self.profile,
                    cause_by=action.name,
                    sent_from=self.name,
                    send_to=send_to
                )
                if tracer:
                    tracer.log("ROLE_COMPLETE", self.name, f"Produced msg={getattr(resp, 'id', None)} send_to={list(send_to) if isinstance(send_to, set) else send_to}")
                return resp
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("ROLE_ERROR", self.name, f"Attempt {attempts} failed: {_preview(last_err)}")

        fail_msg = f"ROLE_FAILED: {self.name} after {ROLE_MAX_ATTEMPTS} attempts - {_preview(last_err)}"
        if tracer:
            tracer.log("ROLE_FAIL", self.name, fail_msg)
        return Message(
            content=fail_msg,
            role=self.profile,
            cause_by=getattr(action, "name", "Unknown"),
            sent_from=self.name,
            send_to={"Team"}
        )

