class Environment:
    """Message bus and routing with simple processed tracking and artifact indexing."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        # processed map: (role.name, msg.id)
        self._processed: Set[Tuple[str, str]] = set()
        # index by cause_by for quick artifact lookup
        self._index_by_cause: Dict[str, List[Message]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # ensure id exists
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        self.history.append(message)
        cb = getattr(message, "cause_by", "")
        if cb:
            self._index_by_cause.setdefault(cb, []).append(message)
        if self.tracer:
            preview = _preview(getattr(message, "content", ""), 160).replace("\n", " ")
            self.tracer.log("ENV_MESSAGE", "Environment", f"From {message.sent_from} cause_by={cb} send_to={getattr(message, 'send_to', None)} preview={preview}")

    def mark_processed(self, role: Role, message: Message):
        mid = getattr(message, "id", None)
        if mid:
            self._processed.add((role.name, mid))
            if self.tracer:
                self.tracer.log("ENV_PROCESSED", "Environment", f"{role.name} processed {mid}")

    def has_processed(self, role: Role, message: Message) -> bool:
        mid = getattr(message, "id", None)
        if not mid:
            return False
        return (role.name, mid) in self._processed

    def get_messages_for_role(self, role: Role) -> List[Message]:
        relevant: List[Message] = []
        for msg in self.history:
            mid = getattr(msg, "id", None)
            if not mid:
                continue
            if self.has_processed(role, msg):
                continue
            if getattr(msg, "sent_from", None) == role.name:
                continue
            # Explicit routing
            send_to = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None)
            if send_to:
                if isinstance(send_to, (set, list)):
                    if role.name in send_to or role.profile in send_to or "*" in send_to:
                        relevant.append(msg)
                        continue
                elif isinstance(send_to, str) and send_to in {role.name, role.profile, "*"}:
                    relevant.append(msg)
                    continue
            # Watch triggers
            if getattr(msg, "cause_by", "") in role.watch_list:
                relevant.append(msg)
                continue
            # User input by default to coder(s)
            if getattr(msg, "cause_by", "") == "UserInput" and "Coder" in role.profile:
                relevant.append(msg)
                continue
        return relevant

class Team:
    """Orchestrates collaboration with deterministic order and stable termination."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self.log_file = log_file
        self._pass_streak: int = 0
        self._last_digest: Optional[str] = None

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Up to {n_round} rounds; require {CONSECUTIVE_VERIFICATION_PASSES_REQUIRED} consecutive PASS")
        # Initial user instruction to coder(s)
        initial = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to={"SimpleCoder"}
        )
        self.env.publish_message(initial)

        order = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]
        no_progress_rounds = 0
        last_history_len = len(self.env.history)
        verified = False

        for r_idx in range(n_round):
            round_num = r_idx + 1
            self.tracer.log("ROUND_START", "Team", f"Round {round_num}/{n_round}")
            before_len = len(self.env.history)

            for RoleClass in order:
                for role in [rr for rr in self.env.roles if isinstance(rr, RoleClass)]:
                    msgs = self.env.get_messages_for_role(role)
                    # Ensure coder sees initial message on first round
                    if isinstance(role, SimpleCoder) and r_idx == 0:
                        if not msgs or all(m.id != initial.id for m in msgs):
                            msgs = [initial] + msgs
                    for msg in msgs:
                        try:
                            resp = await role.act(msg)
                            # mark processed regardless of outcome to avoid reprocessing same input
                            self.env.mark_processed(role, msg)
                            if resp:
                                self.env.publish_message(resp)
                                # Handle verifier outputs
                                if isinstance(role, SimpleVerifier) and isinstance(resp.content, str):
                                    text = resp.content
                                    if "VERIFICATION_RESULT: PASS" in text:
                                        # Extract digest if present
                                        digest = None
                                        for part in text.split("|"):
                                            part = part.strip()
                                            if part.startswith("digest="):
                                                digest = part.split("=", 1)[1]
                                                break
                                        if digest and digest == self._last_digest:
                                            self._pass_streak += 1
                                        else:
                                            self._last_digest = digest
                                            self._pass_streak = 1
                                        self.tracer.log("VERIFIER_PASS", "Team", f"streak={self._pass_streak} digest={digest or 'none'}")
                                    elif "VERIFICATION_RESULT: FAIL" in text:
                                        self._pass_streak = 0
                                        self.tracer.log("VERIFIER_FAIL", "Team", "Reset pass streak")
                        except Exception as e:
                            self.tracer.log("ROLE_EXCEPTION", getattr(role, "name", "Role"), f"act error: {_preview(str(e), 160)}")

            made_progress = len(self.env.history) > before_len
            if not made_progress:
                no_progress_rounds += 1
            else:
                no_progress_rounds = 0

            self.tracer.log("ROUND_END", "Team", f"Round {round_num} progress={made_progress} history={len(self.env.history)} streak={self._pass_streak}")

            # Termination: require consecutive PASS confirmations
            if self._pass_streak >= CONSECUTIVE_VERIFICATION_PASSES_REQUIRED:
                verified = True
                self.tracer.log("TEAM_VERIFIED", "Team", f"Stable verification reached (streak={self._pass_streak})")
                break

            if no_progress_rounds >= NO_PROGRESS_LIMIT:
                self.tracer.log("TEAM_NO_PROGRESS", "Team", f"No progress for {no_progress_rounds} rounds; stopping")
                break

            await asyncio.sleep(0.01)

        self.tracer.log("TEAM_END", "Team", "Project run finished")
        summary = f"Project '{self.idea}' finished after {round_num} rounds with {len(self.env.history)} messages. Verified={verified}"
        self.tracer.log("SUMMARY", "Team", summary)

