import asyncio
import ast
import random
import hashlib
from typing import Tuple, Iterable

# Simplified, robust orchestration with clear roles, strong validation, and reliable termination.

LLM_MAX_RETRIES = 3
LLM_BACKOFF_BASE = 0.5
ROLE_MAX_ATTEMPTS = 2
VERIFY_REQUIRED_STREAK = 2
NO_PROGRESS_GRACE = 2  # stop after 2 consecutive rounds with no new messages

class Action(ABC):
    """Base action with robust LLM wrapper and a simple contract: run(...) -> str."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None
    max_retries: int = LLM_MAX_RETRIES
    base_backoff: float = LLM_BACKOFF_BASE

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        try:
            if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
                self.llm = LLMInterface(self.context.config.llm)
        except Exception:
            self.llm = None

    async def _call_llm(self, messages: List[Dict[str, str]]) -> str:
        """Call LLM with retries/backoff; return string or 'LLM_ERROR:<reason>'."""
        tracer = getattr(self.context, "tracer", None)
        if not self.llm:
            if tracer:
                tracer.log("LLM_UNAVAILABLE", self.name, "No LLMInterface, using deterministic marker")
            return "LLM_ERROR:UNAVAILABLE"

        last_err = None
        for attempt in range(1, self.max_retries + 1):
            try:
                if tracer:
                    tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{self.max_retries}")
                resp = await self.llm.ask(messages)
                # Validate basic success
                if not isinstance(resp, str) or not resp.strip():
                    last_err = "empty_response"
                    raise RuntimeError("Empty LLM response")
                low = resp.strip().lower()
                if low.startswith("error") or "error communicating" in low:
                    last_err = resp
                    raise RuntimeError(f"LLM returned error-like text: {resp[:200]}")
                if tracer:
                    tracer.log("LLM_OK", self.name, f"len={len(resp)}")
                return resp
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {last_err[:200]}")
                # exponential backoff with jitter
                if attempt < self.max_retries:
                    backoff = self.base_backoff * (2 ** (attempt - 1))
                    jitter = random.uniform(0, backoff * 0.1)
                    await asyncio.sleep(backoff + jitter)

        if tracer:
            tracer.log("LLM_GIVEUP", self.name, f"Giving up: {last_err}")
        return f"LLM_ERROR:{last_err or 'unknown'}"

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError()


class SimpleWriteCode(Action):
    """Produce an initial implementation from an idea."""
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"idea_len={len(idea or '')}")
        if not idea or not idea.strip():
            # deterministic, parseable fallback
            code = (
                '"""Fallback implementation (empty idea)."""\n'
                "def placeholder(value=None):\n"
                "    \"\"\"Return input as-is.\"\"\"\n"
                "    return value\n"
            )
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "Empty idea -> placeholder")
            return code

        prompt = (
            "You are an expert Python developer. Produce a single Python module for the task below.\n"
            "Return only valid Python code (no markdown). Include docstrings and defensive checks where sensible.\n\n"
            f"Task:\n{idea}"
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt}
        ]
        code = await self._call_llm(messages)
        if isinstance(code, str) and code.startswith("LLM_ERROR:"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "LLM unavailable -> fallback code")
            return (
                '"""Fallback implementation (LLM error)."""\n'
                "def placeholder(value=None):\n"
                "    \"\"\"Return input; ensures pipeline continuity.\"\"\"\n"
                "    return value\n"
            )
        # Ensure parseable Python
        try:
            ast.parse(code)
        except Exception as e:
            if tracer:
                tracer.log("ACTION_INVALID", self.name, f"Parse error -> fallback: {str(e)[:120]}")
            return (
                '"""Fallback after parse error.""\"\n'
                "def placeholder(value=None):\n"
                "    return value\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"code_len={len(code)}")
        return code


class SimpleWriteTest(Action):
    """Generate pytest-style tests for provided code."""
    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"code_len={len(code or '')}")
        if not code or not code.strip():
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "No code -> minimal tests")
            return "def test_placeholder():\n    assert True\n"

        # Extract a few public symbol candidates to guide tests
        symbols: List[str] = []
        try:
            tree = ast.parse(code)
            for n in tree.body:
                if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_"):
                    symbols.append(n.name)
        except Exception:
            symbols = []

        prompt = (
            "You are an expert QA engineer. Write pytest tests for the given module.\n"
            "Return ONLY pytest-compatible Python code. Include at least one test_ function and asserts.\n\n"
            f"Public symbols (heuristic): {', '.join(symbols[:5]) or '(none detected)'}\n\n"
            f"Module (truncated):\n{(code or '')[:3000]}"
        )
        messages = [
            {"role": "system", "content": "You are an expert QA engineer."},
            {"role": "user", "content": prompt}
        ]
        tests = await self._call_llm(messages)
        if isinstance(tests, str) and tests.startswith("LLM_ERROR:"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "LLM error -> minimal tests")
            return "def test_placeholder():\n    assert True\n"

        # Validate tests parse and contain asserts or test_ fn
        try:
            parsed = ast.parse(tests)
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed)) or ("assert " in tests)
            if not (has_test_fn or has_assert):
                raise ValueError("No test_ function or assert found")
        except Exception as e:
            if tracer:
                tracer.log("ACTION_INVALID", self.name, f"Invalid tests -> fallback ({str(e)[:120]})")
            return "def test_placeholder():\n    assert True\n"

        if tracer:
            tracer.log("ACTION_END", self.name, f"tests_len={len(tests)}")
        return tests


class SimpleWriteReview(Action):
    """Review code + tests and produce concise actionable feedback with verdict."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"human={self.is_human}")
        # Static check first
        issues: List[str] = []
        try:
            ast.parse(code or "")
        except Exception as e:
            issues.append(f"code_syntax_error:{str(e)[:160]}")
        try:
            ast.parse(tests or "")
        except Exception as e:
            issues.append(f"tests_syntax_error:{str(e)[:160]}")

        if self.is_human:
            decision = "REQUEST_CHANGES" if issues else "APPROVE"
            review = "HUMAN_REVIEW: " + ("; ".join(issues) if issues else "no static issues") + f"\nREVIEW_DECISION: {decision}"
            if tracer:
                tracer.log("ACTION_END", self.name, "Human review simulated")
            return review

        prompt = (
            "You are a senior reviewer. Provide a concise, actionable review for the code and tests below.\n"
            "End with a single line: REVIEW_DECISION: APPROVE or REVIEW_DECISION: REQUEST_CHANGES\n\n"
            f"STATIC_ISSUES: {issues}\n\n"
            f"Code (truncated):\n{(code or '')[:1500]}\n\n"
            f"Tests (truncated):\n{(tests or '')[:1500]}"
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code reviews."},
            {"role": "user", "content": prompt}
        ]
        review = await self._call_llm(messages)
        if isinstance(review, str) and review.startswith("LLM_ERROR:"):
            decision = "REQUEST_CHANGES" if issues else "APPROVE"
            review = "Automated review unavailable; static assessment applied.\n" + f"REVIEW_DECISION: {decision}"
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "LLM review unavailable -> static decision")

        if "REVIEW_DECISION:" not in (review or ""):
            decision = "REQUEST_CHANGES" if issues else "APPROVE"
            review = (review or "Review incomplete").strip() + f"\nREVIEW_DECISION: {decision}"

        if tracer:
            tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
        return review


class SimpleVerify(Action):
    """Deterministic verification: syntax, tests presence, cross-reference, and stability digest."""
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "Verifying artifacts")
        diagnostics: List[str] = []
        code_ok = False
        tests_ok = False
        referenced = False

        # Code syntax and defs
        code_defs: Set[str] = set()
        try:
            parsed_code = ast.parse(code or "")
            code_ok = True
            code_defs = {n.name for n in parsed_code.body if isinstance(n, (ast.FunctionDef, ast.ClassDef))}
            diagnostics.append(f"code_defs:{len(code_defs)}")
        except Exception as e:
            diagnostics.append(f"code_parse_error:{str(e)[:160]}")

        # Tests syntax and presence of asserts/test_ functions
        try:
            parsed_tests = ast.parse(tests or "")
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed_tests))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed_tests)) or ("assert " in (tests or ""))
            tests_ok = bool(has_test_fn or has_assert)
            diagnostics.append("tests_has_test_fn" if has_test_fn else "tests_no_test_fn")
            diagnostics.append("tests_has_assert" if has_assert else "tests_no_assert")
        except Exception as e:
            diagnostics.append(f"tests_parse_error:{str(e)[:160]}")

        # Cross-reference: tests mention at least one code def
        try:
            if code_defs and tests_ok:
                tests_text = tests or ""
                referenced = any((name in tests_text) for name in code_defs)
                diagnostics.append(f"tests_reference:{'ok' if referenced else 'missing'}")
            else:
                diagnostics.append("tests_reference:skipped")
        except Exception as e:
            diagnostics.append(f"reference_check_error:{str(e)[:160]}")

        verified = code_ok and tests_ok and referenced
        # stable digest
        try:
            digest_src = (code or "").encode("utf-8") + b"||" + (tests or "").encode("utf-8")
            digest = hashlib.sha256(digest_src).hexdigest()[:12]
        except Exception:
            digest = "nodigest"

        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(diagnostics)
        if tracer:
            tracer.log("ACTION_END", self.name, result)
        return result


class Role(ABC):
    """Base role with explicit watch/trigger logic and idempotent processing."""
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    actions: List[Action] = []
    watch_list: List[str] = []  # action.name or cause tokens
    env: Optional["Environment"] = None
    is_human: bool = False

    def __init__(self, **kwargs):
        self.name = kwargs.get("name", self.name)
        self.profile = kwargs.get("profile", self.profile)
        self.context = kwargs.get("context")
        self.is_human = kwargs.get("is_human", False)
        self.actions = []
        self.watch_list = []
        self.env = kwargs.get("env", None)
        self._processed_ids: Set[str] = set()

    def set_actions(self, actions: List[Action]):
        self.actions = actions

    def watch_actions(self, actions: Iterable[Type[Action]]):
        self.watch_list = [a.name for a in actions]

    def should_respond(self, message: Message) -> bool:
        if not message:
            return False
        mid = getattr(message, "id", None)
        if mid and mid in self._processed_ids:
            return False
        if getattr(message, "sent_from", None) == self.name:
            return False
        # Explicit addressing
        send_to = getattr(message, "send_to", None) or getattr(message, "sent_to", None)
        if send_to:
            try:
                if isinstance(send_to, (set, list)):
                    if self.name in send_to or self.profile in send_to or "*" in send_to:
                        return True
                elif isinstance(send_to, str):
                    if send_to in (self.name, self.profile, "*"):
                        return True
            except Exception:
                pass
            return False
        # Watch-list based on cause_by
        if getattr(message, "cause_by", "") in self.watch_list:
            return True
        # Default: coder reacts to UserInput
        if getattr(message, "cause_by", "") == "UserInput" and "Coder" in self.profile:
            return True
        return False

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        tracer = getattr(self.context, "tracer", None)
        if not self.actions:
            if tracer:
                tracer.log("ROLE_NO_ACTION", self.name, "No actions configured")
            return None
        if message is not None and not self.should_respond(message):
            if tracer:
                tracer.log("ROLE_SKIP", self.name, f"Skip msg={getattr(message,'id',None)}")
            return None

        action = self.actions[0]
        if tracer:
            tracer.log("ROLE_ACT", self.name, f"Executing {action.name}")

        # Try a bounded number of times for role action robustness
        attempts = 0
        last_err = None
        while attempts < ROLE_MAX_ATTEMPTS:
            attempts += 1
            try:
                if isinstance(action, SimpleWriteCode):
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") if message else ""
                    out = await action.run(idea)
                    recipients = {"SimpleTester"}
                elif isinstance(action, SimpleWriteTest):
                    # Look up latest code if not explicitly provided
                    code_text = getattr(message, "content", "") if message else ""
                    if not code_text and getattr(self, "env", None):
                        latest_code = self.env.get_latest_artifact(SimpleWriteCode.name)
                        code_text = latest_code.content if latest_code else ""
                    out = await action.run(code_text)
                    recipients = {"SimpleReviewer", "SimpleVerifier"}
                elif isinstance(action, SimpleWriteReview):
                    code_msg = self.env.get_latest_artifact(SimpleWriteCode.name) if getattr(self, "env", None) else None
                    tests_msg = self.env.get_latest_artifact(SimpleWriteTest.name) if getattr(self, "env", None) else None
                    out = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                    # Route back if requesting changes, else to verifier
                    lowered = (out or "").lower()
                    if "request_changes" in lowered or "not_ready" in lowered:
                        recipients = {"SimpleCoder", "SimpleTester"}
                    else:
                        recipients = {"SimpleVerifier"}
                elif isinstance(action, SimpleVerify):
                    code_msg = self.env.get_latest_artifact(SimpleWriteCode.name) if getattr(self, "env", None) else None
                    tests_msg = self.env.get_latest_artifact(SimpleWriteTest.name) if getattr(self, "env", None) else None
                    out = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                    recipients = set()  # broadcast / orchestrator handles
                else:
                    out = await action.run(getattr(message, "content", "") if message else "")
                    recipients = set()

                # mark processed to avoid duplicate work
                if message and getattr(message, "id", None):
                    self._processed_ids.add(message.id)

                response = Message(
                    content=out,
                    role=self.profile,
                    cause_by=action.name,
                    sent_from=self.name,
                    send_to=recipients
                )
                if tracer:
                    tracer.log("ROLE_COMPLETE", self.name, f"Produced msg={getattr(response,'id',None)} send_to={recipients}")
                return response
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("ROLE_ERROR", self.name, f"Attempt {attempts} failed: {last_err[:160]}")
                await asyncio.sleep(0.05 * attempts)

        # If all attempts failed, emit an error message to keep pipeline moving
        fail_msg = Message(
            content=f"ROLE_FAILED: {self.name} after {ROLE_MAX_ATTEMPTS} attempts - {last_err}",
            role=self.profile,
            cause_by=getattr(action, "name", "Unknown"),
            sent_from=self.name,
            send_to={"Team"}
        )
        return fail_msg


