class Environment:
    """Message bus with deterministic routing, delivery tracking, and artifact lookup."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        # per-role delivered messages
        self._delivered: Dict[str, Set[str]] = {}
        # index by cause_by for quick latest artifact fetch
        self._by_cause: Dict[str, List[Message]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        self._delivered.setdefault(role.name, set())
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # ensure id and normalize send_to to set
        if not getattr(message, "id", None):
            message.id = str(uuid.uuid4())
        if getattr(message, "send_to", None) is None:
            try:
                message.send_to = set(getattr(message, "sent_to", set()) or set())
            except Exception:
                message.send_to = set()
        self.history.append(message)
        cb = getattr(message, "cause_by", "")
        if cb:
            self._by_cause.setdefault(cb, []).append(message)
        if self.tracer:
            preview = (message.content or "")[:160].replace("\n", " ")
            self.tracer.log("ENV_MESSAGE", "Environment", f"From {message.sent_from} cause_by={cb} send_to={list(getattr(message,'send_to',set()))} preview={preview}")

    def get_messages_for_role(self, role: Role) -> List[Message]:
        """Return messages not yet delivered to this role and intended for it (explicitly or by watch)."""
        out: List[Message] = []
        delivered = self._delivered.setdefault(role.name, set())
        for msg in self.history:
            mid = getattr(msg, "id", None)
            if not mid or mid in delivered:
                continue
            if getattr(msg, "sent_from", None) == role.name:
                delivered.add(mid)
                continue
            targeted = False
            send_to = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None)
            try:
                if isinstance(send_to, (set, list)):
                    targeted = role.name in send_to or role.profile in send_to or "*" in send_to
                elif isinstance(send_to, str):
                    targeted = send_to in (role.name, role.profile, "*")
            except Exception:
                targeted = False

            watched = getattr(msg, "cause_by", "") in role.watch_list
            user_input = getattr(msg, "cause_by", "") == "UserInput" and "Coder" in role.profile

            if targeted or watched or user_input:
                out.append(msg)
                delivered.add(mid)
        return out

    def get_latest_artifact(self, cause_name: str) -> Optional[Message]:
        msgs = self._by_cause.get(cause_name, [])
        return msgs[-1] if msgs else None


class Team:
    """Orchestrates roles with deterministic order, robust retries, and stable verification termination."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self._verify_streak: int = 0
        self._verify_required: int = VERIFY_REQUIRED_STREAK
        self._last_digest: Optional[str] = None

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def _process_role(self, role: Role) -> int:
        """Process all pending messages for a role. Return number of produced messages."""
        produced = 0
        msgs = self.env.get_messages_for_role(role)
        for msg in msgs:
            try:
                resp = await role.act(msg)
            except Exception as e:
                self.tracer.log("ROLE_EXCEPTION", role.name, f"Exception during act: {str(e)}")
                resp = Message(content=f"ERROR: role {role.name} crashed: {type(e).__name__}", role=role.profile, cause_by="RoleCrash", sent_from=role.name, send_to=set())
            if resp:
                self.env.publish_message(resp)
                produced += 1
                # track verification streak
                if isinstance(role, SimpleVerifier):
                    content = resp.content or ""
                    if "VERIFICATION_RESULT: PASS" in content:
                        digest = None
                        for token in content.split("|"):
                            token = token.strip()
                            if token.startswith("digest="):
                                digest = token.split("=", 1)[1].strip()
                                break
                        if digest and digest == self._last_digest:
                            self._verify_streak += 1
                        elif digest:
                            self._last_digest = digest
                            self._verify_streak = 1
                        else:
                            self._last_digest = None
                            self._verify_streak += 1
                        self.tracer.log("VERIFY_UPDATE", "Team", f"streak={self._verify_streak} digest={digest}")
                    else:
                        if self._verify_streak:
                            self.tracer.log("VERIFY_RESET", "Team", f"reset streak {self._verify_streak}->0")
                        self._verify_streak = 0
                        self._last_digest = None
        return produced

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Running up to {n_round} rounds; require {self._verify_required} stable passes")
        # Initial message routed to coder(s)
        coder_targets = {r.profile for r in self.env.roles if isinstance(r, SimpleCoder)} or {"SimpleCoder"}
        initial_msg = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to=coder_targets
        )
        self.env.publish_message(initial_msg)

        no_progress_rounds = 0
        last_history_len = len(self.env.history)

        # Deterministic role order
        order = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]

        for round_idx in range(n_round):
            round_num = round_idx + 1
            self.tracer.log("ROUND_START", "Team", f"Round {round_num}/{n_round}")
            round_new_msgs = 0

            for RoleClass in order:
                for role in [r for r in self.env.roles if isinstance(r, RoleClass)]:
                    produced = await self._process_role(role)
                    round_new_msgs += produced

            # Progress and termination checks
            if len(self.env.history) == last_history_len:
                no_progress_rounds += 1
            else:
                no_progress_rounds = 0
            last_history_len = len(self.env.history)

            self.tracer.log("ROUND_END", "Team", f"Round {round_num} produced={round_new_msgs} verify_streak={self._verify_streak}")

            # Stop once verified stably
            if self._verify_streak >= self._verify_required:
                self.tracer.log("TEAM_EARLY_STOP", "Team", f"Verification stable for {self._verify_streak} passes; stopping")
                break

            # If stalled, nudge coder(s) and allow quick follow-up
            if no_progress_rounds >= NO_PROGRESS_GRACE or round_new_msgs == 0:
                self.tracer.log("TEAM_NUDGE", "Team", f"Nudging coder(s) (no_progress_rounds={no_progress_rounds}, new_msgs={round_new_msgs})")
                for coder in [r for r in self.env.roles if isinstance(r, SimpleCoder)]:
                    nudge = Message(
                        content=f"Nudge: please refine or retry implementation for: {self.idea}",
                        instruct_content=self.idea,
                        role="System",
                        sent_from="Orchestrator",
                        cause_by="Nudge",
                        send_to={coder.profile}
                    )
                    self.env.publish_message(nudge)
                    await self._process_role(coder)
                if no_progress_rounds >= NO_PROGRESS_GRACE:
                    # After nudge if still stalled next iteration, termination will occur naturally by rounds limit

                    # Small pause to yield control
                    await asyncio.sleep(0.01)

        self.tracer.log("TEAM_END", "Team", "Project run complete")
        summary = f"Project '{self.idea}' finished after {round_num} rounds with {len(self.env.history)} messages; verify_streak={self._verify_streak}"
        self.tracer.log("SUMMARY", "Team", summary)

