import asyncio
import ast
import random
import re
from typing import Tuple, Any, Dict

# Goals:
# - Clear single-responsibility roles with explicit routing and watch logic.
# - Robust LLM/API calls with retries and jitter.
# - Deterministic verification with digest and stable consecutive PASS requirement.
# - Simplified, readable orchestration and environment routing.
# - Error-tolerant fallbacks that keep the pipeline moving.

LLM_MAX_RETRIES = 3
LLM_BACKOFF_BASE = 0.5
ROLE_MAX_ATTEMPTS = 2
REQUIRED_STABLE_PASSES = 2
NO_PROGRESS_LIMIT = 2  # consecutive rounds


class Action(ABC):
    """Base action with robust LLM wrapper and clear run contract."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        try:
            if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
                self.llm = LLMInterface(self.context.config.llm)
        except Exception:
            self.llm = None

    async def _ask_llm(self, messages: List[Dict[str, str]]) -> str:
        """Ask LLM with retries/backoff. Returns string or 'LLM_ERROR:<reason>'."""
        tracer = getattr(self.context, "tracer", None)
        if not self.llm:
            if tracer:
                tracer.log("LLM_FALLBACK", self.name, "LLM unavailable; returning deterministic error")
            return "LLM_ERROR:unavailable"

        last_err = None
        for attempt in range(1, LLM_MAX_RETRIES + 1):
            try:
                if tracer:
                    tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{LLM_MAX_RETRIES}")
                resp = await self.llm.ask(messages)
                if not isinstance(resp, str) or not resp.strip():
                    last_err = "empty_response"
                    raise RuntimeError("Empty response")
                low = resp.strip().lower()
                if low.startswith("error") or "error communicating" in low:
                    last_err = resp
                    raise RuntimeError("Error response")
                if tracer:
                    tracer.log("LLM_OK", self.name, f"len={len(resp)}")
                return resp
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {last_err[:160]}")
                if attempt < LLM_MAX_RETRIES:
                    backoff = LLM_BACKOFF_BASE * (2 ** (attempt - 1))
                    jitter = random.uniform(0, backoff * 0.15)
                    await asyncio.sleep(backoff + jitter)
        if tracer:
            tracer.log("LLM_GIVEUP", self.name, f"Exhausted retries: {last_err}")
        return f"LLM_ERROR:{last_err or 'unknown'}"

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        """Run the action and return text output."""
        raise NotImplementedError()


class SimpleWriteCode(Action):
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"idea_len={len(idea or '')}")
        if not idea or not idea.strip():
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "Empty idea -> placeholder code")
            return (
                "def placeholder(value=None):\n"
                "    \"\"\"Fallback placeholder implementation.\"\"\"\n"
                "    return value\n"
            )
        prompt = (
            "You are an expert Python developer. Produce a single Python module implementing the task.\n"
            "Constraints:\n- Only Python code (no backticks)\n- Include docstrings and input validation\n- Keep functions small and testable\n\n"
            f"Task: {idea}"
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt},
        ]
        code = await self._ask_llm(messages)
        if code.startswith("LLM_ERROR"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "LLM error -> deterministic fallback code")
            return (
                f'"""Fallback implementation for: {idea[:200]}"""\n\n'
                "def placeholder(input_value=None):\n"
                "    \"\"\"Echo-like fallback for when code generation fails.\"\"\"\n"
                "    if input_value is None:\n"
                "        return None\n"
                "    return input_value\n"
            )
        # syntax validation
        try:
            ast.parse(code)
        except Exception as e:
            if tracer:
                tracer.log("ACTION_VALIDATION_FAIL", self.name, f"Parse error: {e}")
            return (
                f'"""Fallback due to parse error for: {idea[:200]}"""\n\n'
                "def placeholder(value=None):\n"
                "    \"\"\"Fallback after parse error.\"\"\"\n"
                "    return value\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"code_len={len(code)}")
        return code


class SimpleWriteTest(Action):
    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"code_len={len(code or '')}")
        if not code or not code.strip():
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "No code -> minimal test")
            return "def test_placeholder():\n    assert True\n"

        # Extract top-level names for reference
        names: List[str] = []
        try:
            tree = ast.parse(code)
            for n in tree.body:
                if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_"):
                    names.append(n.name)
        except Exception:
            names = []

        prompt = (
            "You are a QA engineer. Write pytest-style tests for the provided module.\n"
            "- Use def test_* functions and assert statements.\n"
            "- Cover typical and edge cases.\n"
            "- Return only test code.\n\n"
            f"Public symbols: {', '.join(names[:8]) or '(none detected)'}\n\n"
            f"Code (truncated):\n{(code or '')[:3000]}"
        )
        messages = [
            {"role": "system", "content": "You are an expert QA engineer."},
            {"role": "user", "content": prompt},
        ]
        tests = await self._ask_llm(messages)
        # validate tests
        def _fallback_tests(target: str) -> str:
            return (
                "import pytest\n\n"
                f"def test_{target}_sanity():\n"
                "    \"\"\"Fallback sanity test.\"\"\"\n"
                "    assert True\n"
            )

        if tests.startswith("LLM_ERROR"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "LLM error -> fallback tests")
            return _fallback_tests(names[0] if names else "placeholder")
        try:
            parsed = ast.parse(tests)
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed)) or ("assert" in (tests or ""))
            if not (has_test_fn or has_assert):
                raise ValueError("No tests/asserts found")
        except Exception:
            if tracer:
                tracer.log("ACTION_VALIDATION_FAIL", self.name, "Tests invalid -> fallback")
            return _fallback_tests(names[0] if names else "placeholder")

        if tracer:
            tracer.log("ACTION_END", self.name, f"tests_len={len(tests)}")
        return tests


class SimpleWriteReview(Action):
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"human={self.is_human}")
        # Static checks
        issues: List[str] = []
        try:
            ast.parse(code or "")
        except Exception as e:
            issues.append(f"code_syntax_error:{str(e)[:120]}")
        try:
            ast.parse(tests or "")
        except Exception as e:
            issues.append(f"tests_syntax_error:{str(e)[:120]}")

        if self.is_human:
            review = "HUMAN_REVIEW: " + ("; ".join(issues) if issues else "OK")
            if tracer:
                tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
            return review

        prompt = (
            "You are a senior reviewer. Provide a concise actionable review.\n"
            "End with a single line: VERDICT: PASS or VERDICT: FAIL.\n\n"
            f"Code:\n{(code or '')[:1500]}\n\n"
            f"Tests:\n{(tests or '')[:1500]}\n\n"
            f"STATIC_ISSUES: {issues or 'none'}"
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code review."},
            {"role": "user", "content": prompt},
        ]
        review = await self._ask_llm(messages)
        if review.startswith("LLM_ERROR") or not isinstance(review, str) or not review.strip():
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "Review LLM error -> request change")
            return "REQUEST_CHANGE: Automated reviewer failed. Please perform manual review.\nVERDICT: FAIL"
        if "VERDICT:" not in review:
            review = review.rstrip() + "\nVERDICT: FAIL"
        if tracer:
            tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
        return review


class SimpleVerify(Action):
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "Verifying artifacts")
        diagnostics: List[str] = []
        code_ok = False
        tests_ok = False
        referenced = False

        # Code syntax and public definitions
        public_defs: Set[str] = set()
        try:
            code_tree = ast.parse(code or "")
            public_defs = {n.name for n in ast.walk(code_tree) if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_")}
            code_ok = True
            diagnostics.append(f"code_defs:{len(public_defs)}")
        except Exception as e:
            diagnostics.append(f"code_parse_error:{str(e)[:120]}")

        # Tests syntax and presence
        try:
            tests_tree = ast.parse(tests or "")
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(tests_tree))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(tests_tree)) or ("assert" in (tests or ""))
            tests_ok = bool(has_test_fn or has_assert)
            diagnostics.append("tests_presence:ok" if tests_ok else "tests_presence:none")
        except Exception as e:
            diagnostics.append(f"tests_parse_error:{str(e)[:120]}")

        # Cross-reference: tests mention code symbols
        try:
            if public_defs and tests_ok:
                name_refs = {n.id for n in ast.walk(ast.parse(tests or "")) if isinstance(n, ast.Name)}
                referenced = bool(public_defs & name_refs)
                diagnostics.append("tests_reference:ok" if referenced else "tests_reference:none")
            else:
                diagnostics.append("tests_reference:skip")
        except Exception as e:
            diagnostics.append(f"reference_check_error:{str(e)[:120]}")

        verified = bool(code_ok and tests_ok and (referenced or not public_defs))

        # Stable digest to confirm successive identical pass
        try:
            import hashlib
            digest = hashlib.sha256(((code or "") + "\n--TESTS--\n" + (tests or "")).encode("utf-8")).hexdigest()[:12]
        except Exception:
            digest = "nodigest"

        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(diagnostics)
        if tracer:
            tracer.log("ACTION_END", self.name, result)
        return result


class Role(ABC):
    """
    Base role with single-responsibility action, explicit watch/routing, and idempotent processing.
    """
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    actions: List[Action] = []
    watch_list: List[str] = []  # action names
    env: Optional["Environment"] = None

    def __init__(self, **kwargs):
        self.name = kwargs.get("name", self.name)
        self.profile = kwargs.get("profile", self.profile)
        self.context = kwargs.get("context")
        self.is_human = kwargs.get("is_human", False)
        self.actions = []
        self.watch_list = []
        self.env = kwargs.get("env", None)
        # Local processed to avoid repeated handling of same msg by a role instance
        self._processed_message_ids: Set[str] = set()

    def set_actions(self, actions: List[Action]):
        self.actions = actions

    def watch_actions(self, actions: List[Type[Action]]):
        self.watch_list = [a.name for a in actions]

    def should_respond(self, message: Message) -> bool:
        """Decide whether to respond based on explicit routing, watch triggers, and prior processing."""
        if not message:
            return False
        # Skip if we already processed this message
        if getattr(message, "id", None) in self._processed_message_ids:
            return False
        # Skip self-authored
        if getattr(message, "sent_from", None) == self.name:
            return False
        # Prefer explicit routing (send_to supports set/list or string)
        targets = getattr(message, "send_to", None) or getattr(message, "sent_to", None)
        if targets:
            if isinstance(targets, (set, list)):
                if self.name in targets or self.profile in targets or "*" in targets:
                    return True
            elif isinstance(targets, str):
                if targets in (self.name, self.profile, "*"):
                    return True
            # if targets specified but not for us, don't respond
            return False
        # Watch-list by cause_by
        if getattr(message, "cause_by", "") in self.watch_list:
            return True
        # Default route: initial user input goes to coder
        if getattr(message, "cause_by", "") == "UserInput" and "Coder" in self.profile:
            return True
        return False

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Execute the single action for this role and produce a routed Message."""
        tracer = getattr(self.context, "tracer", None)
        if not self.actions:
            if tracer:
                tracer.log("ROLE_NO_ACTION", self.name, "No actions configured")
            return None

        action = self.actions[0]
        if message is not None and not self.should_respond(message):
            if tracer:
                tracer.log("ROLE_SKIP", self.name, f"Skip msg id={getattr(message,'id',None)}")
            return None

        attempts = 0
        last_err = None
        while attempts < ROLE_MAX_ATTEMPTS:
            attempts += 1
            try:
                if isinstance(action, SimpleWriteCode):
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") or ""
                    out = await action.run(idea)
                    recipients = {"SimpleTester"}
                elif isinstance(action, SimpleWriteTest):
                    code_text = getattr(message, "content", "") or ""
                    if not code_text and getattr(self, "env", None):
                        # Find latest code artifact
                        for m in reversed(self.env.history):
                            if m.cause_by == SimpleWriteCode.name:
                                code_text = m.content
                                break
                    out = await action.run(code_text)
                    recipients = {"SimpleReviewer", "SimpleVerifier"}
                elif isinstance(action, SimpleWriteReview):
                    code_text, tests_text = "", ""
                    if getattr(self, "env", None):
                        for m in reversed(self.env.history):
                            if m.cause_by == SimpleWriteCode.name and not code_text:
                                code_text = m.content
                            if m.cause_by == SimpleWriteTest.name and not tests_text:
                                tests_text = m.content
                            if code_text and tests_text:
                                break
                    out = await action.run(code_text, tests_text)
                    recipients = {"SimpleVerifier", "SimpleCoder"} if "REQUEST_CHANGE" in (out or "").upper() else {"SimpleVerifier"}
                elif isinstance(action, SimpleVerify):
                    code_text, tests_text = "", ""
                    if getattr(self, "env", None):
                        for m in reversed(self.env.history):
                            if m.cause_by == SimpleWriteCode.name and not code_text:
                                code_text = m.content
                            if m.cause_by == SimpleWriteTest.name and not tests_text:
                                tests_text = m.content
                            if code_text and tests_text:
                                break
                    out = await action.run(code_text, tests_text)
                    recipients = set()  # broadcast semantics
                else:
                    out = await action.run(getattr(message, "content", "") if message else "")
                    recipients = set()

                # mark input as processed
                if message and getattr(message, "id", None):
                    self._processed_message_ids.add(message.id)

                response = Message(
                    content=out,
                    role=self.profile,
                    cause_by=action.name,
                    sent_from=self.name,
                    send_to=recipients
                )
                if tracer:
                    tracer.log("ROLE_COMPLETE", self.name, f"Produced msg cause_by={response.cause_by} send_to={recipients}")
                return response
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("ROLE_ERROR", self.name, f"Attempt {attempts} failed: {last_err[:160]}")
                await asyncio.sleep(0.05 * attempts)

        # Exhausted attempts -> return failure message
        failure = f"ROLE_FAILED: {self.name} after {ROLE_MAX_ATTEMPTS} attempts - {last_err}"
        if tracer:
            tracer.log("ROLE_FAIL", self.name, failure)
        return Message(
            content=failure,
            role=self.profile,
            cause_by=getattr(action, "name", "Unknown"),
            sent_from=self.name,
            send_to={"Team"}
        )


