class Environment:
    """
    Message bus with routing and per-role processed tracking.
    - history: ordered messages
    - _processed: msg_id -> set(role.name) to avoid duplicate handling
    """
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        self._processed: Dict[str, Set[str]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        self.history.append(message)
        self._processed.setdefault(message.id, set())
        if self.tracer:
            preview = (message.content or "")[:160].replace("\n", " ")
            self.tracer.log("ENV_MESSAGE", "Environment", f"from={message.sent_from} cause_by={message.cause_by} send_to={getattr(message,'send_to',None)} preview={preview}")

    def mark_processed(self, message: Message, role: Role):
        if getattr(message, "id", None):
            self._processed.setdefault(message.id, set()).add(role.name)
            if self.tracer:
                self.tracer.log("ENV_PROCESSED", "Environment", f"{role.name} processed {message.id}")

    def has_been_processed_by(self, message: Message, role: Role) -> bool:
        return role.name in self._processed.get(getattr(message, "id", ""), set())

    def get_messages_for_role(self, role: Role) -> List[Message]:
        """Relevant messages for role: explicit routing first, then watch triggers. Skip processed/self-authored."""
        relevant: List[Message] = []
        for msg in self.history:
            mid = getattr(msg, "id", None)
            if not mid:
                continue
            if self.has_been_processed_by(msg, role):
                continue
            if getattr(msg, "sent_from", None) == role.name:
                continue
            # explicit addressing
            send_to = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None)
            matched = False
            if send_to:
                if isinstance(send_to, (set, list)) and (role.name in send_to or role.profile in send_to or "*" in send_to):
                    relevant.append(msg)
                    matched = True
                elif isinstance(send_to, str) and send_to in (role.name, role.profile, "*"):
                    relevant.append(msg)
                    matched = True
                if matched:
                    continue
            # watch-list by cause_by
            if getattr(msg, "cause_by", "") in role.watch_list:
                relevant.append(msg)
        return relevant


class Team:
    """
    Orchestrates roles with deterministic ordering and stable verification-based termination.
    - Requires REQUIRED_STABLE_PASSES consecutive PASS with the same digest.
    - Nudges coder on stalls before terminating for no progress.
    """
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self.log_file = log_file
        # verification stability tracking
        self._last_digest: Optional[str] = None
        self._pass_streak: int = 0

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Run up to {n_round} rounds; require {REQUIRED_STABLE_PASSES} stable passes")
        # Initial user instruction to coder(s)
        initial = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to={"SimpleCoder"}
        )
        self.env.publish_message(initial)

        order = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]
        no_progress_rounds = 0
        last_history_len = len(self.env.history)
        rounds_ran = 0

        for r in range(n_round):
            rounds_ran = r + 1
            self.tracer.log("ROUND_START", "Team", f"Round {rounds_ran}/{n_round}")
            progress = False

            for RoleClass in order:
                for role in [ro for ro in self.env.roles if isinstance(ro, RoleClass)]:
                    msgs = self.env.get_messages_for_role(role)
                    # Guarantee coder sees initial message in first round
                    if isinstance(role, SimpleCoder) and r == 0 and initial not in msgs:
                        msgs = [initial] + msgs
                    for msg in msgs:
                        try:
                            resp = await role.act(msg)
                            # mark processed regardless of result to avoid loops
                            self.env.mark_processed(msg, role)
                            if resp:
                                self.env.publish_message(resp)
                                progress = True
                                # verification tracking
                                if isinstance(role, SimpleVerifier) and isinstance(resp.content, str):
                                    content = resp.content
                                    if "VERIFICATION_RESULT: PASS" in content:
                                        # extract digest if present
                                        digest = None
                                        for part in content.split("|"):
                                            p = part.strip()
                                            if p.startswith("digest="):
                                                digest = p.split("=", 1)[1]
                                                break
                                        if digest:
                                            if digest == self._last_digest:
                                                self._pass_streak += 1
                                            else:
                                                self._last_digest = digest
                                                self._pass_streak = 1
                                            self.tracer.log("VERIFY_PASS", "Team", f"digest={digest} streak={self._pass_streak}")
                                        else:
                                            # pass without digest counts as 1 but cannot be stable across rounds
                                            self._last_digest = None
                                            self._pass_streak = 1
                                    elif "VERIFICATION_RESULT: FAIL" in content:
                                        if self._pass_streak > 0:
                                            self.tracer.log("VERIFY_RESET", "Team", f"Reset streak {self._pass_streak}->0 due to FAIL")
                                        self._pass_streak = 0
                                        self._last_digest = None
                            # note role failures
                            if resp and isinstance(resp.content, str) and resp.content.startswith("ROLE_FAILED"):
                                self.tracer.log("TEAM_NOTE", "Team", f"{resp.content[:160]}")
                        except Exception as e:
                            self.tracer.log("ROLE_EXCEPTION", role.name, f"Exception during act: {type(e).__name__}: {str(e)[:160]}")

            # Termination and stall handling
            current_len = len(self.env.history)
            if current_len == last_history_len:
                no_progress_rounds += 1
            else:
                no_progress_rounds = 0
            last_history_len = current_len

            self.tracer.log("ROUND_END", "Team", f"progress={progress} streak={self._pass_streak} history={len(self.env.history)}")

            # Verified if stable streak achieved
            if self._pass_streak >= REQUIRED_STABLE_PASSES:
                self.tracer.log("TEAM_VERIFIED", "Team", f"Stable verification streak={self._pass_streak}; stopping")
                break

            # Nudge coder on first stall, then terminate if persists
            if no_progress_rounds == 1:
                nudge = Message(
                    content=f"Please refine or improve the implementation for: {self.idea}",
                    instruct_content=self.idea,
                    role="System",
                    sent_from="Orchestrator",
                    cause_by="Nudge",
                    send_to={"SimpleCoder"}
                )
                self.env.publish_message(nudge)
            elif no_progress_rounds >= NO_PROGRESS_LIMIT:
                self.tracer.log("TEAM_NO_PROGRESS", "Team", f"No progress for {no_progress_rounds} rounds; stopping")
                break

            await asyncio.sleep(0.01)

        self.tracer.log("TEAM_END", "Team", "Project run finished")
        verified = self._pass_streak >= REQUIRED_STABLE_PASSES
        summary = f"Project '{self.idea}' finished after {rounds_ran} rounds with {len(self.env.history)} messages. Verified={verified}"
        self.tracer.log("SUMMARY", "Team", summary)

