# This section contains the multi-agent system logic that will be evolved

import asyncio
import ast
import hashlib
import random
from typing import Tuple, Iterable

# Tunable robustness parameters
LLM_MAX_RETRIES = 3
LLM_BACKOFF_BASE = 0.5
ROLE_MAX_ATTEMPTS = 2
VERIFY_REQUIRED_STREAK = 2
NO_PROGRESS_GRACE = 2  # rounds without new messages before terminating

# ---------------------------
# Actions (single responsibility, robust LLM wrappers)
# ---------------------------

class Action(ABC):
    """Base Action providing a safe LLM call with retries and deterministic fallback."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None
    max_retries: int = LLM_MAX_RETRIES
    backoff_base: float = LLM_BACKOFF_BASE

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        try:
            if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
                self.llm = LLMInterface(self.context.config.llm)
        except Exception:
            self.llm = None

    async def _call_llm(self, messages: List[Dict[str, str]]) -> str:
        tracer = getattr(self.context, "tracer", None)
        if not self.llm:
            if tracer:
                tracer.log("LLM_FALLBACK", self.name, "LLMInterface not configured - returning fallback token")
            return "LLM_ERROR:UNAVAILABLE"

        last_err = None
        for attempt in range(1, self.max_retries + 1):
            try:
                if tracer:
                    tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{self.max_retries}")
                resp = await self.llm.ask(messages)
                if not isinstance(resp, str) or not resp.strip():
                    last_err = "empty_response"
                    raise RuntimeError("Empty LLM response")
                low = resp.strip().lower()
                if low.startswith("llm_error") or low.startswith("error") or "error communicating" in low:
                    last_err = resp
                    raise RuntimeError("LLM signaled error")
                if tracer:
                    tracer.log("LLM_OK", self.name, f"Success len={len(resp)}")
                return resp
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {last_err[:200]}")
                if attempt < self.max_retries:
                    backoff = self.backoff_base * (2 ** (attempt - 1))
                    jitter = random.uniform(0, backoff * 0.1)
                    await asyncio.sleep(backoff + jitter)
        if tracer:
            tracer.log("LLM_GIVEUP", self.name, f"Giving up after {self.max_retries} attempts: {last_err}")
        return f"LLM_ERROR:{last_err or 'unknown'}"

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError()


class SimpleWriteCode(Action):
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"idea_len={len(idea or '')}")
        if not idea or not idea.strip():
            if tracer:
                tracer.log("ACTION_SKIPPED", self.name, "Empty idea -> deterministic placeholder")
            return "def placeholder(value=None):\n    \"\"\"Fallback placeholder.\"\"\"\n    return value\n"
        prompt = (
            "You are an expert Python developer. Produce a compact, production-ready Python implementation for the task. "
            "Return only valid Python source code."
            f"\n\nTask: {idea}"
        )
        messages = [{"role": "system", "content": "You are an expert Python programmer."},
                    {"role": "user", "content": prompt}]
        code = await self._call_llm(messages)
        if isinstance(code, str) and code.startswith("LLM_ERROR"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "LLM failed -> deterministic fallback")
            return "def placeholder(value=None):\n    \"\"\"Fallback implementation due to LLM error.\"\"\"\n    return value\n"
        # Validate syntax
        try:
            ast.parse(code)
        except Exception as e:
            if tracer:
                tracer.log("ACTION_INVALID", self.name, f"Generated code not parseable: {e}")
            return "def placeholder(value=None):\n    \"\"\"Fallback due to parse error.\"\"\"\n    return value\n"
        if tracer:
            tracer.log("ACTION_END", self.name, f"code_len={len(code)}")
        return code


class SimpleWriteTest(Action):
    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"code_len={len(code or '')}")
        if not code or not code.strip():
            if tracer:
                tracer.log("ACTION_SKIPPED", self.name, "No code provided -> placeholder test")
            return "def test_placeholder():\n    assert True\n"
        # heuristics
        symbols = []
        try:
            parsed = ast.parse(code)
            for n in parsed.body:
                if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_"):
                    symbols.append(n.name)
        except Exception:
            symbols = []
        prompt = (
            "You are an expert QA engineer. Write pytest-style tests for the provided module. "
            "Return only pytest code."
            f"\n\nPublic symbols (heuristic): {', '.join(symbols[:6]) or '(none)'}\n\nCode:\n{(code or '')[:3000]}"
        )
        messages = [{"role": "system", "content": "You are an expert QA engineer."},
                    {"role": "user", "content": prompt}]
        tests = await self._call_llm(messages)
        if isinstance(tests, str) and tests.startswith("LLM_ERROR"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "LLM failed -> deterministic tests fallback")
            target = symbols[0] if symbols else "placeholder"
            return f"def test_{target}_exists():\n    assert True\n"
        # validate tests parseable and contain assert or test_ function
        try:
            parsed_tests = ast.parse(tests)
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed_tests))
            has_assert = "assert " in tests or any(isinstance(n, ast.Assert) for n in ast.walk(parsed_tests))
            if not (has_test_fn or has_assert):
                raise ValueError("No test functions or asserts")
        except Exception as e:
            if tracer:
                tracer.log("ACTION_VALIDATE_FAIL", self.name, f"Tests invalid: {e}")
            return "def test_placeholder():\n    assert True\n"
        if tracer:
            tracer.log("ACTION_END", self.name, f"tests_len={len(tests)}")
        return tests


class SimpleWriteReview(Action):
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"is_human={self.is_human}")
        issues = []
        try:
            ast.parse(code or "")
        except Exception as e:
            issues.append(f"code_syntax_error:{str(e)[:160]}")
        try:
            ast.parse(tests or "")
        except Exception as e:
            issues.append(f"tests_syntax_error:{str(e)[:160]}")
        if self.is_human:
            review = "HUMAN_REVIEW: " + ("; ".join(issues) if issues else "ok")
            if tracer:
                tracer.log("ACTION_END", self.name, "Human review provided")
            return review
        prompt = (
            "You are a senior engineer. Provide a concise review and conclude with a single-line VERDICT: PASS or VERDICT: FAIL.\n\n"
            f"STATIC_ISSUES: {issues}\n\nCode:\n{(code or '')[:1500]}\n\nTests:\n{(tests or '')[:1500]}"
        )
        messages = [{"role": "system", "content": "You are a senior software engineer doing code reviews."},
                    {"role": "user", "content": prompt}]
        review = await self._call_llm(messages)
        if isinstance(review, str) and review.startswith("LLM_ERROR"):
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "LLM review unavailable; request human review")
            return "REQUEST_CHANGE: Automated review unavailable; request human review."
        if "VERDICT:" not in (review or ""):
            review = (review or "").strip() + "\n\nVERDICT: FAIL"
        if tracer:
            tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
        return review


class SimpleVerify(Action):
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "Deterministic verification")
        diagnostics = []
        code_ok = False
        tests_ok = False
        referenced = False
        code_defs = set()

        try:
            parsed_code = ast.parse(code or "")
            code_defs = {n.name for n in parsed_code.body if isinstance(n, (ast.FunctionDef, ast.ClassDef))}
            code_ok = bool(parsed_code and len(code_defs) >= 0)
            diagnostics.append(f"code_defs:{len(code_defs)}")
        except Exception as e:
            diagnostics.append(f"code_parse_error:{str(e)[:160]}")

        try:
            parsed_tests = ast.parse(tests or "")
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed_tests))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed_tests)) or ("assert " in (tests or ""))
            tests_ok = bool(has_test_fn or has_assert)
            diagnostics.append("tests_has_assert" if has_assert else "tests_no_assert")
            diagnostics.append("tests_has_test_fn" if has_test_fn else "tests_no_test_fn")
        except Exception as e:
            diagnostics.append(f"tests_parse_error:{str(e)[:160]}")
            parsed_tests = None

        try:
            if code_defs and parsed_tests is not None:
                tests_text = tests or ""
                for name in code_defs:
                    if name and name in tests_text:
                        referenced = True
                        break
                diagnostics.append(f"tests_reference_count:{sum(1 for n in code_defs if n in (tests or ''))}")
            else:
                diagnostics.append("tests_reference_count:0")
        except Exception as e:
            diagnostics.append(f"reference_check_error:{str(e)[:160]}")

        verified = code_ok and tests_ok and referenced
        # stable digest
        try:
            digest_src = (code or "").encode() + b"||" + (tests or "").encode()
            digest = hashlib.sha256(digest_src).hexdigest()[:12]
        except Exception:
            digest = "nodigest"
        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(diagnostics)
        if tracer:
            tracer.log("ACTION_END", self.name, result)
        return result

# ---------------------------
# Roles (clear responsibilities, watch/trigger logic)
# ---------------------------

class Role(ABC):
    """Base role with explicit should_respond logic and idempotent processing."""
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    actions: List[Action] = []
    watch_list: List[str] = []

    def __init__(self, **kwargs):
        self.name = kwargs.get("name", self.name)
        self.profile = kwargs.get("profile", self.profile)
        self.context = kwargs.get("context")
        self.is_human = kwargs.get("is_human", False)
        self.actions = []
        self.watch_list = []
        self.env: Optional["Environment"] = kwargs.get("env", None)
        self._processed_ids: Set[str] = set()

    def set_actions(self, actions: List[Action]):
        self.actions = actions

    def watch_actions(self, actions: List[Type[Action]]):
        self.watch_list = [a.name for a in actions]

    def should_respond(self, message: Message) -> bool:
        """Decide whether this role should handle the message."""
        if message is None:
            return False
        if getattr(message, "id", None) in self._processed_ids:
            return False
        # do not handle messages produced by self
        if getattr(message, "sent_from", None) == self.name:
            return False
        # explicit targeting has priority
        send_to = getattr(message, "send_to", None) or getattr(message, "sent_to", None)
        if send_to:
            try:
                if isinstance(send_to, (set, list)):
                    if self.name in send_to or self.profile in send_to or "*" in send_to:
                        return True
                    return False
                if isinstance(send_to, str):
                    return send_to in (self.name, self.profile, "*")
            except Exception:
                return False
        # watch-list trigger
        if getattr(message, "cause_by", None) in self.watch_list:
            return True
        # default: user input goes to coders
        if getattr(message, "cause_by", "") == "UserInput" and "Coder" in self.profile:
            return True
        return False

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        tracer = getattr(self.context, "tracer", None)
        if not self.actions:
            if tracer:
                tracer.log("ROLE_NO_ACTION", self.name, "No actions configured")
            return None
        action = self.actions[0]
        if message is not None and not self.should_respond(message):
            if tracer:
                tracer.log("ROLE_SKIP", self.name, f"Skipping message id={getattr(message,'id',None)}")
            return None

        attempts = 0
        last_err = None
        while attempts < ROLE_MAX_ATTEMPTS:
            attempts += 1
            try:
                if isinstance(action, SimpleWriteCode):
                    idea = getattr(message, "instruct_content", None) or getattr(message, "content", "") if message else ""
                    out = await action.run(idea)
                    send_to = {"SimpleTester"}
                    if isinstance(out, str) and out.startswith("LLM_ERROR"):
                        # retry scenario or route back to self
                        send_to = {self.profile}
                elif isinstance(action, SimpleWriteTest):
                    code_text = getattr(message, "content", "") if message and getattr(message, "content", None) else ""
                    if not code_text and self.env:
                        latest = self.env.get_latest_artifact([SimpleWriteCode.name])
                        code_text = latest.content if latest else ""
                    out = await action.run(code_text)
                    send_to = {"SimpleReviewer"}
                elif isinstance(action, SimpleWriteReview):
                    code_msg = self.env.get_latest_artifact([SimpleWriteCode.name]) if self.env else None
                    tests_msg = self.env.get_latest_artifact([SimpleWriteTest.name]) if self.env else None
                    out = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                    low = (out or "").lower()
                    if "request_change" in low or "request_changes" in low or "request_change" in low or "request_change:" in low:
                        send_to = {"SimpleCoder", "SimpleTester"}
                    else:
                        send_to = {"SimpleVerifier"}
                elif isinstance(action, SimpleVerify):
                    code_msg = self.env.get_latest_artifact([SimpleWriteCode.name]) if self.env else None
                    tests_msg = self.env.get_latest_artifact([SimpleWriteTest.name]) if self.env else None
                    out = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                    # verification broadcasts for trace
                    send_to = set()
                else:
                    out = await action.run(getattr(message, "content", "") if message else "")
                    send_to = set()

                # mark processed (idempotency)
                if message and getattr(message, "id", None):
                    self._processed_ids.add(message.id)

                response = Message(
                    content=out,
                    role=self.profile,
                    cause_by=action.name,
                    sent_from=self.name,
                    send_to=send_to
                )
                if tracer:
                    tracer.log("ROLE_COMPLETE", self.name, f"Produced msg={response.id} send_to={send_to}")
                return response
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("ROLE_ERROR", self.name, f"Attempt {attempts} failed: {last_err[:200]}")
                await asyncio.sleep(0.05 * attempts)
        # exhausted attempts -> return failure message for traceability
        err_msg = f"ROLE_FAILED: {self.name} after {ROLE_MAX_ATTEMPTS} attempts - {last_err}"
        if tracer:
            tracer.log("ROLE_FAIL", self.name, err_msg[:200])
        return Message(content=err_msg, role=self.profile, cause_by="RoleFailure", sent_from=self.name, send_to={"Team"})


class SimpleCoder(Role):
    name = "Alice"
    profile = "SimpleCoder"
    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.set_actions([SimpleWriteCode(context=self.context)])
        self.watch_actions([])


class SimpleTester(Role):
    name = "Bob"
    profile = "SimpleTester"
    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.set_actions([SimpleWriteTest(context=self.context)])
        self.watch_actions([SimpleWriteCode])


class SimpleReviewer(Role):
    name = "Charlie"
    profile = "SimpleReviewer"
    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.set_actions([SimpleWriteReview(is_human=self.is_human, context=self.context)])
        self.watch_actions([SimpleWriteTest, SimpleWriteCode])


class SimpleVerifier(Role):
    name = "Dana"
    profile = "SimpleVerifier"
    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.set_actions([SimpleVerify(context=self.context)])
        self.watch_actions([SimpleWriteTest, SimpleWriteReview, SimpleWriteCode])


# ---------------------------
# Environment (deterministic routing and delivery tracking)
# ---------------------------

class Environment:
    """Message bus with deterministic delivery (delivered once per role) and artifact lookup."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        # delivered map: role.name -> set(message.id)
        self._delivered: Dict[str, Set[str]] = {}
        # index by cause_by for artifact lookup
        self._by_cause: Dict[str, List[Message]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        self._delivered.setdefault(role.name, set())
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # ensure id exists
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        # normalize send_to
        if getattr(message, "send_to", None) is None:
            try:
                message.send_to = set(getattr(message, "sent_to", set()) or set())
            except Exception:
                message.send_to = set()
        self.history.append(message)
        if getattr(message, "cause_by", None):
            self._by_cause.setdefault(message.cause_by, []).append(message)
        if self.tracer:
            preview = (message.content or "")[:160].replace("\n", " ")
            self.tracer.log("ENV_MESSAGE", "Environment", f"Published msg {getattr(message,'id',None)} from {message.sent_from} cause_by={message.cause_by} -> {list(getattr(message,'send_to',set()))} preview={preview}")

    def mark_delivered(self, role: Role, message: Message):
        if not getattr(message, "id", None):
            return
        self._delivered.setdefault(role.name, set()).add(message.id)
        if self.tracer:
            self.tracer.log("ENV_MARK_DELIVERED", "Environment", f"{role.name} delivered {message.id}")

    def has_been_delivered(self, role: Role, message: Message) -> bool:
        return getattr(message, "id", None) in self._delivered.get(role.name, set())

    def get_messages_for_role(self, role: Role) -> List[Message]:
        """Return messages not yet delivered to role that should be handled by it."""
        out: List[Message] = []
        delivered = self._delivered.setdefault(role.name, set())
        for msg in list(self.history):
            mid = getattr(msg, "id", None)
            if not mid or mid in delivered:
                continue
            # don't deliver messages created by same role (mark as delivered)
            if getattr(msg, "sent_from", None) == role.name:
                delivered.add(mid)
                continue
            # explicit routing
            send_to = getattr(msg, "send_to", None) or set(getattr(msg, "sent_to", set()) or set())
            targeted = False
            try:
                if isinstance(send_to, (set, list)):
                    if role.name in send_to or role.profile in send_to or "*" in send_to:
                        targeted = True
                elif isinstance(send_to, str):
                    if send_to in (role.name, role.profile, "*"):
                        targeted = True
            except Exception:
                targeted = False
            if targeted:
                out.append(msg)
                delivered.add(mid)
                continue
            # watch-list by cause_by
            if getattr(msg, "cause_by", None) in role.watch_list:
                out.append(msg)
                delivered.add(mid)
                continue
            # default: user input to coders
            if getattr(msg, "cause_by", "") == "UserInput" and "Coder" in role.profile:
                out.append(msg)
                delivered.add(mid)
                continue
        return out

    def get_latest_artifact(self, cause_names: Iterable[str]) -> Optional[Message]:
        """Return the most recent message whose cause_by is in cause_names."""
        cause_set = set(cause_names)
        for msg in reversed(self.history):
            if getattr(msg, "cause_by", None) in cause_set:
                return msg
        return None

# ---------------------------
# Team orchestrator (deterministic ordering, verification stability, retries)
# ---------------------------

class Team:
    """Orchestrates roles, termination, verification handshake, and stability checks."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self.log_file = log_file
        self._verify_streak: int = 0
        self._verify_required: int = VERIFY_REQUIRED_STREAK
        self._last_digest: Optional[str] = None

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def _process_role(self, role: Role) -> int:
        produced = 0
        msgs = self.env.get_messages_for_role(role)
        if not msgs:
            return 0
        for msg in msgs:
            if getattr(msg, "sent_from", None) == role.name:
                self.env.mark_delivered(role, msg)
                continue
            try:
                resp = await role.act(msg)
            except Exception as e:
                self.tracer.log("ROLE_EXCEPTION", role.name, f"Exception during act: {e}")
                resp = Message(content=f"ERROR: role {role.name} crashed: {e}", role=role.profile, cause_by="RoleCrash", sent_from=role.name, send_to=set())
            # mark delivered so we don't redeliver
            self.env.mark_delivered(role, msg)
            if resp:
                self.env.publish_message(resp)
                produced += 1
                # update verification streak if verifier role
                if isinstance(role, SimpleVerifier):
                    content = resp.content or ""
                    if "VERIFICATION_RESULT: PASS" in content:
                        digest = None
                        for token in content.split("|"):
                            token = token.strip()
                            if token.startswith("digest="):
                                digest = token.split("=", 1)[1]
                                break
                        if digest and digest == self._last_digest:
                            self._verify_streak += 1
                        elif digest:
                            self._last_digest = digest
                            self._verify_streak = 1
                        else:
                            self._last_digest = None
                            self._verify_streak += 1
                        self.tracer.log("VERIFY_UPDATE", "Team", f"streak={self._verify_streak} digest={digest}")
                    else:
                        if self._verify_streak > 0:
                            self.tracer.log("VERIFY_RESET", "Team", f"resetting streak {self._verify_streak}->0")
                        self._verify_streak = 0
                        self._last_digest = None
        return produced

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Running up to {n_round} rounds; require {self._verify_required} stable verifications to stop early")
        # initial message to coder(s)
        coder_targets = {r.profile for r in self.env.roles if isinstance(r, SimpleCoder)} or {"SimpleCoder"}
        initial_msg = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to=coder_targets
        )
        self.env.publish_message(initial_msg)

        verified = False
        no_progress_rounds = 0
        last_history_len = len(self.env.history)

        # deterministic processing order
        orchestration = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]

        for round_idx in range(n_round):
            round_num = round_idx + 1
            self.tracer.log("ROUND_START", "Team", f"Round {round_num}/{n_round}")
            round_new_msgs = 0

            for RoleClass in orchestration:
                roles = [r for r in self.env.roles if isinstance(r, RoleClass)]
                for role in roles:
                    # ensure coder sees initial on first round
                    if isinstance(role, SimpleCoder) and round_idx == 0:
                        if not self.env.has_been_delivered(role, initial_msg):
                            # ensure initial is processed by this coder
                            pass
                    produced = await self._process_role(role)
                    round_new_msgs += produced

            # progress detection
            if len(self.env.history) == last_history_len:
                no_progress_rounds += 1
            else:
                no_progress_rounds = 0
            last_history_len = len(self.env.history)

            self.tracer.log("ROUND_END", "Team", f"Round {round_num} complete produced={round_new_msgs} verify_streak={self._verify_streak} history_len={len(self.env.history)}")

            # termination: require stable verification passes and confirm final verification before stopping
            if self._verify_streak >= self._verify_required and round_num >= 1:
                try:
                    code_msg = self.env.get_latest_artifact([SimpleWriteCode.name])
                    tests_msg = self.env.get_latest_artifact([SimpleWriteTest.name])
                    verifier = SimpleVerify(context=self.context)
                    confirm = await verifier.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                    if isinstance(confirm, str) and "VERIFICATION_RESULT: PASS" in confirm:
                        self.tracer.log("TEAM_EARLY_STOP", "Team", f"Verification stable after confirmation; stopping (streak={self._verify_streak})")
                        verified = True
                        break
                    else:
                        self.tracer.log("VERIFY_RESET", "Team", "Confirmation failed; resetting streak")
                        self._verify_streak = 0
                        self._last_digest = None
                except Exception as e:
                    self.tracer.log("VERIFY_CONFIRM_ERROR", "Team", f"Confirmation exception: {e}")
                    self._verify_streak = 0
                    self._last_digest = None

            # stop if no progress for multiple rounds
            if no_progress_rounds >= NO_PROGRESS_GRACE:
                self.tracer.log("TEAM_NO_PROGRESS", "Team", f"No progress for {no_progress_rounds} rounds; terminating early")
                break

            # nudge coder(s) if stalled
            if round_new_msgs == 0:
                for r in [r for r in self.env.roles if isinstance(r, SimpleCoder)]:
                    nudge = Message(
                        content=f"Nudge: please refine implementation for: {self.idea}",
                        instruct_content=self.idea,
                        role="System",
                        sent_from="Orchestrator",
                        cause_by="Nudge",
                        send_to={r.profile}
                    )
                    self.env.publish_message(nudge)

            # cooperative yield
            await asyncio.sleep(0.01)

        self.tracer.log("TEAM_END", "Team", "Project run finished")
        summary = f"Project '{self.idea}' finished after {round_num} rounds with {len(self.env.history)} messages. Verified={verified}"
        self.tracer.log("SUMMARY", "Team", summary)

# EVOLVE-BLOCK-END

