class SimpleVerify(Action):
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "Deterministic verification")
        diagnostics = []
        code_ok = False
        tests_ok = False
        referenced = False
        code_defs = set()

        try:
            parsed_code = ast.parse(code or "")
            code_defs = {n.name for n in parsed_code.body if isinstance(n, (ast.FunctionDef, ast.ClassDef))}
            code_ok = bool(parsed_code and len(code_defs) >= 0)
            diagnostics.append(f"code_defs:{len(code_defs)}")
        except Exception as e:
            diagnostics.append(f"code_parse_error:{str(e)[:160]}")

        try:
            parsed_tests = ast.parse(tests or "")
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed_tests))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed_tests)) or ("assert " in (tests or ""))
            tests_ok = bool(has_test_fn or has_assert)
            diagnostics.append("tests_has_assert" if has_assert else "tests_no_assert")
            diagnostics.append("tests_has_test_fn" if has_test_fn else "tests_no_test_fn")
        except Exception as e:
            diagnostics.append(f"tests_parse_error:{str(e)[:160]}")
            parsed_tests = None

        try:
            if code_defs and parsed_tests is not None:
                tests_text = tests or ""
                for name in code_defs:
                    if name and name in tests_text:
                        referenced = True
                        break
                diagnostics.append(f"tests_reference_count:{sum(1 for n in code_defs if n in (tests or ''))}")
            else:
                diagnostics.append("tests_reference_count:0")
        except Exception as e:
            diagnostics.append(f"reference_check_error:{str(e)[:160]}")

        verified = code_ok and tests_ok and referenced
        # stable digest
        try:
            digest_src = (code or "").encode() + b"||" + (tests or "").encode()
            digest = hashlib.sha256(digest_src).hexdigest()[:12]
        except Exception:
            digest = "nodigest"
        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(diagnostics)
        if tracer:
            tracer.log("ACTION_END", self.name, result)
        return result

# ---------------------------
# Roles (clear responsibilities, watch/trigger logic)
