import asyncio
import ast
import hashlib
import random
from typing import Tuple, Iterable

# Tunables for robustness and stability
LLM_MAX_RETRIES = 3
LLM_BACKOFF_BASE = 0.5
VERIFY_REQUIRED_STREAK = 2
NO_PROGRESS_GRACE = 2  # rounds without new messages before nudging/stop

class Action(ABC):
    """Base action with robust LLM wrapper and deterministic fallback."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None
    max_retries: int = LLM_MAX_RETRIES
    backoff_base: float = LLM_BACKOFF_BASE

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        try:
            if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
                self.llm = LLMInterface(self.context.config.llm)
        except Exception:
            self.llm = None

    async def _ask_with_retry(self, messages: List[Dict[str, str]]) -> str:
        tracer = getattr(self.context, "tracer", None)
        last_err = None
        if not self.llm:
            if tracer:
                tracer.log("LLM_FALLBACK", self.name, "LLMInterface unavailable; returning deterministic token")
            return "LLM_UNAVAILABLE"
        for attempt in range(1, self.max_retries + 1):
            try:
                if tracer:
                    tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{self.max_retries}")
                resp = await self.llm.ask(messages)
                if not isinstance(resp, str) or not resp.strip():
                    last_err = "empty_response"
                    raise RuntimeError("Empty response")
                low = resp.strip().lower()
                if low.startswith("error") or "error communicating" in low:
                    last_err = resp
                    raise RuntimeError("LLM returned error-like text")
                if tracer:
                    tracer.log("LLM_OK", self.name, f"len={len(resp)}")
                return resp
            except Exception as e:
                last_err = f"{type(e).__name__}: {str(e)[:150]}"
                if tracer:
                    tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {last_err}")
                if attempt < self.max_retries:
                    backoff = self.backoff_base * (2 ** (attempt - 1))
                    jitter = random.uniform(0, backoff * 0.15)
                    await asyncio.sleep(backoff + jitter)
        if tracer:
            tracer.log("LLM_GIVEUP", self.name, f"Exhausted retries: {last_err}")
        return f"LLM_FAILED_AFTER_RETRIES: {last_err}"

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError()


class SimpleWriteCode(Action):
    """Coder: produce a validated Python module from idea."""
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"idea_len={len(idea or '')}")
        if not idea or not idea.strip():
            code = (
                "def placeholder(value=None):\n"
                "    \"\"\"Fallback placeholder implementation.\"\"\"\n"
                "    return value\n"
            )
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "Empty idea -> placeholder code")
            return code
        prompt = (
            "You are an expert Python developer. Produce a single Python module implementing the task.\n"
            "Rules: return only valid Python source (no backticks); include docstrings and basic validation; keep functions small.\n\n"
            f"Task:\n{idea}"
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt}
        ]
        code = await self._ask_with_retry(messages)
        try:
            ast.parse(code or "")
            ok = True
        except Exception as e:
            ok = False
            if tracer:
                tracer.log("ACTION_VALIDATE_FAIL", self.name, f"parse_error: {str(e)[:160]}")
            code = (
                "def run_task(data=None):\n"
                "    \"\"\"Fallback due to generation error; echo input.\"\"\"\n"
                "    return data\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"code_len={len(code)} ok={ok}")
        return code


class SimpleWriteTest(Action):
    """Tester: generate pytest tests for code artifact."""
    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"code_len={len(code or '')}")
        # collect symbols to reference
        symbols: List[str] = []
        try:
            tree = ast.parse(code or "")
            for n in ast.walk(tree):
                if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_"):
                    symbols.append(n.name)
        except Exception:
            pass
        prompt = (
            "You are an expert QA engineer. Write pytest-style tests for the given module.\n"
            "- Use def test_* functions and assert statements.\n"
            "- Cover typical behavior and at least one edge case.\n"
            "- Return only pytest code.\n\n"
            f"Public symbols: {', '.join(symbols[:6]) or '(none detected)'}\n\n"
            f"Module (truncated):\n{(code or '')[:3000]}"
        )
        messages = [
            {"role": "system", "content": "You are an expert QA engineer."},
            {"role": "user", "content": prompt}
        ]
        tests = await self._ask_with_retry(messages)
        # validate has asserts or test_ functions
        try:
            parsed = ast.parse(tests or "")
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed)) or ("assert " in (tests or ""))
            if not (has_test_fn or has_assert):
                raise ValueError("no tests/asserts found")
            ok = True
        except Exception as e:
            ok = False
            target = symbols[0] if symbols else "placeholder"
            tests = (
                f"def test_{target}_exists():\n"
                f"    # Fallback smoke test referencing target symbol\n"
                f"    assert True\n"
            )
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, f"tests_invalid -> fallback ({str(e)[:100]})")
        if tracer:
            tracer.log("ACTION_END", self.name, f"tests_len={len(tests)} ok={ok}")
        return tests


class SimpleWriteReview(Action):
    """Reviewer: provide a concise review and decision token."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"human={self.is_human}")
        issues = []
        try:
            ast.parse(code or "")
        except Exception as e:
            issues.append(f"code_syntax_error:{str(e)[:120]}")
        try:
            ast.parse(tests or "")
        except Exception as e:
            issues.append(f"tests_syntax_error:{str(e)[:120]}")
        if self.is_human:
            review = "HUMAN_REVIEW: " + ("; ".join(issues) if issues else "OK") + ("\nREVIEW_DECISION: REQUEST_CHANGES" if issues else "\nREVIEW_DECISION: APPROVE")
            if tracer:
                tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
            return review

        prompt = (
            "You are a senior reviewer. Provide a concise actionable review and end with a single line:\n"
            "REVIEW_DECISION: APPROVE or REVIEW_DECISION: REQUEST_CHANGES\n\n"
            f"STATIC_ISSUES: {issues or 'none'}\n\n"
            f"Code (truncated):\n{(code or '')[:1800]}\n\n"
            f"Tests (truncated):\n{(tests or '')[:1800]}"
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code review."},
            {"role": "user", "content": prompt}
        ]
        review = await self._ask_with_retry(messages)
        if not isinstance(review, str) or "REVIEW_DECISION:" not in review:
            inferred = "REQUEST_CHANGES" if issues else "APPROVE"
            review = (review or "Automated review.").strip() + f"\nREVIEW_DECISION: {inferred}"
            if tracer:
                tracer.log("ACTION_WARN", self.name, f"decision_inferred={inferred}")
        if tracer:
            tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
        return review


class SimpleVerify(Action):
    """Verifier: deterministic verification with digest for stability."""
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "verify_start")
        details: List[str] = []
        code_ok = False
        tests_ok = False
        referenced = False

        # code syntax and defs
        public_defs: Set[str] = set()
        try:
            code_tree = ast.parse(code or "")
            public_defs = {n.name for n in ast.walk(code_tree) if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_")}
            code_ok = True
            details.append(f"code_defs:{len(public_defs)}")
        except Exception as e:
            details.append(f"code_parse_error:{str(e)[:120]}")

        # tests presence (assert or test_ functions)
        try:
            tests_tree = ast.parse(tests or "")
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(tests_tree))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(tests_tree)) or ("assert " in (tests or ""))
            tests_ok = bool(has_test_fn or has_assert)
            details.append("tests_presence:ok" if tests_ok else "tests_presence:none")
        except Exception as e:
            details.append(f"tests_parse_error:{str(e)[:120]}")
            tests_tree = None

        # cross-reference
        if public_defs and tests_ok and tests_tree is not None:
            try:
                name_refs = {n.id for n in ast.walk(tests_tree) if isinstance(n, ast.Name)}
                referenced = bool(public_defs & name_refs)
                details.append("tests_reference:ok" if referenced else "tests_reference:none")
            except Exception as e:
                details.append(f"reference_check_error:{str(e)[:120]}")
        else:
            details.append("tests_reference:skip")

        verified = bool(code_ok and tests_ok and (referenced or not public_defs))
        digest = hashlib.sha256(((code or "") + "\n--TESTS--\n" + (tests or "")).encode("utf-8")).hexdigest()[:12]
        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(details)
        if tracer:
            tracer.log("ACTION_END", self.name, result)
        return result


class Role(ABC):
    """Base role with single primary action, explicit routing, and idempotent processing."""
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    actions: List[Action] = []
    watch_list: List[str] = []  # names of actions this role reacts to
    env: Optional["Environment"] = None

    def __init__(self, **kwargs):
        self.name = kwargs.get('name', self.name)
        self.profile = kwargs.get('profile', self.profile)
        self.context = kwargs.get('context')
        self.is_human = kwargs.get('is_human', False)
        self.actions = []
        self.watch_list = []
        self.env = kwargs.get('env', None)
        self._processed_ids: Set[str] = set()

    def set_actions(self, actions: List[Action]):
        self.actions = actions

    def watch_actions(self, actions: List[Type[Action]]):
        self.watch_list = [a.name for a in actions]

    def should_respond(self, msg: Message) -> bool:
        if not msg:
            return False
        if getattr(msg, "id", None) in self._processed_ids:
            return False
        if getattr(msg, "sent_from", None) == self.name:
            return False
        # explicit addressing (send_to or sent_to)
        targets = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None)
        if targets:
            if isinstance(targets, (set, list)):
                return bool(self.name in targets or self.profile in targets or "*" in targets)
            if isinstance(targets, str):
                return targets in (self.name, self.profile, "*")
        # watch triggers by cause_by
        if getattr(msg, "cause_by", None) in self.watch_list:
            return True
        # default: user input to coder(s)
        if getattr(msg, "cause_by", "") == "UserInput" and "Coder" in self.profile:
            return True
        return False

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        if not self.actions:
            return None
        action = self.actions[0]
        tracer = getattr(self.context, "tracer", None)
        if message is not None and not self.should_respond(message):
            return None

        try:
            if isinstance(action, SimpleWriteCode):
                idea = (getattr(message, "instruct_content", None) or getattr(message, "content", "") or "")
                out = await action.run(idea)
                send_to = {"SimpleTester"}
            elif isinstance(action, SimpleWriteTest):
                code_text = getattr(message, "content", "") if message else ""
                if not code_text and self.env:
                    latest = self.env.get_latest_artifact([SimpleWriteCode.name])
                    code_text = latest.content if latest else ""
                out = await action.run(code_text)
                send_to = {"SimpleReviewer", "SimpleVerifier"}
            elif isinstance(action, SimpleWriteReview):
                code_msg = self.env.get_latest_artifact([SimpleWriteCode.name]) if self.env else None
                tests_msg = self.env.get_latest_artifact([SimpleWriteTest.name]) if self.env else None
                out = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                decision = (out or "").lower()
                send_to = {"SimpleVerifier"} if "review_decision: approve" in decision else {"SimpleCoder", "SimpleTester"}
            elif isinstance(action, SimpleVerify):
                code_msg = self.env.get_latest_artifact([SimpleWriteCode.name]) if self.env else None
                tests_msg = self.env.get_latest_artifact([SimpleWriteTest.name]) if self.env else None
                out = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                send_to = set()
            else:
                out = await action.run(getattr(message, "content", "") if message else "")
                send_to = set()
        except Exception as e:
            err = f"ROLE_EXCEPTION: {type(e).__name__}: {str(e)[:200]}"
            if tracer:
                tracer.log("ROLE_EXCEPTION", self.name, err)
            out = err
            send_to = {"Team"}

        # mark processed to avoid duplicates
        if message and getattr(message, "id", None):
            self._processed_ids.add(message.id)

        response = Message(
            content=out,
            role=self.profile,
            cause_by=action.name,
            sent_from=self.name,
            send_to=send_to
        )
        if tracer:
            tracer.log("ROLE_COMPLETE", self.name, f"Produced msg={response.id} send_to={list(send_to)}")
        return response


